css:
@-moz-document url("http://www.tumblr.com"), url("https://www.tumblr.com") {
/* Tumblr Dashboard Theme - Fall Out Boy Xmas v1.0 */
/* Created by user notpetewentz */
}

@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
/* main background and bg color */
body, #footer, div#footer_container.clearfix, #auto_pagination_loader, #search_actions_search, #search_actions_search .posts_wrapper_inner.clearfix, #search_actions_search .search_results_top_wrapper.blogs_wrapper.clearfix, #search_actions_search .search_flex_loader, #search_actions_search .search_results_bottom_wrapper, #search_actions_search .blogs_wrapper_carousel {
    background: #000020 url(http://i.imgur.com/96byaCR.png) left fixed !important;
    }
/* new header bar */
.l-header-container {
    background: rgba(0,0,0,.1) !important;
    }
.controls_section_item:hover, .follow_list_item_blog:hover {background-color: rgba(0,0,0,1) !important; }
#auto_pagination_loader {
    border: none !important;
    }
/* search tweaks */
 .search_query {
    background: rgb(0,0,0) !important;
    }
#search_actions_search .search_query, #tagged_actions_display .search_query {    
    color: #ffffff !important;
    }
#search_actions_search .tumblelog_mask_item {
    background: none !important;
    }
.search_query {
    color: #ffffff !important;
    }
/* increase visibility of tumblr and web search buttons in search form */
.search_query_input_controls .search_query_btn {
    background: #ffffff !important;
    }
#search_actions_search .search_query_input_controls .search_query_btn {
    background: rgb(186, 186, 186) !important;
    }
#search_actions_search .blogs_wrapper_carousel:after, #search_actions_search .blogs_wrapper_carousel:before {   
    background: none !important;
    }    
/* in-dash notifications */
.notification_inner {
    background: rgba(0,0,0,0.6) !important;
    }
/* blue content fill and gradient at the top of page */
#dashboard_index .l-content, #dashboard_posts_editing .l-content, #dashboard_useraction_followers .l-content, #activity_actions_index .l-content, #dashboard_drafts .l-content, #dashboard_group_members .l-content, #dashboard_post_queue .l-content, #dashboard_posts_likes .l-content, #dashboard_messages_inbox .l-content, #dashboard_messages_submissions .l-content, #tagged_actions_display .l-content {
    background: url(http://i.imgur.com/df6KqRX.png) top repeat-x !important;
    }
.selection_nipple {
    border-bottom: 12px solid rgb(0,0,0) !important;
    }
/* fix post submission forms and customize appearance forms */
.mceContentBody, #upload_image_form {
    background: #ffffff  !important;
    } 
.dashboard_iframe {
    background: transparent !important;
    }
/* follow button in notifications */
.control.follow.ui_follow {
    background: rgba(255,255,255,0.8) !important;
    }
/* "dog ear" permalink fix */
.post_permalink { 
    background: rgb(60,60,100) !important;
    }
.post_permalink {
    border-color: #000010 #000010 transparent transparent !important;
    }
/* remove blue background from left and right columns, remove errant gradient from the sidebar */
div#right_column,div#right_column:after, .left_column {
    background: none !important;
    }
/* Prev and Next page buttons */
#pagination a {
    color: #eaeaff;
    background: rgba(60,60,100,.3) !important;
    border: 1px solid #eaeaff;
    }
/* transparent icons in queue, add glow to avatars */
.post_avatar {
    background-color: transparent !important;
    border-color: transparent !important;
    box-shadow: 0px 0px 8px #79A0C4;
    }
/* remove gradients from recommended blogs and radar section */
.controls_section.user_list li a:before, .radar_footer .radar_avatar:before {
    background: transparent !important;
    border: none !important;
    }
/* Use a standard white color for follow icons in recommended blogs section */
.icon_plus {
    color: rgba(255,255,255,0.32) !important;
    }
.icon_plus:hover {
    color: rgb(255, 255, 255) !important;
    }
/* transparent post box */    
#new_post, #posts .post {
    background: rgba(0,0,00,.7) !important;
    transition: all 0.5s linear 0s;
    }
#new_post:hover, #posts .post:hover {
    background: rgba(0,0,0,1) !important;
    transition: all 0.3s linear 0s;
    }
/* majority of foreground text */
.post .post_content {
    background: rgba(0,0,0,0) !important;
    color: #eaeaff !important;
    }
.icon_post_text, .new_post a, .post .post_content a, .post_full.is_quote .post_title, .post_source_name_prefix, .search_actions_search .quote {
    color: #eaeaff !important;   
    }
/* link text */
.post_info_link, .note_link_current, .post_full .post_info, .posts .post_tag {
    color: #82AED1 !important;
    }
/* featured tags */
.post_tag.featured {
    color: #eaeaff !important; 
    }
/* post nipple beside avatar */
div.post_avatar:after {
    border-right: 9px solid rgba(0,0,00,.35) !important;  
    }
/* opacity for the reblog/favorite/other buttons */
.post_control:after { 
    opacity: .6 !important;
    }
.post_control.active:after, .post_control:hover:after {
    opacity: 1 !important;
    }
    .post_full .post_footer {color: #000000 !important;}
/* maintain original color for the activity page and other items */
.notifications_page a, .indash_blog .post_body, .indash_blog .post_body a, .indash_blog .post_title, .audio_search_results li a, .audio_tabs li a, .popover_menu_item a, .indash_blog .answer.post_info {
    color: #444 !important;
    } 
/* reblogged-from item in posts */
.reblog_icon {
    background: url(data:image/png;base64,) center no-repeat !important; 
    opacity: .75 !important;
    }
/* inbox color corrections */
.note_item {
    color: #444 !important;
    }
.note_item a.name, .note_item .text a, .post_question_fan_mail {
    color: #444 !important;
    }
.answer.post_info {
    color: #eaeaff !important;
    }
.post_content blockquote {
    border-left: solid 3px #eaeaff !important;
    }
/* remove ending gradient in the tags field */
.post_tags:after {
    background:none !important;
    }
/* chat post correction */
.post_full.is_conversation .conversation_lines .chat_line:nth-child(2n) {
    background: #000020 !important;
    }   
pre {
    background: #000020 !important;
    }
.indash_blog pre {
    background: none !important;
    }
/* restore white bg for editing posts */    
#posts .post.editing {
    background: #fff !important;
    }
#dashboard_index .link_button, #dashboard_drafts .link_button, #search_actions_search .link_button, #tagged_actions_display .link_button { 
    background: rgb(0,0,00) !important;
    border: 1px solid #eaeaff !important;
    }
/* preserve text color for link posts indash, and text fields in popover blogs */
.indash_blog .link_text a {
    color: inherit !important;
    } 
.post_micro .post_content, .post_micro .post_content a {
    color: #444 !important;
    }
/* color correction for reblog button in post headers */
a.reblog_follow_button:not(.icon_follow_rounded_small):before, .reblog_follow_button i {
    color: #fff !important;
    background-color: #000020 !important; 
    }
}
