css:
@-moz-document url-prefix("http://www.hiveworkshop.com/") {
/*
	=====TOP BAR=====
*/

#opm {
    background: url('http://www.hiveworkshop.com/forums/pastebin_data/n77cmu/topbarloophor.jpg') repeat-x center top;
}

#paypal {
    top: 65px;
}

#tpr, #tpl {
    top: 4px;
}

.topad {
    display: none;
}

/*
	=====COLORING=====
*/

.alt1, .alt1Active {
    background-color: #2A2424;
}

.alt1, .alt1Active, .alt2, .alt2Active, .tcat, .thead {
    font: normal 12px/1.5 Arial, sans-serif;
}

.alt2, .alt2Active {
    background-color: #221D1D;
    color: #a99877;
}

.smallfont {
    color: #a99877;
    font: normal 11px/1.5 Arial, sans-serif;
}

.tcat, .thead {
    color: #C4AA69;
}

.grass {
    background: none;
}

/*
	=====VANILLA BORDERS=====
*/

.outleft, .outright, #opl, #opr, .leaves {
    display: none;
}

.wbt, .wbm, .outbottom {
    background: none;
}

.wbtaa, .wbtl, .wbtr, .wbtrb, .wbr, .wbl, .wbl2, .wbr2, .wbb, .wbbla, .wbbra, .outbottomcorner, .wbtlb {
    background: none;
    display: none;
}

/*
  	=====LINKS=====
*/
  
a:link, body_alink, .vbmenu_control a:visited, .vbmenu_control a:link, .vbmenu_control_alink, a:visited {
    color: #96782C;
}

a:hover, .vbmenu_control a:hover {
    color: #E0B851;
}

body>#tpl>a:link, body>#tpl>a:visited {
    color: #D6BF18;
    text-shadow: 1px 1px #000;
}

body>#tpl>a:hover {
    color: #F7E567;
    text-shadow: 2px 2px #000;
}

body>#tpr>a:link, body>#tpr>a:visited {
    color: #D6BF18;
    text-shadow: 1px 1px #000;
}

body>#tpr>a:hover {
    color: #F7E567;
    text-shadow: 2px 2px #000;
}

/*
    =====POSTS=====
*/

div>div.page>div>div>table.tborder>tbody {
    background-color: #25211D;
    background-image: url('http://www.hiveworkshop.com/forums/pastebin_data/n77cmu/postbg.jpg');
    background-repeat: repeat-x;
}

div>div.page>div>div>table.tborder>tbody>tr>td {
    background: none;
}

div>div.page>div>div>table.tborder>tbody>tr[valign="top"]+tr>td {
    background: linear-gradient(#272119, #111);
    border-color: #000;
    border-style: solid;
    border-width: 1px 0px 0px 0px;
}
    
/*
    =====PAGE=====
*/
    
div>div.page > div {
    background: url('http://www.hiveworkshop.com/forums/pastebin_data/n77cmu/bgloop.jpg');
    background-repeat: repeat;
}

table.mb {
    width: 100%;
}

td.grass>div[align=center]>.page {
    box-shadow: -2px -2px 0 #511A00, 
 2px -2px 0 #511A00, 
 -2px 2px 0 #511A00, 
 2px 2px 0 #511A00, 
 -3px -3px 0 #292521, 
 3px -3px 0 #292521, 
 -3px 3px 0 #292521, 
 3px 3px 0 #292521, 
 -5px -5px 5px rgba(0,0,0,0.5),
 -5px 5px 5px rgba(0,0,0,0.5),
 5px -5px 5px rgba(0,0,0,0.5),
 5px 5px 5px rgba(0,0,0,0.5);
    margin-top: 28px;
    max-width: 1200px;
    width: 100%;
}

/*
    =====ELSE=====
*/
   
td.outbottom {
    background-color: rgba(0,0,0,0.5);
}


body {
    -moz-background-size: cover;
    -o-background-size: cover;
    -webkit-background-size: cover;
    background: url('http://www.hiveworkshop.com/forums/pastebin_data/n77cmu/artwork-0128-full.jpg') no-repeat center center fixed;
    background-size: cover;
}
  
/*
	=====TOP-MOST PAGE ROW=====
*/

td.wbm>table.tborder>tbody>tr>td.alt1 {
    background: none !important;
    background: #221D1D !important;
;
}

td.wbm>table.tborder>tbody>tr>td.alt2 {
    background: none !important;
    background-color: #222 !important;
}
  
/*
	=====RESOURCE ICONS======
*/
  
td.alt1>div>table>tbody>tr>td[height="6"] {
    display: none;
}
  
td.alt1>div>table>tbody>tr>td[background="http://www.hiveworkshop.com/forums/images_custom/style_wc3s/borders3_trans/top.gif"] {
    display: none;
}
  
td.alt1>div>table>tbody>tr>td[background="http://www.hiveworkshop.com/forums/images_custom/style_wc3s/borders3_trans/left.gif"] {
    display: none;
}
  
td.alt1>div>table>tbody>tr>td[background="http://www.hiveworkshop.com/forums/images_custom/style_wc3s/borders3_trans/right.gif"] {
    display: none;
}
  
td.alt1>div>table>tbody>tr>td[background="http://www.hiveworkshop.com/forums/images_custom/style_wc3s/borders3_trans/bottom.gif"] {
    display: none;
}
  
td.alt1>div>table>tbody>tr>td[bgcolor="#000000"] {
  background: none !important;
}
  
td.alt1>div[style="height: 106px; width: 106px; background: #000000"] {
  height: 70px !important;
  width: 70px !important;
  background: none !important;
}
  
img[src="/gfx/res_section/pending.gif"] {
  display: none;
}
  
td.alt1>div>table>tbody>tr>td>a>div {
  overflow: visible !important;
  transition-duration: 0.5s !important;
  height: 70px !important;
  width: 70px !important;
  position: relative;
  left: 0px;
  top: 0px;
  border-radius: 10px!important;
  z-index: 50!important;
  background-color: rgba(0, 0, 0, 0.55) !important;
  box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 0.55), 0px 0px 0px 1px rgba(0, 0, 0, 0.55), 0px 0px 0px 1px rgba(255, 255, 255, 0.12) inset!important;
  background-size: cover !important;
}
  
td.alt1>div>table>tbody>tr>td>a>div:hover {
  transition-duration: 0.4s !important;
  background-color: rgba(0,0,0,1) !important;
  box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 0.85), 0px 0px 0px 1px rgba(0, 0, 0, 0.85), 0px 0px 0px 1px rgba(255, 255, 255, 0.42) inset!important;
}
  
td.alt1>div>table>tbody>tr>td>a {
  height: 70px !important;
  width: 70px !important;
}
    
tr[valign="top"]>td>table.tborder>tbody>tr[valign="top"]>td.alt1>br {
  display: none;   
}
    
td[width="140"]+td>table.tborder[align="center"] {
  border-spacing: 0px !important;
  background-color: #222222;
  padding: 0;
  margin: 0;
  list-style: none;
      
  display: -webkit-box;
  display: -moz-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  display: flex;
      
  -webkit-flex-flow: row wrap;
  justify-content: space-around;
}
    
td[width="140"]+td>table.tborder[align="center"]>tbody>tr[valign="top"] {
  display: inline-block;
      
  margin-top: 10px;
      
      
  -moz-border-radius: 10px;
  -webkit-border-radius: 10px;
      
  border: 1px solid #000;
  box-shadow: 0px 0px 3px 0px rgba(0, 0, 0, 0.55), 0px 0px 0px 1px rgba(0, 0, 0, 0.55), 0px 0px 0px 1px rgba(255, 255, 255, 0.12) inset!important;
  background-color: #332C29 !important;
      
  margin-right: 15px;
}
    
td[width="140"]+td>table.tborder[align="center"]>tbody>tr[valign="top"]>td { 
  float:left;
  background: none;
}
    
td[width="140"]+td>table.tborder[align="center"]>tbody>tr[valign="top"]>td+td { 
  height: 50px !important;
  z-index: 10!important;
  margin-left: -5px;
}
}
