css:
@-moz-document url-prefix("http://fr.massivemusicquiz.com"), regexp("http://(fr|en)\\.massivemusicquiz\\.com/(?!users/show/).*") {
/*CSS et images de Quentin Chtl. No copy.*/ 


/*PAGE D'ACCUEIL*/ 


.content a#logo:before {
  content: url("http://zupimages.net/up/14/44/gmf5.png") !important ;
}
.content a#logo {width : 150px ; height : 65px ;}


#header #top-bar #logo img { 
  opacity: 0;
}
body {
background-image: url('http://zupimages.net/up/14/44/o1vp.png');
}

#content .content {
   background-image: linear-gradient(#464646,#000000); 
  border: #2C8D9B solid 1px;
}

#content {
  box-shadow: #2C8D9B !important;
}

#content {
  box-shadow: none;
}

#footer .content .game .massive {
  color: #888888;
}

#footer .content {
  background-image: linear-gradient(#0F1661,#070922) !important;
  border: 1px solid #2C8D9B;
  border-radius: 2 px;
}

#footer .content .game .description {
color: #2FB0E2;
}

#footer .content h3 {
  color: white;
}

#header {
  background-color: black;
}

.block {
    background-color: #02062d;
    border: 2px solid #2C8D9B;
}

.yesterday-top .top .avatar-link img {
   border: rgb(3, 124, 255) solid 2px;
   border-radius: 5px
}

     
.presentation.logged-in {
   background-image: url('http://zupimages.net/up/14/44/eu3y.png');
   border: 2px solid #2C8D9B;
}

.presentation .news-feed li {
      background-color: black;
      border: 2px solid #2C8D9B;
      color: #B0C2CC;
}

.block h2 {
  color: #DBDBDB;
  }
  
.block .preTitle {
  color: #2C8D9B;
      }

.presentation .news-feed h3 {
      color: #B0C2CC;
}

.presentation .online-friends {
          color: #B0C2CC;        
 }

.presentation .online-friends h3 {
          color: #B0C2CC;
}
.presentation .online-friends ul {
border: 2px solid #B0C2CC;
    
}
.enter-game {
  background-image: url('http://image.noelshack.com/fichiers/2014/44/1414874751-enter-game.png');
                border: solid 2px #2C8D9B;
              }
.enter-game #total_player_count {
    color: #B0C2CC;
  }
.enter-game select {
      background-color: #02062d;
      color: #CCCCCC;
}
.enter-game input[type=submit] {
    background-image: linear-gradient(#5abaed,#1175aa);
    text-shadow: 0px -1px #2E2C4B;
  }
  
.enter-game input[type=submit]:hover {
    background-image: linear-gradient(#1175aa,#5abaed);
    text-shadow: 0px -1px #2E2C4B;
  }
  
.presentation .premium_section {
    background-image: linear-gradient(#FFFFFF,#9C9C9C); 
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
          
.presentation .premium_section:hover {
    background-image: linear-gradient(#9C9C9C,#FFFFFF);  
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
                
.enter-game .signup .more-link {
     background-image: linear-gradient(#FFFFFF,#9C9C9C); 
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
                      
.enter-game .signup .more-link:hover {
     background-image: linear-gradient(#9C9C9C,#FFFFFF); 
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
#logged-submenu .handle {
  background-color: #02062d;
}


#menu > ul > li > a {
  color: white;
  text-shadow: 0px -1px #2E2C4B;
 
}
#menu {
   border-bottom: none;
  ;
}


/*PROFIL*/
.id-card img[src*="/users/"] {
  border-radius: 5px;
  }
.id-card {
    padding: 10px;
  }
  
.friends-and-badges p img {
    border-radius: 5px;
    }
.wall-posts table .avatar-cell img {
        border-radius: 5px;
      }
.wall-posts table .comment {
          color: #B0C2CC;
      }
        
.scores-and-profile .tab.scores .user_cat_desc .category {
   color: #B0C2CC;
      }
.scores-and-profile .tab.scores .user_cat_desc .score {
     color: #B0C2CC;
     }

.orange-button {
     background-image: linear-gradient(#9C9C9C,#FFFFFF);
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
  
.orange-button:hover {
      background-image: linear-gradient(#FFFFFF,#9C9C9C);  
    color: rgb(54, 54, 54) !important;
    text-shadow: none;
    border: solid 1px rgb(54, 54, 54);
  }
 #logged-submenu ul { 
  background-color: #02062d;
  box-shadow: none;
  border: 1px solid #bebebe;
}

/*CLASSEMENTS*/

.highscore-list td a.more {
    background-color: #19142E;
  }
.highscore-list td .cell-wrapper {
      background-color: black;
    }

body table tr.even td {
  background: #07051B;
}
  

/*IN GAME*/

.block.game {
      background-color: #02062d;
    }
    
.block.game h2 {
     -webkit-box-shadow: inset 0px -20px 5px #000;
        }

#past_songs {
      width: 200px;
      background-color: rgba(244, 244, 244, 0);
      border: white 1px solid;
      color: white;
          }
#past_songs_wrapper {
      color: white;
        }
#past_songs h3 {
      color: white;
        }
        
#past_songs_body img[src*="/images/picture/"] {
          border-radius: 5px;
        }
        
.block.game .tab-handles > div {
        background: rgba(14, 14, 14, 0);
        border: 1px solid white;
        color: white;
              }
#guess_zone {
        background: rgba(244, 244, 244, 0);
        color: white;
        border: white 1px solid;
                }
#guess_zone #statusImage.nothing {
        border-radius: 5px;
              }
#guess_zone #statusImage.guess {
        border-radius: 5px;
              }
#guess_zone #statusImage.both {
        border-radius: 5px;
              }
#guess_zone #statusImage.field_one {
        border-radius: 5px;
              }
#guess_zone #statusImage.cup3 {
        border-radius: 5px;
              }
#guess_zone #statusImage.cup1 {
        border-radius: 5px;
              }
#guess_zone #statusImage.cup2 {
        border-radius: 5px;
              }
#guess_zone #statusImage.field_two {
        border-radius: 5px;
              }

.ranking .others {
        background-color: #464646;
 }
.ranking h3 {
        background-color: #464646;
        color: white;
 }
      
.ranking h3 {
 -webkit-box-shadow: inset 0px -3px 5px #222;
          }
#guess_form .input-wrapper input[type=submit] {
      background-image: linear-gradient(#5abaed,#1175aa);
        text-shadow: 0px -1px #2E2C4B;
        
      }
#guess_form .input-wrapper input[type=submit]:hover {
      background-image: linear-gradient(#1175aa,#5abaed);
          text-shadow: 0px -1px #2E2C4B;
      }
}
