css:
@-moz-document domain(meduza.io) {

/* white mode */

body.white {
	background-color: #fff;
	-webkit-transition: background-color .25s;
}

body.white .Page>.Nav {
	background: rgba(255,255,255,.98) !important;
}

body.white .Page>.Nav .Logo {
	background-image: url(data:image/svg+xml;base64,) !important;
}

body.white .Page>.Nav .Switcher.Switcher--white {
	background: #000;
}

body.white .Page>.Nav .Switcher.Switcher--white .Switcher-knob {
	background: #fff;
}

body.white .Page>.Nav a {
	color: #000;
}

body.white .Page>.Nav a.Link--active {
	color: #B38D59;
	/*border-bottom: 2px solid #B38D59;*/
}

/* cards */

body.white .Preview.Preview--card {
	background-color: #ddd;
}

body.white .Preview.Preview--card .Preview-title {
	color: #000;
}

body.white .Preview.Preview--card .Preview-chaptersCount {
	color: #000;
}

body.white .Preview.Preview--card .CardsThesis {
	color: #000;
}

}
