css:
/* Created by ultra */
@namespace"http://www.w3.org/1999/xhtml";
@-moz-document domain("nulled.cc") {
html{background:url(http://hq-oboi.ru/photo/golubaya_abstrakciya_fon_na_rabochiy_stol_1920x1080.jpg) repeat-x center fixed #003e75;background-position:center;background-attachment:fixed}
body{color:#fff}
.EstBS-bnContent,.shit_top_block{display:none}
#content .pageContent{background:rgba(0,0,0,.23)}
#taigachat_full #taigachat_box{height:500px}
.navTabs{border:none}
.navTabs .navTab.selected .navLink{background:none;color:#fff;border:none;padding-top:0;margin-top:0}
#logo{display:none}
#logo img{vertical-align:middle;max-width:100%;top:-100px;margin-top:-500px}
.navTabs .navTab.selected .tabLinks{background:none}
.navTabs .navTab.selected .tabLinks a{color:#dedede;padding:0 9px;font:11px arial}
.Popup .PopupControl:hover,.Popup.PopupContainerControl:hover{background:none}
.navTabs .navTab.PopupClosed:hover{background:none}
.navTabs .navTab.PopupOpen .navLink{color:#FFF}
.Popup .PopupControl.PopupOpen,.Popup.PopupContainerControl.PopupOpen{background:none;border:medium none;color:#FFF;text-shadow:0 0 0 rgba(0,0,0,0),1px 1px 2px #FFF}
.Menu .primaryContent,.Menu .secondaryContent{background:#262626}
#headerMover #headerProxy{height:51px}
#headerMover{position:relative;margin-top:20px}
.secondaryContent,.primaryContent{background:none;border:none}
.Menu .menuHeader h3{font-size:14pt;color:#6cb2e4}
.blockLinksList label{color:#e1e1e1}
.blockLinksList a:hover,.blockLinksList a:focus,.blockLinksList li.kbSelect a,.blockLinksList label:hover,.blockLinksList label:focus,.blockLinksList li.kbSelect label{text-decoration:none;background:none;color:#FFF;text-shadow:0 0 0 rgba(0,0,0,0),1px 1px 2px #FFF}
.blockLinksList a,.blockLinksList label{color:#d5d5d5}
.navTabs .navTab.selected .tabLinks a:hover,.navTabs .navTab.selected .tabLinks a:focus{color:#FFF;text-decoration:none;background:none;padding:0 9px;border:none;border-radius:5px;text-shadow:none;outline:0 none}
.navPopup .PopupItemLinkActive:hover{margin:0 -8px;padding:5px 8px;border:none;cursor:pointer;background:#1d1d1d}
.Menu .secondaryContent{color:#FFF}
.secondaryContent a{color:#a3c3da}
.Menu .sectionFooter{background:#1e1e1e}
.sectionFooter{color:#65a5d1;background:none;border-bottom:0}
.formPopup{width:250px;background:none}
.formPopup .textCtrl,.formPopup .button{width:244px}
#QuickSearch{display:block;position:absolute;right:20px;top:-25px;margin:0;border-radius:2px;padding-top:2px;z-index:7500;background:#121212}
.formPopup .primaryControls input.textCtrl{background:#262626;color:#FFF;border:none}
.formPopup .controlsWrapper{background:none;border:none;padding:5px;margin:0;font-size:11px;color:#FFF}
.formPopup .primaryControls{white-space:nowrap;word-wrap:normal;padding:0;background:none}
.button{color:#000;background:#f3f3f3;padding:0 6px;border-radius:2px;box-shadow:none;text-shadow:none;border:none}
#QuickSearch input.button.primary{float:left;width:105px}
#QuickSearch .moreOptions{width:102px}
.breadcrumb{background:none;border:none}
.estPD_info_block .estPD_info_block_content{padding:0;border:none;background:rgba(0,0,0,0)}
.sectionMain{padding:0;margin:0 auto;border:none;background:rgba(0,0,0,0)}
.estPD_info_block .estPD_top_collapser{font-size:11px;color:#fff;height:10px;border:none;background:rgba(0,0,0,0)}
.estPD_info_block_collapsed{font-size:11px;color:#FFF;padding:4px 6px;margin-top:20px;margin-bottom:20px;border-radius:2px;position:relative;cursor:pointer;text-align:center;height:14px;background:rgba(0,0,0,.3)}
#taigachat_box{border:none;padding:5px;background:none}
.taigachat_messagetext{color:#FFF}
.emCtrl:hover,.emCtrl:focus,.ugc a:hover,.ugc a:focus{text-decoration:none;box-shadow:none;outline:0 none}
.ugc a:hover,.ugc a:focus{color:#bacaff;background:none}
a:link,a:visited{color:#96c4ff;text-decoration:none}
.textCtrl{color:#fff;padding:3px;margin-bottom:20px;border:none;background:rgba(0,0,0,.27)}
.textCtrl:focus,.textCtrl.Focus{background:rgba(0,0,0,.27);border:none;color:#FFF}
.taigachat_msg_pm{background:rgba(0,0,0,.43)!important}
.taigachat_msg_um{background:rgba(29,29,29,.27)!important}
.tabs{background:none;border-bottom:none}
.tabs li a,.tabs.noLinks li{color:#fff;background:none;border:none}
.tabs li.active a,.tabs.noLinks li.active{background:none;text-shadow:0 0 0 rgba(0,0,0,0),1px 1px 2px #FFF}
.tabs li:hover a,.tabs.noLinks li:hover{text-decoration:none;background:none}
.estsp_donation_form{background:none;padding:5px;margin:10px auto;border-bottom:none;position:relative}
.estsp_donation_form .estsp_donation_announcement{color:#e9e9e9;padding:0;margin-top:5px;border-radius:0;background:rgba(0,0,0,0)}
.estsp_donation_form .estsp_donation_history{color:#2b485c;padding:0;margin-top:10px;border-radius:0;height:200px;background:rgba(0,0,0,0)}
.nodeList .categoryStrip{font-size:11px;color:#000;padding:5px 10px;margin:0;border-radius:2px;border:medium none;min-height:6px;background:rgba(0,0,0,.25)}
.nodeList .categoryStrip .nodeTitle a{color:#fff;font-size:12pt}
.nodeList .CategoryStripCollasped{font-size:12pt;color:#fff;background:rgba(0,0,0,.38);padding:5px 10px;margin:0;border-top:0;border-bottom:0;border-radius:2px;min-height:6px}
.nodeList .CategoryStripCollasped .nodeTitle a{color:#FFF;font-size:12pt}
.nodeList .categoryForumNodeInfo,.nodeList .forumNodeInfo,.nodeList .pageNodeInfo,.nodeList .linkNodeInfo{border-bottom:0;padding:0;background:rgba(0,0,0,.26)}
.node .nodeLastPost{background:rgba(0,0,0,.38);font-size:11px;padding:3px 10px;margin:10px;border:none;color:#FFF}
.footer .pageContent{background:rgba(0,0,0,.22)}
.est-forum-top{background:rgba(0,0,0,.23)}
.est-forum-top .est-left{text-shadow:none}
.est-forum-top .est-right{color:#fff;background:rgba(0,0,0,.15)}
a.callToAction span{background:rgba(0,0,0,.33);padding:0 50px;border:none}
a.callToAction{background:none;border:none}
.discussionList .sectionHeaders{font-size:11px;color:#000;background:rgba(0,0,0,.3);margin:3px auto 0;border-top:0;border-bottom:0;padding:0}
.discussionList .sectionHeaders a{color:#fff}
.discussionListItem.visible.sticky,.discussionListItem.visible.sticky .posterAvatar,.discussionListItem.visible.sticky .stats{background:rgba(0,0,0,.29);font-style:italic}
.discussionListItem{border-bottom:0;background:rgba(0,0,0,.3)}
.discussionListItem .posterAvatar,.discussionListItem .stats{background:rgba(0,0,0,.35);color:#FFF}
li.locked,li.locked .stats,li.locked .posterAvatar{background:rgba(0,0,0,.56)}
#DiscussionListOptionsHandle a{border:none;background:rgba(0,0,0,0)}
.PageNav a{border:none;background:rgba(0,0,0,.41)}
.PageNav a.currentPage{background:rgba(0,0,0,.5)}
.breadcrumb .crust a.crumb>span{color:#000}
.navPopup .listItem{overflow:hidden;padding:5px 0;border-bottom:0}
.pairs dt,.pairsInline dt,.pairsRows dt,.pairsColumns dt,.pairsJustified dt{color:#fff}
.messageUserBlock div.avatarHolder{background:rgba(0,0,0,0)}
.messageUserBlock h3.userText{background:rgba(0,0,0,0)}
.messageUserBlock .extraUserInfo{background:rgba(0,0,0,0)}
.messageUserBlock{border:none;border-radius:5px;position:relative;background:rgba(0,0,0,.29)}
.messageUserBlock .arrow{display:none}
.redactor_box{font-size:0;border:none;line-height:0;position:relative;overflow:hidden}
.redactor_toolbar{background:rgba(0,0,0,.39)}
.bbCodeQuote .attribution{color:#fff;background:rgba(0,0,0,.3);border-bottom:0}
.bbCodeQuote{border:none}
.bbCodeQuote .quoteContainer{background:rgba(0,0,0,.53)}
.sidebar .avatarList .userTitle{color:#fff}
}
