css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
body {
background: #212121 !important;
}

a:link, a:visited, a:active {
color: #ffffff !important;
text-decoration: none !important;
}

a:hover {
  color: #F2F2F2 !important;
}

.identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content {
background-color: transparent;}
.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
background-color: transparent;}
.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
background-color: #212121 !important;
width: 229px;
}

.l-header-container--refresh {
box-shadow: 0 0 0 1px rgba(255,255,255,.13);
-webkit-transform: translateZ(0);
-moz-transform: translateZ(0);
-ms-transform: translateZ(0);
-o-transform: translateZ(0);
transform: translateZ(0);
z-index: 90;
background-color: #161616;
position: fixed;
width: 100%;
box-shadow: 0px 0px 10px #000000;
}

.identity-refresh .l-container.l-container--flex .l-content {
background-color: transparent;
}

.identity-refresh .post_full .post_permalink {
border-color: #212121 #212121 transparent transparent;
border-radius: 0 0 0 5px;
border-style: solid;
position: absolute;
border-width: 2px;
background: #ccc;
display: block;
opacity: 0;
height: 0;
width: 0;
right: 0;
top: 0;
}

.identity-refresh .plexi.show.color {
background: transparent;
}

.identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue {
background: transparent;
}

.post_avatar, .post_avatar_link, .search_form_row { background-color: transparent!important; }

.controls_section.user_list li a:before { content: none!important; }
.controls_section .item:hover .icon_plus, .identity-legacy .controls_section .item:hover .icon_plus { color: #fff; }
div.ui_follow:hover, .controls_section li .follow:hover, .identity-legacy div.ui_follow:hover, .identity-legacy .controls_section li .follow:hover { background: rgba(80,80,80,0.8); 
border-radius: 0px !important;}

.right_column:after { content: none!important; }

a.radar_avatar:before { content: none!important; }

.fullscreen_post_bg {
display: none !important
}

.notifications_page .section.divider {
padding-bottom: 8px;
background: transparent url("http://i96.photobucket.com/albums/l172/jaygold56/Tumblr%20Stuff/Logo%20and%20Stuff/divider_zps2b8e3ac0.png") 0 bottom no-repeat;
}

.l-header-container--refresh .tab_notice {
color: #212121;
right: auto;
left: 31px;
top: -4px;
}

.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
border: 2px solid #212121;
border-radius: 4px;
min-width: 21px;
height: 21px;
}

.identity-refresh .tab_notice .tab_notice_value {
color: #212121;
}

.identity-refresh .search_landing_inner_full_wrapper:before {
background-color: #161616;
}


.post_avatar, .post, #search_query, .search_popover, .post_avatar_link, .control-item, .control-anchor, .posts, ul, .controls_section_item , .post_control, .controls_section_item, hr  {
border-radius: 0px !important;
background: transparent;
  border: 0px;
}

.post_avatar, .post_avatar_link, .avatar {
border-radius: 50px !important;
box-shadow: 0px 3px 10px #000000;
}

#right_column .controls_section li:first-child{
    border-radius: 0px !important;
background: #212121 !important;
  border: 0px;
}

img {
    border-radius: 0px !important;
}

#right_column .controls_section li:last-child, .radar_controls {
    border-radius: 0px !important;
	background:  #212121 !important;
  	border: 0px !important;
}


    
    
.controls_section_item, #posts_control {
        border: 0px !important;}
    


.post, #new_post, .controls_section, .post_body, .post_title, .clearfix, .to_label, .toast_inner {
	background-color: transparent !important;
	color: whitesmoke !important;
  	border: 0px !important;
}
  
.note_item {
  background: #161616 !important;
      border-radius: 0px !important;
  border: 1px solid #000000 !important;
      border-color: #000000 !important;
    }

.nipple { visibility: hidden !important;}
  
#permalink, .post_permalink {
    color: #000000;
  }

.avatar {
    box-shadow: 0 0 0;
  }

hr {
  width: 0px;
  height: 0px;
}

.search_results_section, #popover_search, .scrollable_container, .post_form_wrapper_inner, .ask_form {
  background: #161616 !important;
      border-radius: 0px !important;
      border-color: #000000 !important;
  }

.popover_inner, #popover_search, #question {
  border: 1px solid #000000 !important;
  }
    
 #question {
      background: #000000 !important;
      color: #ffffff !important;
    }
    
#ask_button{
      background: #000000 !important;
      border-color: #000000 !important;
    }

.identity, .identity-refresh, .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .identity-refresh .controls_section li:not(.section_header):hover {
  background: transparent;
}

.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_full .post_permalink {
  border-color: transparent transparent #000 #000;
  background: transparent;
}

.compose-button {
  color: #383838;
}

.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .tab_notice .tab_notice_value {
  color: #212121;
  border-color: #212121;
}

.plus-follow-button:hover, .identity-refresh .controls_section .item:hover .follow:hover, .compose-button {
  background-color: #aaa;
  border-color: #aaa;
}

#dashboard_controls a, #dashboard_controls .control {
  color: #aaa;
  text-shadow: #333 1px 1px 1px;
}

#pagination a {
  color: #aaa;
}

#pagination a:hover {
  color: #ccc;
}

.post_tags::after {
      background: transparent !important;
    }

#sidebar_footer_nav::before {
  display: hidden !important;
}


.post_tag, .post_tags_inner, .post_tags {
  font-size: 10px !important;
  }

.explore_topics_link:hover {
  background: #000000 !important;
    }
    
.popover_scrollbar {
  margin-top: -6px !important;
  border-radius: 0px !important;
      }
      
.popover_inner{
      margin: 0px !important;
        }
    
.popover_menu_item:hover, .result_link.search_typeahead:hover, .result_link:hover {
        background: #000000 !important;
        color: #ffffff !important;
        
          }
}

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
.popover, .popover_content_wrapper {
    width: 280px !important;
  }

.popover, .popover_content_wrapper, .poptica_header {
  	background: #212121 !important;
    border: 0px solid !important;
    border-color: transparent !important;
    border-radius: 0px !important;
    box-shadow: 0px 1px 3px #000000;
    color: #ffffff !important;
  }


.recent_posts, .info_wrapper, .indash_header_wrapper {
    background: #161616 !important;
  	border: 0px !important;
    }
    
.avatar.square {
  	border: 0px !important;
    border-color: #161616 !important;
      }

.chrome.unfollow, .chrome.follow {
      background: #161616 !important;
      color: #ffffff !important;
    }

.chrome.unfollow:hover, .chrome.follow:hover {
      box-shadow: 0px 1px 5px #000000 !important;
      }

.info_popover.popover.popover_gradient, .popover_options {
      background: transparent !important;
    }

.popover_options {
      border: 0px !important;
      border-radius: 0px !important;
        }

.popover li:hover {
          background: #161616 !important;
          border-radius: 0px  !important;
        }

.popover_inner {
  background: #000000 !important;
  }

.fan_mail_menu_item span {
          background: transparent !important;
              }

.description_gradient {
    color: #ffffff !important;
    background: #161616 !important;
  }

.title, .description {
    color: #ffffff !important;
  }
  
.post_avatar_link img {
      border-radius: 50px !important;
    }



.post_micro {
  width: 10px;
  }

.popover.popover_menu.popover_gradient.popover_share_social, .popover_inner {
  		  background: transparent !important;
  color: #ffffff !important;
          border: 0px !important;
        }

.popover_inner ul {
  box-shadow: 0px 0px 3px #000000 !important;
  }

.share_options.active, .popover_options li, .popup_inner li, .popover_options ul {
  color: #ffffff !important;
  border-radius: 0px !important;
      margin-top: -4px !important;
              background: #000000 !important;
            }
}

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
.chat_line {
background: #000000 !important;
padding: 5px !important;
}
  
.chat_line:nth-child(odd) {
background: #161616 !important;
}
      

          
.post_answer_length {
background: #000000 !important;
color: #ffffff !important;
}
          
div.post_answer_form_field::after, div.post_answer_form_field::before {
display: none !important;
}
            
.post_answer_submit.chrome.blue.big, .chrome.blue {
background: #161616 !important;
border: 1px solid #000000 !important;
}

pre {
background: #161616 !important;
}

.post_header_button.post_header_follow.chrome.blue.follow {
border: 1px solid #000000 !important;
}

.post_dismiss_tooltip {
background: transparent !important;
}             

.reply_text {
background: #000000 !important;
}

.popover.popover_gradient.popover_post_tools {
background: #000000 !important;
border-radius: 0px !important;
border: 1px solid #000000 !important;
box-shadow: 0px 0px 5px #000000 !important;
}

.popover.popover_gradient.popover_post_tools.popover_inner {
margin-top: -4px !important;
}

.popover_inner.textarea {
background: #212121 !important;
border-radius: 0px !important;
color: #ffffff !important;
}
  
.popover_inner {
border: 0px solid !important;
border-radius: 0px !important;
border-top:  0px solid !important;
}

.chrome {
background: #000000 !important;
border: 1px solid #000000 !important;
}

.chrome:hover {
background: #0F0F0F !important;
border: 1px solid #000000 !important;
}
}

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
.white_pane, .left_column.followers, .notification_pane, .section.first.divider, .left_column.notifications_page, .ui_note, .is_reblog, .date_header.clearfix {
background: transparent !important;
border: 0px !important;
}

.is_friend {
border: 0px !important;
color: #ffffff !important;
background: #161616 !important;
}
  
input, form, .text_field, #user_to_check_for {
border-radius: 0px;
}

.wrapper, .button, .stage {
color: #ffffff !important;
background: #000000 !important;
              }

.plop::before {
color: #161616 !important;
      }

.header:active {
border: 1px solid #ffffff !important;
    }

div.date_header.clearfix {
border: 0px !important;
border-top: 0px !important;
}

.anchor, .anchor:link, .toast_inner {
color: #ffffff !important;
background: #161616 !important;
  }
    
    
.v_center_outer {
 background: rgba(0,0,0,.8) !important;
      }
 

.plexi.color.active.show {
 background: rgba(0,0,0,.8) !important;
      }

.borderless.full.title.optional, .borderless.full.title.resize_y, .borderless.full.title, .bordertop.full.title.optional {
color: #000000 !important;
              }

.popover_header {
color: #ffffff !important;
background: #161616 !important;
border-radius: 0px !important;
          }
}

@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
.post_form_wrapper_inner {
background: #ffffff !important;
color: #000000 !important;
}

.form_wrapper {
padding: 10px !important;
background: #ffffff !important;
color: #000000 !important;
}

#post_header, .to_label, #character_count, .anonymous_wrapper {
color: #000000 !important;
}

.main_content.ask_form, #question, textarea {
background: #ffffff !important;
-moz-box-shadow: inset 0 0 10px #888;
-webkit-box-shadow: inset 0 0 10px #888;
box-shadow: inner 0 0 10px #888;
border: 0px !important;
border-radius: 3px !important;
color: #000000 !important;
}

.post_permalink {
background: transparent !important;
}

.tag.search_results_section, .search_subheading {
width: 277px !important;
          }

.tumblelog_mask.right {
background: #000000 !important;
              }

.notes_container.popover_inner {
width: 355px !important;
padding: 0px !important;
margin: 0px !important;
background: #161616 !important;
box-shadow: 0px 0px 5px #000000 !important;
                  }

.notes ol, .note, .reblog, .like, .reply{
list-style: none;
border: 0px !important;
}
 .note.reblog:hover, .note.like:hover, .note.reply:hover {
background: #000000 !important;
                        }

.notes_outer_container.popover.popover_gradient.nipple_on_left {
width: 0px !important;
height: 0px !important;
                      }

.post.post_full.fan_mail, .post.post_full.fan_mail:link, .post.post_full.fan_mail:active, .post.post_full.fan_mail:visited {
color: #000000 !important;
      }
        
.message {
background: transparent !important;
-moz-box-shadow: inset 0 0 0px #888;
-webkit-box-shadow: inset 0 0 0px #888;
box-shadow: inner 0 0 0px #888;
          }

.message.sans {
background: #ffffff !important;
              }

.info_popover.popover.popover_gradient, .popover.popover_menu.popover_gradient.popover_share_social, .popover_menu_item_anchor {
background: #000000 !important;
width: 120px !important;
                    }

.note_item {
background: #000000 !important;
color: #ffffff !important;
border: 0px !important;
  }

.avatar img {
border-radius: 50px !important;
    }
}
