@-moz-document url-prefix("https://twitter.com"), url-prefix("http://twitter.com") {

@font-face {
    font-family: 'Samim';
    src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v4.0.5/dist/Samim.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v4.0.5/dist/Samim.eot?#iefix') format('embedded-opentype'),
		 url('https://cdn.rawgit.com/rastikerdar/samim-font/v4.0.5/dist/Samim.woff') format('woff'),
		 url('https://cdn.rawgit.com/rastikerdar/samim-font/v4.0.5/dist/Samim.ttf') format('truetype');
    font-weight: normal;
}

div[lang="fa"] .r-1qd0xha 
  {
  font-family:Samim !important;
  font-size:15px;
}

header[role="banner"]{
    background:#80a8cc
}

main[role="main"]{
    background:#f2eee9
}

.r-14lw9ot,.r-1l5qxre{
    background: #fefbf5;
}

.r-1u4rsef {
	border-radius:0;
    background:none;
}
.r-156q2ks svg g{
    color:#80a8cc;
}
.r-156q2ks div:hover svg g{
    color:#4b6f8e;
}
.r-e84r5y {
    background-color: #f2eee9;
}
.r-1hycxz .r-sdzlij,.r-1f0042m {
   border-radius:0;
    margin:0 5px;
}
header[role="banner"] .css-901oao,
nav[role="navigation"] svg g,
h1[role="heading"] svg g{
    color:#4b6f8e
}
div[role="menu"] a span,
div[role="menu"] a svg g{
    color:#80a8cc
}

header[role="banner"] [role="link"]:hover .css-901oao,
header[role="banner"] [role="button"]:hover .css-901oao,
nav[role="navigation"] a:hover svg g,
nav[role="navigation"] div:hover svg g,
h1[role="heading"]:hover svg g,
div[role="menu"] a:hover svg g
{
    color:white
}
header[role="banner"] [role="link"]:hover>div{
    background:none;
}
.r-1re7ezh,.r-1n1174f{
    color:#4b6f8e
}
header[role="banner"] [role="link"]:hover .r-urgr8i {
    color:#4b6f8e
}
header[role="banner"] [role="link"] .r-urgr8i {
    background:white
}

header[role="banner"] a[aria-label="Tweet"],.r-urgr8i {
    background:#4b6f8e;
}
header[role="banner"] a[aria-label="Tweet"] .css-901oao{
    color:white
}
div[role="menu"]{
    background:#4b6f8e;
    color:white
}
div[role="menu"] a:hover{
    background:none;
    
}
div[role="menu"] a:hover span{
    color:white
}

}
