css:
/*@import url(http://fonts.googleapis.com/css?family=Raleway);
@import url(http://fonts.googleapis.com/css?family=Ubuntu+Mono);*/

/* Uncomment ^ userstyles.org doesn't like imports but Google Fonts is pretty fast*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("ulillillia.us") {
	/* Credits for cool stuff check it */
	/* Neat gradients from http://www.colorzilla.com/gradient-editor/ */
	/* Fancy inset text from http://www.css3generator.in/text-shadow.html */
		
	html {
		background-color: #999 !important;
		background-repeat: no-repeat !important;
		background-attachment: fixed !important;
		background-size: 100% !important;
	}
	body {
		/* Setting a background with the background-origin: border-box property give you a border background! */
				
		background:linear-gradient(to bottom, rgba(228, 245, 252, 1) 0%, rgba(191, 232, 249, 1) 50%, rgba(159, 216, 239, 1) 51%, rgba(42, 176, 237, 1) 100%) !important;
		background-origin: border-box !important;
		background-repeat: no-repeat !important;
		background-size: auto 64px !important;
		border: 3px solid rgba(244, 244, 244, 0.9) !important;
		border-top: 64px solid rgba(0, 0, 0, 0.0) !important;
		border-radius: 32px !important;
		border-bottom-right-radius: 0px !important;
		border-bottom-left-radius: 0px !important; /* You can use a background and background-color, this color is the actual background */
		background-color: rgba(216, 216, 216, 0.8) !important; /* Fancy inset text */
		color: rgb(97, 97, 97);
		text-shadow: rgb(224, 224, 224) 1px 1px 0px;
		/* box-sizing: border-box !important;
		don't think I need this */
		position: absolute !important;
		min-width: 600px !important;
		left: 240px !important;
		right: 240px !important;
		padding-right: 12px !important;
		padding-left: 12px !important;
	}
	
	/* Announcements (not a good selector for this tbh */
	span > b {
		font-family: "Raleway" !important;
		color: #52A357 !important;
	}
	
	/* unvisited link */
	a:link {
		color: #4A5987 !important;
	}
	
	/* visited link */
	a:visited {
		color: #856094 !important;
	}
	
	/* mouse over link */
	a:hover {
		background-color: rgba(0, 0, 0, 0.0) !important;
		color: #768EDB !important;
	}
	
	/* selected link */
	a:active {
		color: #0000FF !important;
	}
	
	/* First level header X */
	h1 {
		background: linear-gradient(to bottom, #b8e1fc 0%, #a9d2f3 10%, #90bae4 25%, #90bcea 37%, #90bff0 50%, #6ba8e5 51%, #a2daf5 83%, #bdf3fd 100%) !important;
		font-family: "Raleway" !important;
		color: rgb(97, 97, 97) !important;
		text-shadow: rgb(224, 224, 224) 1px 1px 0px !important;
		color: #364232 !important;
		border-radius: 16px;
		background-color: rgba(64, 64, 255, 0.8) !important;
	}
	
	/* Second level header 1.X */
	h2 {
		background: linear-gradient(to bottom, #6db3f2 0%, #54a3ee 50%, #3690f0 51%, #1e69de 100%) !important;
		color: #364232 !important;
		text-shadow: rgb(164, 164, 164) 1px 1px 0px !important;
		font-family: "Raleway" !important;
		border-radius: 12px;
		background-color: rgba(64, 64, 255, 0.6) !important;
	}
	
	/* Third level header 1.1.X */
	h3 {
		background: linear-gradient(to bottom, #e4f5fc 0%, #bfe8f9 50%, #9fd8ef 51%, #2ab0ed 100%) !important;
		border-radius: 10px !important;
	}
	
	/* This is used for the scale on the side of some blog posts */
	html > body> table {
		/* Same trick to get top border with neat gradient as used in the body */
		background: linear-gradient(to bottom, rgba(228, 245, 252, 1) 0%, rgba(191, 232, 249, 1) 50%, rgba(159, 216, 239, 1) 51%, rgba(42, 176, 237, 1) 100%) !important;
		background-origin: border-box !important;
		background-repeat: no-repeat !important;
		background-size: auto 32px !important;
		border: 3px solid rgba(244, 244, 244, 0.9) !important;
		border-top: 32px solid rgba(0, 0, 0, 0.0) !important;
		border-radius: 16px !important;
		border-bottom-right-radius: 0px !important;
		border-bottom-left-radius: 0px !important; /* You can use a background and background-color, this color is the actual background */
		background-color: rgba(216, 216, 216, 0.8) !important; /* Fancy inset text */
		color: rgb(97, 97, 97);
		text-shadow: rgb(224, 224, 224) 1px 1px 0px;
		font-family: 'Ubuntu Mono' !important;
		top: 8px !important;
		position: fixed !important;
		right: 8px !important;
		width: 200px !important;
	}
	td {
		padding: 6px !important;
	}
	
	/* Move the home button to the top left for easy access */
	[src = "http://www.ulillillia.us/images/home.GIF"] {
		position: fixed !important;
		top: 30px !important;
		left: 45px !important;
	}
	[src = "http://www.ulillillia.us/images/FAQ.gif"] {
		position: fixed !important;
		top: 180px !important;
		left: 45px !important;
	}
	
	/* Used in code snippets */
	pre {
		color: #39C9BB !important;
		text-shadow: none !important;
		font-family: 'Ubuntu Mono' !important;
	}
}
