css:
@-moz-document url("http://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.post.new_post .post_buttons {
padding: 0 3px 0 5px;}

.identity-refresh .post_avatar .post_avatar_link{margin-top:0px;}
.new_post_label {
padding: 75px 1px 8px 0 !important;
font-size: 11px !important;
  text-transform: lowercase;margin-top:0px;}

.new_post_label > I::before {content: none}
A.new_post_label > I {
transition: background-position .08s linear;
height: 90px;
background: url('/*[[color]]*/ /*[[colors]]*/ /*[[grey]]*/') no-repeat !important;
z-index: -1;}

.new_post_label > I {
color: transparent !important;transition: background-position .08s linear !important; top:3px;}

#new_post_label_text > I {background-position: 2px -5px !important;}
#new_post_label_photo > I {background-position: -73px -5px !important;}
#new_post_label_quote > I {background-position: -148px -5px !important;}
#new_post_label_link > I {background-position: -223px -5px  !important;}
#new_post_label_chat > I {background-position: -298px -5px !important;}
#new_post_label_audio > I {background-position: -373px -5px !important;}
#new_post_label_video > I {background-position: -448px -5px  !important;}

#new_post_label_text:hover > I {background-position: 2px -9px !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_photo:hover > I {background-position: -73px -9px !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_quote:hover > I {background-position: -148px -9px !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_link:hover > I {background-position: -223px -9px  !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_chat:hover > I {background-position: -298px -9px !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_audio:hover > I {background-position: -373px -9px  !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}
#new_post_label_video:hover > I {background-position: -448px -9px  !important;-webkit-animation-name: shakeybakey;
	-webkit-animation-duration: 0.8s;
	-webkit-transform-origin:50% 50%;
	-webkit-animation-iteration-count: infinite;
	-webkit-animation-timing-function: linear;}

@-webkit-keyframes shakeybakey {
	0% { -webkit-transform: translate(2px, 1px) rotate(0deg); }
	10% { -webkit-transform: translate(-1px, -2px) rotate(-1deg); }
	20% { -webkit-transform: translate(-3px, 0px) rotate(1deg); }
	30% { -webkit-transform: translate(0px, 2px) rotate(0deg); }
	40% { -webkit-transform: translate(1px, -1px) rotate(1deg); }
	50% { -webkit-transform: translate(-1px, 2px) rotate(-1deg); }
	60% { -webkit-transform: translate(-3px, 1px) rotate(0deg); }
	70% { -webkit-transform: translate(2px, 1px) rotate(-1deg); }
	80% { -webkit-transform: translate(-1px, -1px) rotate(1deg); }
	90% { -webkit-transform: translate(2px, 2px) rotate(0deg); }
	100% { -webkit-transform: translate(1px, -2px) rotate(-1deg); }
}}
