css:
@-moz-document domain("netvibes.com") {
.item-actions.sticky          { width:  75px !important; }
.item-feedinfo-name           { width:  70px !important; }
.item-time                    { right:  70px !important; }
.item-preview-wrapper         { right: 135px !important; }
.smartreader-header-top.right { top:    12px;            }
.nv-reader-item               { padding-left: 15px;      }
div.grey.nv-dropdown.content  { top:    28px !important; }
div.nv-ir-close-feed          { top:    48px !important; }

.group-label {
  height: 20px !important;
  line-height: 14px !important;
  font-size: 11pt !important;
  padding-top: 4px !important;
}

#smartreader-feeds-header {
  height: 30px;
  padding-top: 12px;
}

#analyseLink,
#switch-view,
#universal-zorch-overview,
#universal-zorch,
#userPages,
#smartreader-sidebar-handler.right,
.action-share,
.action-star, .prev-next,
#footer-toggle {
  display: none !important;
}
}
