css:
@-moz-document url-prefix("https://www.tumblr.com") {
      
            .l-header-container--refresh .l-header {
                  min-width: 910px !important;
                  max-width: 910px !important;
                }
                
                
              .identity-refresh .selected .tab_notice, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
  						border: 2px solid #395875 !important;
                        background: #D95E40 !important;
        				left: 32px !important;
						top: -10px !important;
                      }
                      
                 .identity-refresh .tab_notice .tab_notice_value {
                              color: #fff !important;
                            }

.l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .l-content {
        padding: 15px 15px 0px 15px !important;
  margin-top: -20px !important;
      }

.l-header-container--refresh {
    padding-top: 9px !important;
    position: absolute !important;
  }
  
  .elevator-wrapper {
      top: 20px !important;
     right: 20px !important;
    }

    
.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before, .ui_search .search_query {
                border: 0px !important;
                background: #2C4762 !important;
              }



.elevator {
  width: 55px !important;
  height: 55px !important;
    background: rgba(255,255,255,0.22) !important;
    border-radius: 10px !important;
  }
  
  
  .elevator:before {
  font-size: 55px !important;
  line-height: 80px !important;
  color: #2C4762 !important;
      }


.l-header-container--refresh .l-header .compose-button {
        display: none !important;
      }
      
.l-header-container--refresh .tab-bar-container {
            right: -60px !important;
          }

}
