css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(userstyles.org) {

body {margin:15px 0 0 0 !important}
h2, h3, h1, p {margin:0!important;padding:5px 2px 5px 2px!important;color:#426395!important;}
html > body > ul > li {color:#426395!important;}
pre#view-code {background:#ffffee!important;color:#000!important;border: 1px dashed #7594c1!important;margin:5px!important;overflow:auto}

ul {padding:0 0 0 0} 
li a:link, li a:visited, li a:hover, li a:active {text-decoration:none!important;}
a:hover,  a:active {color:#000!important;border-bottom:1px dotted #000!important;}
ul, li, p {font-size:12px!important;}
#navigation {margin:21px 0 0 0!important;padding:0 30px 0 0!important;}
#navigation a:hover {border:1px solid #7594c1!important;border-bottom:0!important;background:#426395!important;color:#fff!important}
#navigation .current-page {border-top: 1px solid #7594c1!important;border-left: 1px solid #426395!important;border-right: 1px solid #426395!important;}

div#logo {margin:0 0 15px 20px !important;border:0!important;background:none!important;padding:0 8px 4px 8px !important;}
div#logo a {color:#426395!important;opacity:0.6!important;}

#main-text {padding-top:10px!important;line-height:1.5!important;}
#style-list {-moz-column-count: 4 !important;}
#style-list li, li {color:#426395!important;padding:2px!important;}
#account-link a {color:#426395!important;text-decoration:none!important;}
#header-bar {background:none!important;border-bottom:1px dotted #7594c1;margin:0 0 10px 0!important;}
#sidebar {margin:0 26px 0 20px!important;padding:7px 0 0 0!important;float:left!important;}

.simple-link-ads, #simple-link-ads {display:none!important;}
.category-style-top ul li a:hover {padding:0!important;color:#000!important;font-weight:bold!important;}
.category-style-top ul li {padding:2px!important;}
.category-style-top ul {margin:0!important;padding:2px!important;background:none!important;color:#fff!important;}

a[class*="poor-rank"] {background:#ccc!important;opacity:1!important;padding:1px!important;-moz-border-radius:2px!important;border: 1px dotted #a00!important;}
img[src*="/images/feed-icon-16x16.png"] {margin-bottom:-3px!important;}

}
