css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dict.leo.org") {

  /*BG*/
 html {
    color: #FFF;
    background: none repeat scroll 0% 0% #000;
    height: 100%;
} 
  
  /*add free*/
  span.gray {
   display: none !important;
  }
 
  /*footer begone*/
  div#footer {
    display:none !important;
  }
  
  
  /*header*/
  #topMenu {
    background: none repeat scroll 0% 0% #000; /*changed value*/
    height: 32px;
    padding-left: 4px;
    z-index: 30;
}
  
  /*below the header/search bar*/
  #searchBar {
    background-color: #000; /*yes*/
    padding-right: 4px;
    padding-left: 12px;
    padding-bottom: 20px;
    margin-bottom: 12px;
    height: 68px;
    position: relative;
    z-index: 10;
}
  
  /*results BG of the headers*/
  .bg-darkyellow {
    background-color: #020;
    color: #FFF;
}
  
  /*result column 1*/
  .tblf-alternate > tbody > tr {
    background-color: #000;
}
  
  /*result column 2*/
  .tblf-alternate > tbody > tr:nth-child(2n) {
    background-color: #010;
}
  
  /*buttons*/
  .button {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0px, rgba(99, 99, 99, 0.2) 100%) repeat scroll 0px 0px #252525; /*yes*/
    border-color: rgba(0, 0, 0, 0.2) rgba(0, 0, 0, 0.2) rgba(0, 0, 0, 0.3);
    border-width: 1px;
    border-radius: 3px;
    text-shadow: 0px 1px rgba(255, 255, 255, 0.75);
    transition: all 0.5s ease 0s;
    cursor: pointer;
    margin-right: 4px;
    -moz-user-select: none;
    white-space: nowrap;
    position: relative;
    padding: 0px 4px;
}
 
  /*2,focussed*/
  
  .button:focus {
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0.5) 0px, rgba(99, 99, 99, 0.5) 100%) repeat scroll 0px 0px #AAA;
    color: #555;
}
  
  /*drop down menu table*/
  /*1*/
  .tblf-alternate-blue > tbody > tr > td {
    border-bottom: 1px solid #252525;
}
  /*2*/
  .tblf-alternate-blue > tbody > tr:nth-child(2n) > td {
    border-bottom: 1px solid #191919;
}
  
  /*dropdown below logo*/
  .bg-darkblue {
    background-color: #151515; /*visible by default*/
    color: #FFF;
}
  /*Forumdiscussions*/
   /*Forum*/
    /*highlight*/
    .bg-lightgray {
    background-color: #010;
    color: #0a0; /*yes (global)*/
    } 
    /*BG*/
    .bg-lightyellow {
    background-color: #000; /*global*/
    color: #090;
    }
    /*borders (of posts)*/
    section {
    border: 1px solid #f00;
    text-align: left;
    margin-top: 20px;
    overflow: hidden;
    }
  /*Javascript alert*/
  .bg-blue {
    background-color: #004404;
    color: #0f0;
  }
  
  /*Searchbar*/
  div#searchContainer {
    background-color: #000;
  }
  .search {
    background-color: #000;
    border-radius: 5px;
    white-space: nowrap;
    padding-left: 10px;
    color: #0a0;
  }
  #search-field:active, #search-field:focus {
    color: #0a0; /*fix: words while typing(focussed)*/
  }
  .search input[type="text"]:focus, .search input[type="search"]:focus {
    outline: 0px auto #000;
}
.search input[type="text"], .search input[type="search"] {
    margin: 2px 0px;
    background-color: #000;
    color: #0a0;
    border: 0px solid #0f0;
    outline: 0px auto #000;
  }
  /*Sidebar*/
   .is-active {
    background-color: #030; /*active for selected modules*/
    }
    /*fix border-top*/
    #right ul li:not(:last-child) {
    border-bottom: 1px solid #C8C8C8;
      border-top: 1px solid #fff; /*fix*/
    }
  /*2n child fix*/
   .section.forumResults table tbody tr:nth-child(4n+3) td, .section.forumResults table tbody tr:nth-child(4n+4) td {
    background-color: #010; /*global*/
   }  
  /*gray fix*/
   .gray {
     color: #040; /*global value*/
   }
  /*hyperlinks (global) */
    a {
    color: #0d0; /* little bit too bright LOL*/
    }
    a:hover {
      text-shadow: 1px 1px 1px #090; /*fix*/
    }
  /*Drop down menus*/
   /*Help & toolbar apps*/
    .dropdown > div > a {
      background-color: #000;
      color: #0a0; /*border could be better*/
    }
     /*hover*/
       .dropdown > div > a:hover {
         background-color: #020;
         color: #0f0;
       }
    /*active dropdown menu header*/
     .dropdown
     span.activeMenu {
       color: #0f0;
       background-color: #010;
       border: 1px solid #0f0;
     }
  /*main drop down menu*/
    /*1st child*/
     .tblf-alternate-blue > tbody > tr {
    background-color: #000;
       color: #0f0 !important;
     }
    /*2nd child*/
     .tblf-alternate-blue > tbody > tr:nth-child(2n) {
    background-color: #010;
    }
     /*text (global)*/
     .tblf-alternate-blue > tbody > tr > td > a {
    color: #0a0;
    }
  
    .bg-yellow {
    background-color: #000;
    color: #0a0;
      border: 1px solid #0a0;
   }
   /*login drop down*/
   .dropdown > div {
    background-color: #000;
    color: #0a0;
    display: none;
    border: 1px solid #0f0;
    box-shadow: none;
    }
  /**/
   .imginputs {
    background-color: #000; /*yes*/
    border: 1px solid #00C800;
    border-radius: 5px;
    box-shadow: 2px 2px 1px #00C800;
    margin: 4px;
    padding-left: 4px;
    display: block;
    max-width: 200px;
    }   
  
     .imginputs > input {
    cursor: text;
    background-color: #000;
    margin: 0px 4px;
    width: 160px;
    border: 0px none;
       color: #0f0;
    }
  /*more footer*/
    .section > footer.more {
    background: -moz-linear-gradient(left center , #030 0px, #010 55%, rgba(0, 50, 0, 0) 100%) repeat scroll 0% 0% transparent;
    position: absolute;
    padding: 1px 4px;
    bottom: 0px;
    left: 0px;
    right: 0px;
    height: 20px;
    line-height: 20px;
   }
  /*layout*/
  #centerColumn {
  position:inherit!important;
  top:-35px!important;
  left:0px!important;
  width:94.5%!important;
  }
}
