css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org"), url-prefix("http://forum.userstyles.org") {
body {
    font-family: Verdana,sans-serif;
    font-size: smaller;
    max-width: 980px !important;
    margin-left: auto !important;
    margin-right: auto !important;
    color: #0a0;
    background: none repeat scroll 0% 0% #000 !important;
}
    #main-header {
    font-family: Trebuchet MS;
    background-color: #010;
    padding: 5px;
    margin-bottom: 10px;
    margin-left: 5px;
    border-radius: 5px;
}
    #left-sidebar > * {
    background-color: #010;
    border-radius: 5px;
    margin: 5px;
    padding: 5px;
}
    a {
        color: #0f0;
    }
    a:hover {
        color: #0a5;
    }
    /*Searchbar*/
    input#search-terms {
        background-color: #000;
        border: 1px solid #0f0;
    }
    input#search-submit {
        background-color: #000;
        border: 1px solid #0a0;
    }
    /*Show CSS*/
       pre {
           background-color: #000;
           border: 1px dashed #0f0;
           color: #0f0;
       }
    /*Buttons*/
       /*install?*/
          .install {
          background-color: #000;
          background-image: linear-gradient(#002200, #030);
          cursor: pointer;
          color: #0f0;
           }
          .install:hover {
          background-color: #004400;
          background: #030;
          border: 1px dashed #0f0;
          }
       /*installed*/
          .install-info {
          background-color: #000;
          background-image: linear-gradient(#005B00, #003400);
          cursor: default;
          color: #0f0;
              border: 1px solid #0f0;
    }
    #screenshots {
        border: 1px solid #0f0;
    }
/*New Style*/
    /*Name*/
    input#style_short_description {
        background-color: #000;
    }
    /*Description*/
    textarea#style_long_description {
        background-color: #000;
    }
    /*additional info*/
    textarea#style_additional_info {
        background-color: #000;
    }
    /*CSS*/
    textarea#css {
        background-color: #000;
    }
    /*example URL*/
    input#example-url {
        background-color: #000;
    }
    /*Style Settings*/
    
/*Forum*/
    #Head {
        background-color: #010;
    }
    input#Form_Search.InputBox {
        background-color: #000;
    }
    .SiteSearch {
        border: 1px solid #0f0;
    }
    .MItem {
        color: #090;
    }
    .ItemContent.Discussion {
        border-bottom: 1px solid #0a0;
    }
    .Tag {
    background: none repeat scroll 0% 0% #040;
    border-radius: 2px;
    color: #FFF;
    padding: 1px 4px;
    line-height: 14px;
    }
    /*read*/
    .Item.Read {
    background: none repeat scroll 0% 0% #010;
    opacity: 0.9;
    }
    /*Actual threads*/
    .ItemDiscussion .Message {
    margin-bottom: 5px;
    font-size: 110%;
    border-bottom: 2px solid #0a0;
    }
    .Comment {
        border-bottom: 1px solid #0a0;
    }
    /*Panel(left)*/
    div#Panel.Column.PanelColumn {
        border: 1px solid #010;
    }    
    
        /*vanilla ad begone*/
    .PoweredByVanilla {
    display: none !important;
    }
        /*buttons*/
    .Button {
    display: inline-block;
    cursor: pointer;
    margin: 0px;
    font-size: 12px;
    line-height: 1;
    font-weight: bold;
    padding: 4px 6px;
    background: -moz-linear-gradient(center bottom , #010 0%, #040 100%) repeat scroll 0% 0% #F8F8F8;
    border: 1px solid #0f0;
    border-radius: 2px;
    white-space: nowrap;
    text-shadow: 0px 1px 0px #00cc00;
    box-shadow: 0px 1px 0px #00cc00 inset, 0px -1px 2px #00aa00 inset;
    color: #0f0; /*inscribed text*/
    }
    /*Test*/
    .Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active {
    border-color: #AAA;
    background: none repeat scroll 0% 0% #000;
    box-shadow: none;
    }
    .Button:hover {
        color: #0a0;
    }
    
    /*leave a comment*/
       .EditCommentForm textarea.TextBox, .CommentForm textarea.TextBox {
    min-height: 50px;
    height: 50px;
           background-color: #000;
           border: 1px solid #0a0;
           color: #0d0;
    }
    /*small fixes (create new style)*/
       input {
           background-color: #000;
       }
}
