css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("410chan.org"){


/*	Name: 410chan MadScientist theme */


html {
	text-size-adjust:100%;
	-moz-text-size-adjust:100%;
	-webkit-text-size-adjust:100%;
	-ms-text-size-adjust:100%;
}

html, body {
	background-color: #212121 !important;
	color:#678CB1 !important;
	font-family: 'Droid Sans', Tahoma, sans-serif;
}

a {
	color:#C9BE89 !important;
}

a:hover {
	color:#789922 !important;
}

input,textarea {
	color: #F0F0F0 !important;
	background: #293134 !important;
}

.reflink a:hover{
	font-weight: bold;
}

.adminbar {
	text-align:right;
	clear:both;
	background:inherit;
	float:right;
	margin-right:20;
}

.logo {
	clear:both;
	text-align:center;
	font-size:2em;
	color:#F0F0F0 !important;
	width:100%;	
}

.replymode {
	background:#3F4B4E !important;
	text-align:center;
	padding:2px;
	color:#81969A !important;
	width:100%;
}

.catalogmode {
	background: #3F4B4E !important;
	text-align: center;
	padding: 2px;
	color: #FFFFFF;
	width: 100%;
}
	
span.catalogmsg {
	color:#678CB1 !important;
}
	
span.catalogposts {
	color:#C9BE89 !important;
}

table.catalogtable td {
	border:1px solid #3F4B4E !important;
}

span.catalogsubject {
	color:#C9BE89 !important;
}
	
span.catalogsubject:hover {
	color:#789922 !important;
}
	
div.head {
	background: #3F4B4E !important;
	
}

.postarea {
	border-top:1px solid #3F4B4E !important;
	border-bottom:1px solid #3F4B4E !important;
}

.rules {
	/*font-size:0.7em;*/
	width: 468px;
	font-size: 10px;
	font-family: serif;
}

.rules li {
	margin-left: 1em;
	/*text-indent: 0em;*/
}

div.thrdcntnr {
	border-bottom:1px solid #3F4B4E !important;
}

div.pgstbl table {
	border:1px solid #3F4B4E !important;
}

.postblock {
	background:#3F4B4E !important;
	color:#81969A !important;
	font-weight:800;
}

.footer {
	text-align:center;
	font-size:12px;
	font-family:serif;
}

.passvalid {
	background:#EEAA88;
	text-align:center;
	width:100%;
	color:#ffffff;
}

.dellist {
	font-weight: bold;
	text-align:center;
}

.delbuttons {
	text-align:center;
	padding-bottom:4px;

}

.managehead {
	background:#AAAA66;
	color:#400000;
	padding:0px;
}

.postlists {
	background:#FFFFFF;
	width:100%;
	padding:0px;
	color:#800000;
}

.row1 {
	background:#EEEECC;
	color:#090E00;
}

.row2 {
	background:#DDDDAA;
	color:#090E00;
}

.unkfunc {
	background:inherit;
	color:#789922;
}

.filesize {
	text-decoration:none;
	font-family:sans-serif;
	font-size: 10px;
}

.filetitle {
	background:inherit;
	color:#4e8e93 !important;
	font-weight:800;
}

.postername {
	color:#93C763 !important;
	font-family:sans-serif;
}

.postertrip {
	color:#228854;
}

.oldpost {
	color:#CC1105;
	font-weight:800;
}

.omittedposts {
	color:#707070;
}

.reply {
	background: #293134 !important;
	color: #678CB1 !important;
	border: 1px solid #394144 !important;
}
.replyhl {
	background: #292010 !important;
	color: #678CB1 !important;
}

.doubledash {
	vertical-align:top;
	clear:both;
	float:left;
}

.replytitle {
	font-size: 1.5em;	color:#CC1105;
	font-weight:800;
}

.commentpostername {
	color:#93C763 !important;
	font-weight:800;
}

.thumbnailmsg {
	font-size: small;
	color:#707070 !important;
}

.abbrev {
	color:#707070;
}

.highlight {
	background:#2C2C2C !important;
	color:#678CB1 !important;
	border: 2px dashed #678CB1 !important;
}


.watchedthreads {
	background: #F0E0D6 !important;
}


.reflinkpreview {
	background: #242629 !important;
}
.userdelete table tr td {
	border: 1px solid #F9E0A8;


}
table {
	border-style: none;


}
button {
	border: 1px solid #394144 !important;
	background-color: #293134 !important;
}

input[type=text], input[type=password], textarea {
	margin: 0px;
	margin-right: 2px;
	padding: 2px 4px 3px 4px;
	border: 1px solid #394144 !important;
	outline: none;
	font-size: 10pt;
}

input[type=submit] {		
	border: 1px solid #394144 !important;
	outline: none;
	background: #293134 !important;
	margin: 0px;
	margin-right: 2px;
	padding: 2px 4px 3px 4px;
}

input[type=submit]:hover {		
	border: 1px solid #394144 !important;
	outline: none;
	background: #FFECB2;
	margin: 0px;
	margin-right: 2px;
	padding: 2px 4px 3px 4px;
}

.hidethread {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.unhidethread {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.watchthread {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.expandthread {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.quickreply {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.hidewatchedthreads {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.refreshwatchedthreads {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

.restorewatchedthreads {
	background: transparent url('data:image/png;base64,') no-repeat !important;
}

hr {
	border-style: solid none none none;
	border-width: 1px;
	border-color: #394144 !important;
}
.topmenu {
	border-bottom: 1px solid #394144 !important;
	top:0px;
	left:0px;
	width:100%;
	opacity: 0.95;
	z-index:9000;
	height:inherit;
	position:fixed;
	clear:both;
	overflow:hidden;
	background:#212121 !important;
}
.adminbar img{
	vertical-align:middle;
	border:0;
}
.navbar {
	padding:5px;
}

body {
	margin-top: 33px;
	padding:5px;
}

.reply blockquote, blockquote :last-child {
	margin-bottom: 0em;
}

h2 {
	background:#3F4B4E !important;
	color:#B1C6CA !important;
}

h1 {
	background:#212121 !important;
	color:#F0F0F0 !important;
}

h3 {
	color:#93C763 !important;
}

.plus {
	float: right;
	font-size: 8px;
	font-weight: normal;
	padding: 1px 4px 2px 4px;
	margin: 0px 0px;
	background: #293134 !important;
	color: #81969A !important;
	border: 1px solid #394144 !important;
	cursor: hand;
	cursor: pointer;
}

.plus:hover {
	background: #386669 !important;
	border: 1px solid #394144 !important;
}

.post {
    background:#293134 !important;
    border: 1px solid #394144 !important;
}

.threadldiv, .thread, .midhead, .newthread{
    background: #3F4B4E !important;
}

}
