css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://djangobook.py3k.cn/) {
  body{
    font:75%/1.5 "Microsoft YaHei","Lucida Grande",Verdana !important;
  }
  h1,h2,h3,h4,h5,h6{
    font-family: "Bookman Old Style","Microsoft YaHei","Lucida Grande",Verdana !important;
    font-weight:bold !important;}
  p{
    font-size:115% !important ;
    font-family:"Corbel","Microsoft YaHei",Arial !important;
    line-height:1.8 !important;
    color:#333 !important;}
  pre{
    font-family:Consolas,Anonymous,Monaco,Courier,"Courier New" !important;
    font-size:12px !important;}
  .pre{
    font:800 12px Consolas,Monaco,Courier,"Courier New" !important;}
  blockquote{ font-size:12px !important;
   color:#333 !important;font-family:"Microsoft YaHei","Lucida Grande",Verdana !important;}
  blockquote div.translation-indicator {
    right:-56px !important;}
  blockquote div.comment-indicator {left:-58px !important;}
  blockquote blockquote{border:none !important;}
  blockquote blockquote div.comment-indicator{left:-70px !important;}
  blockquote blockquote div.translation-indicator{right:-68px !important;}
  #djangobookcomments{z-index:1000 !important;}
  #djangobooktranslations{z-index:1000 !important;}
  .vLargeTextField{font-family:"Microsoft YaHei","Lucida Grande",Verdana !important;font-size:12px !important;color:#333 !important;}
  .tabset li{font-size:12px !important;}
  ul p,dl p{padding-bottom:0 !important;}
}
