css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {

    html,
body {
  background-image: URL("http://slowbuddy.com/wp-content/gallery/abstract-wallpapers-hd/abstract-hd-wallpaper-1920x1080-10.jpg") !important ;
  background-attachment: fixed !important ;
  background-color: #000000 !important;
  background-position: center ;
background-size:100% 100%!important;
 
}

.logo-link-4522, /* Variable size class; causes Search Container to shift */
#aboutMozilla,
#brandLogo,
#restorePreviousSessionSeparator,
#searchLogoContainer,
#snippetContainer,
.launchButton::before {
  display: none !important ;
}

#searchContainer {
  border: 2px solid !important;
  border-radius: 12px !important ;
  margin: 0 auto !important;
  margin-top: 34px !important;
  opacity: 0.9 !important ;
  padding: 6px 0px 8px 0px !important;
  width: 330px !important;
}

#searchForm {
  width: 310px !important;
}

#searchText {
  background: #3f3f3f !important ;
  border-color: #4a4a4a !important ;
  border-radius: 6px !important ;
  box-shadow: 1px 1px 5px #000000 inset,
              0 -1px 1px #7a7a7a inset !important ;
  color: #ffffff !important ;
  height: 28px !important ;
  padding: 0px 0px 4px 8px !important ;
}

#searchContainer,
#searchSubmit,
#restorePreviousSession,
.launchButton {
  background: -moz-linear-gradient(top, #7a7a7a, #3f3f3f, #101010) !important ;
  border-bottom: none !important ;
  border-color: #4a4a4a !important ;
  box-shadow: 1px 1px 4px #000000 inset,
              1px 1px 4px #323232 inset,
              0 -1px 1px 1px #7a7a7a inset,
              4px 6px 12px #000000 !important ;
  color: #000000 !important ;
  text-shadow: .5px .5px .5px #000000,
               -1px -1px .5px #b9b9b9 !important ;
}

#searchSubmit {
  border-radius: 45px !important ;
  font-size: 0px !important ;
  font-style: italic !important;
  margin-left: 8px !important ;
  padding: 0 0 5px !important ;
  width: 30px !important ;
}

#launcher {
  border: none !important ;
  box-shadow: none !important ;
}

.launchButton {
  border-radius: 6px !important ;
  font-size: 10px !important ;
  margin-left: 4px !important ;
  margin-right: 4px !important ;
  min-width: 64px !important ;
  max-width: 64px !important ;
  padding: 0 0 2px !important ;
}

#restorePreviousSession {
  font-size: 10px !important ;
  min-width: 120px !important ;
}

#searchSubmit:hover,
#restorePreviousSession:hover,
.launchButton:hover {
  background: -moz-linear-gradient(top, #9a9a9a, #4a4a4a, #101010) !important ;
  color: #53b3c5 !important ;
}

#searchSubmit:hover:active,
#restorePreviousSession:hover:active,
.launchButton:hover:active {
  background: -moz-linear-gradient(top, #4a4a4a, #3a3a3a, #2f2f2f) !important ;
  box-shadow: 1px 1px 4px #000000 inset,
              1px 1px 4px #323232 inset,
              0 -1px 1px 1px #7a7a7a inset !important ;
  color: #2a2a2a !important ;
}

}

/* Note: Delete the following to prevent setting about:blank background. */

@-moz-document url(about:blank) {

html,
body {
  background-image: URL("http://slowbuddy.com/wp-content/gallery/abstract-wallpapers-hd/abstract-hd-wallpaper-1920x1080-10.jpg") !important ;
  background-attachment: fixed !important ;
  background-color: #000000 !important;
  background-position: center top ;
  background-repeat: no-repeat !important ;
  background-size:100% 100%!important;
}

}
