@namespace url(http://www.w3.org/1999/xhtml);
/* Input left, no promoted tweets and trends */
@-moz-document domain("twitter.com") {
  #dashboard-matches,
    .promoted-tweet,
    li.preview-promo-card {
       display: none !important;
  }
  .timeline-tweet-box {
    left: 15px;
    position: absolute;
    top: 261px;
    width: 286px;
    }
  .top-timeline-tweetbox .timeline-tweet-box {
    border: 1px solid #e1e8ed;
    border-radius: 5px;
   }
   .trends {
     display: none !important;
   }
}
