css:
@-moz-document url-prefix(https://sites.google.com/)

{
body{
background: url("https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcRtUv4gMI06TvL5zSFndUTmNIaWYt_9pO7JdDrsXz-zHOk5ChTd") fixed!important;
background-size: cover !important;
}
#sites-chrome-main-wrapper, #sites-chrome-horizontal-nav, #goog-ws-list-floatingBar {
background-color: rgba(255, 255, 255, 0)!important;
}
#sites-chrome-header .sites-header-nav a, #sites-chrome-header .sites-header-nav a:hover, #sites-chrome-header .sites-header-nav li, #sites-chrome-header .sites-header-nav li:hover, #sites-chrome-header .sites-header-nav li.current, #sites-chrome-header .sites-header-nav li.current:hover {
color: #FFF;
font-weight: 600;
text-decoration: none;
text-transform: uppercase;
}
#sites-chrome-everything {
color: #FFDCDC;
line-height: 1.5;
padding-top: 0;
padding-bottom: 20px;
font-family: "Open Sans",sans-serif;
font-size: 14px;
}
body #sites-page-title-header {
color: #FFB5B5;
font: normal 26px "Open Sans",sans-serif;
margin: 0;
padding: 33px 10px 0 10px;
}
#sites-chrome-header .sites-header-nav-container-tabs li.current, #sites-chrome-header .sites-header-nav-container-boxes li.current {
background-color: rgba(45, 36, 36, 0.71);
}


#logo-img-id {
         opacity: 0.7;
}
.sites-header-nav-dropdown-menu {
background-color: rgba(0, 0, 0, 0.58);
}
}
