css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.gamer.co.il") {
#ifrmNana { display: none !important }
body { padding: 0px !important }
table.TopNavBar { height: 65px; background: #d3d3d3 url(http://img127.imageshack.us/img127/4728/gamerbt8.jpg) no-repeat center left; }
table.TopNavBar>tbody>tr>td:first-child table { margin-left: 280px !important }
.tnb { direction: rtl }
.tnb table { direction: ltr }
a { color: #000000 !important }
a.box { color: #043871 !important }
body>table>tbody>tr>td:last-child  { text-align: center !important }
td.tnb {  text-align: right !important; }
body>table>tbody  { background-color: #d3d3d3 !important }
td.PersonalNavBar, td.pnb, a.PersonalNavBarButtonText  { background-color: #d3d3d3 !important; color: #000000 !important }
td.ForumChannelHeaderBar, .forumName, .PrettyBoxTop, .PrettyBox table.Structural td, body>table>tbody>tr>td>div, table.TopNavBar, td.tnb { background-color: #005b90 !important; color: #ffffff !important }
th.ForumsListHeaderBar, .gr, table.IndexBox .HardSmallHeadline { background-color: #999999 !important; color: #ffffff !important; text-align: center }
.PrettyBox { background-color: #f6f6f6 !important; color: #000000 !important }
table.Structural .PrettyBox table.Structural td { text-align: center !important }
a.ChannelNavBar, td.ChannelNavBar, .PrettyBoxHeadline a, a.rnbs, a.rnb, a.BlockNavBar, table.UserComsPanel a { color: #ffffff !important }
div.nav-box table.IndexBox { background-color: #b1b1b1 !important }
table.IndexBox td.StatisticValue, table.IndexBox td.StatisticName, table.IndexBox td.StatisticName a { background-color: #f6f6f6 !important; color: #000000 !important }
}
