css:
@-moz-document domain("wikipedia.org") {



#mw-head-base, #mw-navigation, #mw-page-base, #footer-info-copyright, #footer-icons, #footer-places {
    display: none;
}

/* Can't display:none the "from wikibedia" subheader because a page's coordinates get pushed up into the topmost divider */
#siteSub {
    color: transparent;
}

/* remove menu sidebar artifact and footer spacing */
#content, #footer {
    margin: 0 !important;
}

/* minor 1px blu eborder artifact on the top and the left from side/top bars */
.mw-body {
    border: none;
}

/* the "enlarge" button beside images, it's useless because clicking the image produces the exact same effect */
div.magnify a {
  	display: none;
}

/* the text "Contents [hide]" at the top of an article. Everyone knows what the contents are without a title and there's no practical reason to hide it */
#toctitle {
    display: none;
}

/* the "[edit]" button beside sections */
span.mw-editsection {
    display: none;
}

/*[[asdf]]*/

}
