css:
@-moz-document url("http://www.reddit.com"), regexp("https?:\\/\\/www.reddit.com\\/(([a-z]{2,}.*)|(#.*))?") {
/******************************************************/
/****************** Hide elements *********************/
/******************************************************/
div.sidebox.create > .spacer,
.sidebox.hohoho,
.goldvertisement,
.account-activity-box,
span.rank,
.morelink .nub,
.hohoho-header,
[href="https://www.reddit.com/ads/"].choice,
[href="https://www.reddit.com/wiki/"].choice,
[href="https://www.reddit.com/controversial/"].choice,
[href="https://www.reddit.com/gilded/"].choice,
div#sponsorship,
.userkarma, .score,
#RESShortcutsEditContainer {
  display: none !important;
}

/******************************************************/
/******************** Hide borders ********************/
/******************************************************/
#siteTable_organic, a.choice, #header,
#sr-header-area, div.NERPageMarker,
input[name="q"], div.sidebox div.morelink {
	border: none !important;
}

/******************************************************/
/********************* The rest ***********************/
/******************************************************/
#header-img {
	background-image: url("data:image/png;base64,") !important;
	background-position: 8px 2px !important;
}

#sr-header-area {
	position: fixed;
	width: 80%;
	left: 10% !important;
	background-color: transparent;
}

#header-bottom-right {
	background-color: transparent;
	top: 16px !important;
}

.tabmenu {
	position: fixed;
	margin-top: -5px;
	top: 30px;
	left: 48px;
}

li { margin: 0 0 0 0 !important; }

div.sidebox div.morelink:hover { background-color: rgb(206, 227, 248); }
div.thing.even				   { background-color: rgb(250, 250, 250); }
input[name="q"]                { background-color: #EEE;               }

body.with-listing-chooser .listing-chooser { top: 40px; }

div#RESPinnedHeaderSpacer { height: 44px !important; }
a.thumbnail               { height: 35px !important; }
#header                   { height: 40px;            }

div.sidebox div.morelink {
	background-image: none;
	background-color: rgb(239, 247, 255);
}

a.thumbnail img {
	position: absolute;
	clip: rect(10px, 75px, 45px, 0px);
	margin-top: -10px;
}

.expando-button {
	height: 16px !important;
	width:  16px !important;
	background-image: url("data:image/png;base64,") !important;
	background-position: 0 0 !important;
	background-color: transparent;
}

.expando-button:hover {
	-webkit-filter: invert(75%);
	filter: invert(75%);
}

a[action='hide'],
a.report-thing,
li.save-button a[href='#'],
li.share span.share-button a.option.active {
	display: inline-block;
	background-repeat: no-repeat;
	height: 16px;
	width: 16px;
	text-indent: -9999px;
	white-space: nowrap;
}

a[action='hide']                           { background-image: url('data:image/gif;base64,') !important; }
a.report-thing                             { background-image: url('data:image/gif;base64,') !important; }
li.save-button a[href='#']                 { background-image: url('data:image/gif;base64,')                 !important; }
li.share span.share-button a.option.active { background-image: url('data:image/gif;base64,')     !important; }

ul.flat-list li {
	padding-right: 0 !important;
	padding-left: 2px !important;
}

ul.flat-list.buttons {
	margin-top: -12px;
	float: right;
}
}
