css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.pathofexile.com/") {
  #statusBar {
    position:fixed;
    z-index: 1000;
    width:100%;
    border-bottom:1px solid #555555;
    height:6%;
    background-image:none;
    background-color:#333333;
  }
  #statusBar .row2.loggedOut {
    margin: 0;
    padding: 10px 0;
    text-align: center;    
    background-image:none;
  }
  #statusBar .row2 .statusItem {
    margin: 0 10px;
  }  
  #statusBar, #statusBar .statusBarCenter, #statusBar .statusBarRight, .pointsIcon, #socialBar {
    background-image:none;
  }
  #statusBar .row2 a {
    font-size:100%;
  }
  #mainLogoLink {
    display: none;
  }
  #socialBar .twitter {
    float: none;
    width:100px;
    right:4px;
    top:30px;
    position: absolute;
  }
  #socialBar .twitterFollow {
    float: none;
    width:100px;
    right:4px;
    top:65px;
    margin: 0;
    position: absolute;
  }  
  #socialBar .facebook {
    float: none;
    width:100px;
    right:4px;
    top:100px;
    position: absolute;
  }
  #mainHeader {
    margin:10% 0 0;
    top:-10%;
    width:100%;  
    height: 28%;
    background-size:120% auto;
    background-position:center -20%;
    position:absolute;    
  }
  #inventory-controls {
    top:17px;
  }
  #siteNav {    
    width:100%;
    height:110%;
    background-color: transparent;
    margin: 0 auto;  
    margin-top:20%;
    position:relative;
    padding:0 2%;
    z-index:10000;
  }
  #siteNav li {
    width:16%;
    height:70%;
  }
  #siteNav #nav-forum {
    margin: 0;
  }
  #siteNav #nav-home {
    margin:0;
  }
  #siteNav #nav-home a {    
    background: url(http://i.imgur.com/eE5JLZJ.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%;    
  }
  
  #siteNav div.dropDown li {
    width:100%;
  }
  #siteNav #nav-home a span {
    display: inline;
  }
  #siteNav #nav-game a {    
    background: url(http://i.imgur.com/eivuBId.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%; 
  }
  #siteNav #nav-game a span {
    display: inline;
  }
  #siteNav #nav-media a {    
    background: url(http://i.imgur.com/oT4oiOK.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%;     
  }
  #siteNav #nav-media a span {
    display: inline;
  }
  #siteNav #nav-events a {    
    background: url(http://i.imgur.com/NEB0ogB.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%;     
  }
  #siteNav #nav-events a span {
    display: inline;
  }
  #siteNav #nav-forum a {    
    background: url(http://i.imgur.com/ry8iiJI.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%;     
  }
  #siteNav #nav-forum a span {
    display: inline;
  }
  #siteNav #nav-shop a {    
    background: url(http://i.imgur.com/SudKizL.png) no-repeat transparent;
    background-size: 100% auto;
    width: 100%;
    height: 100%;     
  }
  #siteNav #nav-shop a span {
    display: inline;
  }
  #largeHeaderButton {
    display: none;
  }
  #siteContainer {
    height:100%; 
  }
  #siteCentered {
    width: 100%;  
    height:100%;
    top:-10%;
  }
  #mainContainer {
    margin: 0 auto;
    margin-top:10%;
    padding:0;    
    width:100%;    
  }
  #mainContainerBg {    
    background-image: none;
    background-color: black;
    margin: 0;
    margin-top:20%;
    opacity: 0.6;
  }
  .layoutBoxMedium {
    width: 100%;
    margin: 0;
  }
  .layoutBoxMedium .layoutBoxContent {
    margin-top:38%;
    
  }
  #mainNewsItem {
    width: 50%;
  }
  #mainNewsItem a img {
    width: 100%;
  }
  #secondaryNewsItem {
    width:50%;
  }
  #secondaryNewsItem a img {
    width: 100%;
  }
  #promotedNewsVideo .videoContainer .video {
    width: 100%;
    margin: 0;
  }
  .layoutBoxSmall  {
    margin: 0 auto;    
  }
  .newsListItem {
    padding: 5px;
  }
  #footerImage {
    width: 100%;
  }
  #footer {
    width: 100%;
    padding: 25px 0 0;
    
  }
}
