css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.patreon.com") {

#mainmiddlePan, #mainmiddleSection,
.cbTitle, .cbCreatorDate, .cbData,
#userTag div, #spotDescription,
.boxWrapper
{
background: #000000 !important;
}
.categoryList li, .wrapper-dropdown,
.dropdown, #email, #password
{
background: #333333 !important;
}
.categoryList li:hover,
.dropdown li:hover
{
background: #FE5900 !important;
}
.patreon-input, .wrapper-dropdown,
#email, #password
{
border: 1px #FE5900 solid !important;
}
h1, h2, h3, h4, h5, h6, #userTag div,
a, li, strong, .patreon-input, p,
span, .cbDataText, .creation_description,
.creatorDate, .searchUserCreate, .describeNum,
.creator_paid_amount_per, .creator_patron_count,
#search_description, #spotDescription, 
.boxWrapper, .boxPrice,
#email, #password
{
color: #FFFFFF !important;
}
}
