@charset "utf-8";
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 
@-moz-document url-prefix(http://www.google), url-prefix(https://www.google) {
/*logo*/
#hplogo {
opacity: 0.5 !important;
background-image: url("https://www.google.pl/images/srpr/logo11w.png") 0% 0%/269px 95px no-repeat transparent !important;
background: url("https://www.google.pl/images/srpr/logo11w.png") 0% 0%/269px 95px no-repeat transparent !important;
background-size: 269px 95px !important;
}
#logo {
background-image: url("https://ssl.gstatic.com/gb/images/i1_71651352.png") no-repeat !important;
background: url("https://ssl.gstatic.com/gb/images/i1_71651352.png") no-repeat !important;
background-size: 356px 144px !important;
}
.csb {
opacity: 0.7 !important;
background-image: url("https://www.google.pl/images/nav_logo195.png") -24px 0px no-repeat !important;
background: url("https://www.google.pl/images/nav_logo195.png") -24px 0px no-repeat !important;
}

/*change browser - spam, cookies*/
.pmoabs,
#epbar /*cookies webhp*/{
display: none !important;
}
/*background*/
body, #page, #body-container, .sfbgg,  .footer-container , .guide-pinned.show-guide .guide-pinning-enabled #page, 
.guide-pinned.show-guide .guide-pinning-enabled #appbar-content, body #footer-container, #content-container, #body-containe,
#topabar, /*gorny pasek webhp*/
#hdtbSum, /*gorny pasek webhp*/ 
._Fmb, ._Fmb:hover, ._Fmb.selected, ._Fmb.selected:hover  /*linki webhp*/{ 
background: #f1f1f1 !important;
background-color: #f1f1f1 !important;
}
/*search*/
.lst, .lsbb,
.gb_W, /*przycisk webhp*/ 
.gb_D, /*popup z uslugami webhp*/
.ab_dropdown, /*popup z uslugami webhp*/
.sbib_a,  /*wyszukiwarka webhp*/
.gbqfqw, /*wyszukiwarka webhp*/
#gbqfb, /*przycisk szukaj webhp*/
.hdtb-mn-o, .hdtb-mn-c, /*popup więcej webhp*/
.gbqfba  /*przyciski szukaj na glownej webhp*/{
background: #e7e7e7 !important;
box-shadow: inset 0 -15px 30px rgba(72,141,234,0.4),
			0 5px 10px rgba(72,141,234,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #0050bd !important;
}
#gbqfb {
margin-left: 30px !important;
}
.sbsb_a, /*popup wyszukiwarki*/
.vk-box /*klawiatura ekranowa webhp*/{ 
background: rgba(231,231,231,0.8) !important;
box-shadow: inset 0 -15px 30px rgba(72,141,234,0.4),
			0 5px 10px rgba(72,141,234,0.5) !important;
border: none !important;
-webkit-border-radius: 5px !important;
-moz-border-radius: 5px !important;
border-radius: 5px !important;
color: #0050bd !important;
}
.lsb {
background-color: none !important;
text-shadow: 0px 0px 10px #488DEA !important;
color: #0050bd !important;
}
.lsb:hover {
background: #e7e7e7 !important;
}
.gssb_m , .gbmc, .gbm,
.gb_F /*popup z uslugami webhp*/{ 
background: rgba(231,231,231,0.8) !important;
border: none !important;
color: #0050bd !important;
text-shadow: 0px 0px 10px #488DEA !important;
}
.gssb_e {
border: none !important;
outline: none !important;
box-shadow: inset 0 -15px 30px rgba(72,141,234,0.4),
			0 5px 10px rgba(72,141,234,0.5) !important;
}
.gssb_a:hover, .gbmt:hover, .gbmt:focus, .gbzt:hover, .gbzt:focus, .gbgt-hvr, .gbgt:focus, .gbto .gbts,
.gbqfba:hover /*przyciski szukaj na glownej webhp*/ {
background: rgba(72,141,234,0.3) !important;
text-shadow: none !important;
border: none !important;
}
.gbqfif, .gbqfsf /*wyszukiwarka popup kolor webhp */ {
color: #0050bd !important;
}
.sbdd_b /*wyszukiwarka popup borders webhp */ {
border: none !important;
}

/*toolbar*/
#gbx3, #gbx4 {
background: #f1f1f1 !important;
box-shadow: inset 0 -15px 30px rgba(72,141,234,0.4),
			0 5px 10px rgba(72,141,234,0.5) !important;
text-shadow: 0px 0px 10px #488DEA !important;
}
.gbz0l .gbts, #gbz .gbzt, #gbz .gbgt, #gbg .gbgt, .ftl, #fll a, #footer a, .fl a    {
color: #0050bd !important;
}
.gbz0l .gbtb2 {
border-top-color: #2b85ff !important;
}
/*toolbar webhp*/
.gb_Sb {
background: #f1f1f1 !important;
box-shadow: inset 0 -15px 30px rgba(72,141,234,0.4),
			0 5px 10px rgba(72,141,234,0.5) !important;
text-shadow: 0px 0px 10px #488DEA !important;
}

/*stopka*/
.ftl, #fll a, .sblc a, #footer a, #foot a  {
text-shadow: 0px 0px 10px #488DEA !important;
}

/*wyszukiwarka main*/
.lst-a {
background: transparent !important;
width: 810px !important;
border: none !important;
}
.lst {
width: 760px !important;
}
.gsq_a {
width: 748px !important;
}
/*środek*/
._Ihd, .tbos,
#hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre, /*webhp*/ 
.hdtb_mitem a, #hdtb_more_mn a /*linki webhp*/{
color: #0050bd !important;
text-shadow: 0px 0px 10px #488DEA !important;
border-bottom-color: #0050bd !important;
}
#res h3 {
font-size: 18px !important;
color: #0050bd !important;
text-shadow: 0px 0px 10px #488DEA !important;
}
a:link {
color: #0050bd !important;
}
.s, .spell,
#hdtb_msb #hdtb_more, #hdtb_msb #hdtb_tls /*więcej webhp*/ {
color: #8ba0bd !important;
}
cite, cite a:link, .spell_orig {
color: #0048ab !important;
}



}
