css:
@-moz-document url(https://www.9lives.be/forum),
url-prefix("https://www.9lives.be/forum") {
    
/* Losjes gebasseerd op stylesheet van CrashTheButton */
    
/* Kleuren referentie. Kunnen gebruikt worden voor andere dingen  */
/* Userkleur - #ebebeb */
/* Crew-kleur/kleur voor links - #eb4024 */
/* Text-kleur - #e3e3e3 */
/* Kleur achtergrond threads    - */   
/* border - #6B91AB */
    
body {
    padding: 0px 0 10px 0 !important;
    min-height: 99vh !important;   
}     
    
    /*  HEADER  */

#header {
    margin: auto !important;
    position: relative !important;
    width: 100% !important;
    max-width: 1200px !important; /* Wil je de header breder? Verander dit dan. Opgepast! Verander de waarde van de header ook! Momenteel is 1099px het kleinste waar alles goed lijkt. Kleiner kan ook maar raad ik (voorlopig) nog niet aan*/
    z-index: 1 !important;
}

/*  Width & Height forum  */

.ctrContentWrapper {
    margin: auto !important;
    width: 100% !important;
    max-width: 1200px !important;; /* Wil je het forum breder? Verander dit dan. Opgepast! Verander de waarde van de header ook! Momenteel is 1099px het kleinste waar alles goed lijkt. Kleiner kan ook maar raad ik (voorlopig) nog niet aan.*/
}

    /*  THREAD READ/UNREAD */

.threadtitle_unread {
    font-weight: bold !important;
    font-size: 14px;
}

    /*  FONT SIZE  */

ul.floatcontainer, 
div.signaturecontainer, 
#wgo_onlineusers, 
#wgo_stats, 
#wgo_legend, 
#wgo_birthdays, 
div.forumhead h2, 
div.forum_info.block, 
div.thread_info.block,
.moderators h4,
.subforums h4 {
    font-size: 13px !important;
}

/*  THREAD POST  */

.bbcode_postedby strong {
    color: #eb4024 !important;
}

/*  USERNAME USERS */
    
.userinfo .username strong {
    color: #ebebeb !important;  
}

/*  Varia  */

.lastposttitle {
    margin: 0 !important;
}
    
.forumdescription {
    line-height: 1.2em !important;
}
    
/* poll-fixes */
li.blockrow p,
span.percentvotes {
    color: #ffffff;
    font-size: 14px;
    margin-top: -2px;
}
span.numvotes {
    margin-top: -3px;
}    
span.numvotes a {
    color: #ffffff;
    font-size: 14px;
}
div.votes {
    margin-top: 2px;
}
#pollresults {
    border: 0px
}
#pollinfo .blockhead h2{
    color: #eb4024;
}    
    
#pollinfo .blockfoot {
    background: #3e3e3e !important;
}        
    
/* text positie fixes */
    
dl.pagination {
    height: 11px;
}
   
ul.header-menu {
    display: none;
}
.pagination span a {
    color: #ebebeb !important;
    background: #3E3E3E !important;
    border: 1px solid #4e4e4e !important;
}
    
.pagination span.selected a {
    color: #eb4024 !important;
} 
    
body a,
body a:hover {
	color:#eb4024;
}

body a:hover .time,
body a:hover .shade,
body a:hover .understate {
    color:#ebebeb;
    text-decoration:underline;
}
    
div.moderators h4 {
    margin-top: 2px;
}

ol.commalist {
    margin-top: 4px;
}

h4.subforumlistlabel {
    margin-top: 3px;
}      
    
/* Verschillende kleur fixes, teveel om specifiek te benoemen */    

/* code fix */    
.bbcode_container div.bbcode_code,
.bbcode_container pre.bbcode_code {
    color: #ebebeb !important;
    background: #3e3e3e !important;
}    

/* */
    
    
p.forumdescription a,    
li.restore a b ,   
body h2.forumtitle a:hover,    
#usercp_nav .blockrow * a:hover,
.moderators .commalist li a span,     
.subforums .subforum a:hover,
#forums a.username:hover    {
    color: #eb4024 !important;
}
    
a.firstunread {
    color: #4E4E4E !important;
}
       
a.threadtitle,    
.shade,
.wgo_block .section,    
.wgo_block .section dl.icon_legends dd,    
.wgo_block .section div p,    
.wgo_block .section dl dt,
.wgo_block .section dl dd,    
.wgo_block .blocksubhead,
.textbox,
legend,    
.time,
body,
.footer_time,
.footer_copyright,
.footer_morecopyright {
    color:#ebebeb !important;    
}
 span.threadtitle a {
    color: #ebebeb !important;
}       
    
#usercp_nav .blockbody {
    background: #444  !important
}

.bbcode_quote div.quote_container,    
.bbcode_quote,
div.quote_container{
    background: #252525 !important;
    border: 1px solid !important;
}
    
.bbcode_quote_container {
    visibility: hidden;
}
    
#foldercontrols .blocksubhead{
    background: #3e3e3e !important;
    color: #e3e3e3;
    border: none;
}    
    
#pollinfo,
.blockfoot,
.blocksubfoot,
span.cke_skin_kama,
.wysiwyg_block .formcontrols .blockrow,
.formcontrols .showsignature,
.formcontrols .section,
.formcontrols .blockrow input.textbox:focus,
.formcontrols .blockrow textarea:focus,
.formcontrols > :first-child,
.formcontrols .blocksubhead + .blockrow,
.formcontrols .section .blockrow:first-child,
.formcontrols .blockrow,
.pmlist .blockrow:first-child,
blockquote.preview.forumcontent,
blockquote.preview,
.pmlist .blockrow {
    background: #3e3e3e !important;
}

.blockfoot{    
       background: #2d2d2d !important
}
 
    
#forumrules {
    margin-bottom: 0px;   
}    
    
.restore h1,
.restore h2,
.restore h3,
.restore h4,
.restore h5,
.restore h6,
.restore strong,
.restore dt {
    color: #ABABAB  !important;   
}
    
/* usergroup fixes */

.widget_content a  {
    color: #eb4024 !important;
}  
    

.postbit.postbit-lite {
    background: #3e3e3e !important;
}    
    
.postbit-lite .posttext,     
.postbit-lite .postbithead {
    color: #ebebeb !important;
    background: #555 !important;    
}
 
#sidebar_container a:hover,    
.postbit-lite .postbitfoot .postcontrols a {
    color: #eb4024 !important    
}    
    
.postbit-lite {
    padding-bottom: 0px;    
}
    
.postbit-lite .posttext.no_avatar,
.postbit-lite .postbithead.no_avatar {
    margin-left: 80px       
}
    
#sidebar_container .blockrow {
    background: #888 !important;
}
    
#sidebar_container > ul {
    background: #888 !important;
    border: 1px solid #fff;
}
    
#sidebar_container .underblock {
    height: 0px;
}

li.sgicon .controls a.textcontrol {
    color:#2d2d2d !important;
}    

#sidebar_container a {
    color: #eb4024;
}    

#groupstats dl.stats dt,
#groupstats dl.stats dd {
    color: #e3e3e3 !important;
}
    
li.sgicon .maincol{
    color: #e3e3e3;
}
   
#sidebar_container .blockbody {
    background: #3e3e3e;
} 

#sidebar_container > ul {
    border: 0px;    
}
    
#sidebar_container .block {
    padding: 0px;
} 
  
#sidebar li.sgicon .maincol {
    background: #2d2d2d;    
}         
   
  
/* DA RULES fixes */

.info_subblock {
    padding: 4px 10px 26px;    
}
    
#forumrules .rules_link {
    margin-top: 0px;
}    
/* search fix */

.blockrow {    
    background: #3e3e3e !important
}     
a.level1 {
    font-size: 11px !important     
}

/* profiel fixes */

dl.as-tabs dd.selected a,    
dl.tabslight dd.userprof_module a {
    color: #fff !important;
}
    
#sidebar_container.member_summary {
    background: #999 !important;
}    
.member_summary dl.stats dt {
    font-size: 100%;
    color: #ebebeb;
}

.member_summary dl.stats dd {
    color: #ebebeb;
    float: right !important;
    width: 90px !important;
}    
    
li.activitybit {
    color: #ebebeb !important;
    background: #3e3e3e !important;
    border: 1px solid;
}
    
li.activitybit a:hover,
li.activitybit a {
    color: #eb4024 !important;
}    
    
.moduleinactive_bg {
    background-image: none !important;
}    
    
/* PM fixes */
    
.pmlist .blockrow .unread {
    font-weight: bold !important;
    font-size: 14px !important;
}

.pmlist .blockrow .title {
    font-size: inherit !important;
}
    
.blocksubhead a,    
.blocksubhead a:hover {
    color: #eb4024;
}   
.cp_content .blocksubhead,    
.pmlist .blockrow {
    border-left: 1px solid #6B91AB;
    border-right: 1px solid #6B91AB;
}
    
    
/* chrome fixes */
    
div.title a {
    color: #eb4024;
}

div.fulllink a {
    color: #eb4024;
}

p.description a {
    color: #ebebeb;
}
    
a.username {
    color: #ebebeb !important;
}

li.navbit a span {
    color: #ebebeb;
}

a.title {
    color: #ebebeb;
}

div p a {
    color: #ebebeb;
}

div p a:hover {
    color: #eb4024;
}

ul li a {
    color: #ebebeb;
}

ul li a:hover {
    color: #eb4024;
}

div.shade.footer_morecopyright a {
    color: #ebebeb;
}

div.shade.footer_morecopyright a:hover {
    color: #eb4024;
}

#vbulletinlink {
    color: #ebebeb;
}

#vbulletinlink:hover {
    color: #eb4024;
}

div.forum_info_block.blockbody.formcontrols.floatcontainer.options_correct {
    background-color: #3e3e3e;
}
div.thread_info_block.blockbody.formcontrols.floatcontainer.options_correct {
    background-color: #3e3e3e;
}
    
span.threadtitle a {
    color: #eb4024;
}

dl.userinfo_extra dd a {
    color: #eb4024;
}

div.navlinks a {
    color: #eb4024;
}

div.title a {
    color: #eb4024;
}

div.fulllink a {
    color: #eb4024;
}

/* ledenlijst/forumleiding fixes */

dl.stats dt{
    color: #ebebeb
}    
    
.groupbit {
    background: #3e3e3e
}
.blockbody {
    background: #2e2e2e
}
    
.groupbit .blocksubhead {
    background: #2e2e2e; 
}    
  
#charnav dt{    
    color: #ebebeb;
}
#memberlist_table td    {
    background: #3e3e3e !important
}
#memberlist.block .blockbody {
    padding-right: 1px;
    padding-bottom: 1px;
}
ol.commalist li a {
    color: #ebebeb !important;
}

dl dd a {
    color: #eb4024 !important;
}

a.username span {
    color: #eb4024 !important;
}

/* Fixes 29-03-15 */
    
/* nieuwe quote boxes */
    
#vB_Editor_QR_editor,
#vB_Editor_001_editor {
    background-color: #3e3e3e;
    color: #ffffff;
}  
  
}
