css:
@-moz-document url-prefix(http://mail.google.com/),url-prefix(https://mail.google.com/)
{
/*----------------------------------------------------------------------
Background color box labels same as the others
----------------------------------------------------------------------*/
.XPj4ef .R7iiN, .XPj4ef .wWwc8d{background-color:#c3d9ff!important; }
div.VfONdd, div.haNaWc, div.IUntof, #guser a, #gbar a, a.zD5BAe, a.Q9o7yd, a.Tm9rOd, span.zD5BAe, div.ooCKse a, div[class="tYBwhe SUicbd"], span.bsABdf, div.DiWSpb, div[class="QOxrP Qj7Rff"], td[class="Cipym"] {color:rgb(0,0,204)!important; color:GrayText!important;}
a.Q9o7yd, div[class="QOxrP Qj7Rff"] {font-size: 8px !important;}

/*----------------------------------------------------------------------
buttons colorize (to particularize button decomment it)
----------------------------------------------------------------------*/
/*search buttons * / button[type="button"]{color: grey !important;} button[type="button"]:hover{color: black!important;}
/*Mark as Read* /    button[act="1"]{ color: darkgreen!important;}
/*Remove star* /     button[act="4"]{ color: magenta!important;}
/*Archive* /         button[act="7"]{ color: navy!important;}
/*Move to* /         button[act="8"]{ color: blue!important;}
/*report spam* /     button[act="9"]{ color: brown!important;}
/*delete* /          button[act="10"]{color: red !important;}
/*remove label* /    button[act="13"]{display: none !important ;}
/*discard drafts* /  button[act="16"]{color: red !important;font-weight:bold !important;}
/*delete for ever* / button[act="17"]{color: red !important;font-weight:bold !important;}
/*not spam* /        button[act="18"]{color: darkgreen!important;font-weight:bold !important;}
/*buttons */        button{color: grey !important;} button:hover{color: black!important;}

/*----------------------------------------------------------------------
Thread List : row color on hover
----------------------------------------------------------------------*/
table.N2mZ7b tr.AnqB9d:hover { background-color: rgb(255,235,134) !important;} 
table.N2mZ7b tr.QhHSYc:hover { background-color: rgb(205,243,159) !important;}

/*----------------------------------------------------------------------
Thread List : Labels visibility : visible only on hover
----------------------------------------------------------------------*/
/* mask label */
div.C5uTre:first-child{height:15px!important;padding:0px!important;padding-left:4px!important;width:00px!important;height:15px!important;float:left!important;overflow:hidden!important;}
/* see label on hover */
div.C5uTre:first-child:hover{min-width:75px!important;width:auto!important;}

/*----------------------------------------------------------------------
GMail thread list : boxes around fields
----------------------------------------------------------------------*/
COL:first-child + COL + COL + COL  + * + COL{width:28px!important;}
/* from field */
COL:first-child + COL + COL{width:170px!important;}
/* personal level indicators (> and Â») field : hide it */
COL:first-child + COL + COL + COL{width:0px!important;}
/* icon field */
table tr td:first-child +td+td+td+td+td{border-left:1px solid #bbbbbb!important;border-right:1px solid #bbbbbb!important;padding:0px!important;text-align:center;}
/* subject field */
table tr td:first-child +td+td+td+td{padding-right:4px!important;}
/* labels field */
table tr td:first-child +td+td+td{border-right:1px solid #bbbbbb!important;}
/* date field */
table tr td:first-child +td+td+td+td+td+td{padding-left:4px!important;border-right:1px solid #bbbbbb!important;}
/* right border of each empty line */
tr.lW3BFe {border-right:1px solid #bbbbbb!important;}


/*----------------------------------------------------------------------
Remove labels in mail reading header
----------------------------------------------------------------------*/
table.Ir5Jyf {display: none !important; }

/*----------------------------------------------------------------------
Gmail SETTINGS : remove photo : I already know my face... :-)
----------------------------------------------------------------------*/
img[alt="Picture"][src^="https://mail.google.com/mail/photos/static"]{display: none !important;}

/*----------------------------------------------------------------------
Don't see "<< Back to ..." in message reading only on hover the "<<"
----------------------------------------------------------------------*/
div[act="19"]{width: 9px !important;height: 15px !important;float:left!important;overflow:hidden!important;text-decoration:none ! important;}
div[act="19"]:hover{width: auto !important;float:left!important; overflow:hidden!important;text-decoration:none ! important;}

/*----------------------------------------------------------------------
remove "Search result for :" text : I know it : it's in the search box...
----------------------------------------------------------------------*/
div.zXXI1 { display: none !important ;}

/*----------------------------------------------------------------------
remove bottom suggest 
----------------------------------------------------------------------*/
div.s7hnoe, div.tBLpAf, div.pghVUe {display: none !important; }
div.AxsVkf {display: none !important;}

}
