css:
@-moz-document domain("gimper.pl") {
html, body, div, object, iframe, h1, h3, h4, h5, h6, p, blockquote, pre, abbr, address, cite, code, del, dfn, em, ins, kbd, q, samp, small, sub, sup, var, b, i, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, figcaption, figure, footer, header, hgroup, menu, nav, section, summary, time, mark, audio, video {
margin: 0;
padding: 0;
border: 0;
outline: 0;
font-size: 100%;
vertical-align: baseline;
background-image:url(http://i.imgur.com/wKLzxIf.png);
}
.fb_reset {
background-image:url(http://i.imgur.com/wKLzxIf.png);
border: 0;
border-spacing: 0;
color: #FFFFFF;
cursor: auto;
direction: ltr;
font-family: "lucida grande", tahoma, verdana, arial, sans-serif;
font-size: 11px;
font-style: normal;
font-variant: normal;
font-weight: normal;
letter-spacing: normal;
line-height: 1;
margin: 0;
overflow: visible;
padding: 0;
text-align: left;
text-decoration: none;
text-indent: 0;
text-shadow: none;
text-transform: none;
visibility: visible;
white-space: normal;
word-spacing: normal;
}
.mikro:nth-child(2n+1) {
background: #222;
}
.mikro.my {
background: #222!important;
}
.mikro .mikro-content .mikro-post {
padding: 5px 0;
color: #7f7f7f;
}
.mikro .mikro-content .mikro-header .mikro-rating .rate {
color: #00A2FF;
font-size: 16px;
vertical-align: middle;
display: inline-block;
padding: 0 5px;
-webkit-transition: background 1s ease-out;
-moz-transition: background 1s ease-out;
-o-transition: background 1s ease-out;
transition: background 1s ease-out;
}
a {
color: #00A2FF;
text-decoration: none;
}
.mikro .mikro-content .mikro-comments .mikro-showmore {
text-align: center;
font-size: 11px;
background: #00A2FF;
padding: 5px 0;
}
.mikro .mikro-content .mikro-comments .mikro-showmore a {
color: #000000;
font-weight: bold;  
}
.mikro .mikro-content .mikro-header {
border-bottom: 1px solid #00A2FF;
height: 25px;
}
#mikro #mikro-left #mikro-form #mikro-form-content .mikro-textarea textarea {
min-height: 70px;
width: 100%;
margin-bottom: 10px;
border: 1px solid #2F2F2F;
background: #2F2F2F;
color: #00A2FF;
font-weight: bold;
padding: 10px;
resize: none;
}
#mikro #mikro-left #mikro-form #mikro-form-footer {
background: #222;
border: 1px solid #222;
margin-bottom: 10px;
margin-left: 60px;
display: none;
height: 45px;
margin-top: -10px;
border-top: none;
line-height: 50px;
}
#wrapper #user .user .menu a button i {
color: #7f7f7f!important;
text-decoration: none!important;
}
#mikro #mikro-left #mikro-new {
display: none;
background: #00A2FF;
padding: 5px;
width: 100%;
margin: 10px 0;
text-align: center;
color: #000;
font-weight: bold;
cursor: pointer;
}
#mikro #mikro-right .mikro-tags .mikro-tag a {
color: #00A2FF;
word-wrap: break-word;
}
#mikro #mikro-left #mikro-header ul li a {
border-left: 1px solid #222;
border-right: 1px solid #222;
text-transform: uppercase;
font-size: 12px;
color: #7f7f7f;
}
#mikro #mikro-left #mikro-header ul li.head {
color: #00A2FF;
}
.navbar-default .navbar-nav>li>a {
color: #00A2FF;
font-weight: bold;
}
#profile .about {
border: 1px solid #e6e6e6;
padding: 55px 7px;
background: #222;
margin-top: 25px;
color: #FFFFFF;
text-align: center;
}
#profile .header {
border: 1px solid #222;
padding: 7px;
background: #222;
position: relative;
}
#posts .post {
background: #00A2FF;
padding: 5px;
height: 325px;
margin-bottom: 10px;
}
#ask-box {
background: #00A2FF;
border: 1px solid #00A2FF;
border-radius: 4px;
position: relative;
min-height: 135px;
margin-top: 40px;
margin-bottom: 15px;
font-size: 11px;
padding-bottom: 5px;
}
#posts .pagination ul li a {
color: #ffffff;
font-family: Arial;
}
#ask #questions .ask .answer {
padding: 10px 0;
line-height: 18px;
padding: 10px;
color: #ffffff;
}
#ask #question-form .foot {
background: #222;
border: 1px solid #222;
border-top: none;
padding: 5px;
height: 45px;
}
#ask {
margin: 0 -15px;
width: calc(100% + 30px);
background: #222;
}
#ask #questions .ask .info .rate .likes.good {
background: #00ff00;
}
#ask h3 {
font-family: Roboto;
color: #ffffff;
font-size: 14px;
padding-bottom: 10px;
border-bottom: 1px solid #e8e8e8;
margin-top: 15px;
}
#posts .post .content p, #posts .post .content div {
margin: 0;
padding: 5px;
color: #7f7f7f;
}
#posts .author {
background: #222;
padding: 20px;
}
#posts .comments {
background: #222;
}
#posts .comments .sortBy a {
background: #eee;
padding: 10px;
border-radius: 4px;
color: #000000;
}
#posts .comments .comment .content .title, #posts .comments .comment .content .text, #posts .comments .comment .content .options {
padding: 10px;
color: #ffffff;
}
#notify-list ul li .content, #notifications ul li .content {
width: 100%;
font-size: 12px;
color: #ffffff;
}
#notify-list ul li .time, #notifications ul li .time {
display: block;
font-size: 10px;
color: #ffffff;
}
#notify-list strong, #notifications strong {
font-size: 12px;
border-bottom: 1px solid #00A2FF;
display: block;
width: 100%;
padding-left: 10px;
color: #00A2FF;
}
#ask-box .questions .question {
color: #ffffff;
display: block;
}
.search input {
border: 1px solid #2f2f2f;
padding-left: 5px;
height: 30px;
width: 265px;
font-size: 13px;
line-height: 13px;
color: #00A2FF;
font-weight: bold;
background-color: #2f2f2f;
}
#posts .comments textarea {
height: 100px;
width: calc(100% - 100px);
resize: vertical;
background: #2f2f2f;
border: none;
border-radius: 4px;
float: right;
padding: 10px;
color: #00A2FF;
font-weight: bold;
}
.mikro .mikro-reply-form .reply-textarea textarea {
display: block;
width: 100%;
border: 1px solid #222;
background: #2f2f2f;
color: #00A2FF;
font-weight: bold;
padding: 10px;
resize: vertical;
}
.mikro .mikro-reply-form {
width: 100%;
background: #222;
padding: 10px;
display: none;
}
#profile .header .info .rank {
color: #e23a3b;
margin-top: 0px;
font-weight: bold;
font-size: 12px;
}
.thumbnail>img, .thumbnail a>img {
display: block;
height: auto;
max-width: 100%;
margin-right: auto;
margin-left: auto;
opacity: 0.5;  
}
.uploader .uploader-data {
height: 40px;
background: #428bca;
vertical-align: top;
margin-left: 5px;
line-height: 40px;
padding: 0 20px;
border-radius: 4px;
}
#profile .header .links {
border-left: #222;
height: 140px;
font-size: 14px;
display: inline-block;
vertical-align: top;
position: absolute;
right: 20px;
top: 20px;
padding-left: 15px;
}
#mikro #mikro-right .mikro-tags .mikro-tag:hover {
background: #2f2f2f;
}
#mikro #mikro-right .mikro-tags .mikro-tag {
display: table;
padding: 2px 5px;
max-width: 100%;
}
#wrapper #banners {
display: none;
}
#wrapper #menu nav ul li a {
line-height: 30px;
border-left: 1px solid #222;
border-right: 1px solid #222;
text-transform: uppercase;
font-size: 12px;
}
#ask #questions .ask .question.single {
font-size: 24px;
color: #00A2FF;
}
#ask #question-form textarea {
width: 100%;
height: 70px;
min-height: 70px;
padding: 5px;
background: #2f2f2f;
color: #00A2FF;
border: 1px solid #222;
resize: none;
border-bottom: none;
overflow: hidden;
}
#ask #questions .ask .answer {
padding: 10px 0;
line-height: 18px;
padding: 10px;
color: #7f7f7f;
}
#ask h3 {
font-family: Roboto;
color: #00A2FF;
font-size: 14px;
padding-bottom: 10px;
border-bottom: 1px solid #e8e8e8;
margin-top: 15px;
}
#ask #questions .ask .info .rate .likes.good {
background: #00A2FF;
}
#posts .post .background {
height: 180px;
background-size: cover;
background-color: #000;
position: relative;
background-position: center center;
}
}
