css:
@-moz-document domain("pikabu.ru") {
body, div[id^='wrap'] {
  font-family: "PT Sans", Verdana, Arial, Helvetica, sans-serif!important; 
  background-color: #222!important; 
  color: #bbb!important;
}

div[id^='facebook_page_block_lenta'], div[id^='facebook_page_block'], table[style$="5px;text-align: center"] {display:none!important;}

.b-story__header-info, .story_head, .b-story__header, .slide_up_container, .footer_links, .footer_links2, .footer {
  font-family: "PT Sans", Verdana, Arial, Helvetica, sans-serif!important; 
  background-color: #222!important; 
  color: #bbb!important;
}

.count{
  background-color:#171616 !important;
  border-color: gray !important;
  border-radius: 3px!important;
  border-style: solid!important;
  border-width: 1px!important;
} 

.personals {
  background-color:#222!important;
  border: 1px solid #171616!important;
  border-radius: 3px!important;
}  

.text_story_div {
  background: #222!important;
  opacity: .5!important;
  border: 0px!important;
  color: gray!important;
}
    
.text_story_div:hover {
  background: #222!important;
  opacity: 1!important;
  border: 0px!important;
  color: gray!important;
  transition: all 300ms;
}

.comment_msg {color:gray!important;}
.b-textarea {background-color:#171616!important;}
.header_links a {color: gray!important;}
.noactive a {color:#D7D8D8!important;background-color:#555!important;}
.active2 a, .active a {color:#D7D8D8!important;background-color:#171616!important;border:none!important;background:none!important;}

table[lang*="/go/"] {display: none;}
table[class="inner_wrap inner_wrap_visible"] {display: none;}
div[style="height: 30px"] {display: none;}
div[style^="width: 100%; background: url('http://s.pikabu.ru/images/header_bg13.gif')"] {
  background: url("http://i.imgur.com/teUgsDm.png")  #f0f0f0 repeat-x bottom!important;
}

.b-story__link, .story_link, .b-story__link_visited_yes {
  color: #738290!important; 
}

td[style^="background: url(http://s.pikabu.ru/images/menu_elements_top_bottom.png)"] {display: none!important;}

.rating_bl, .name {
  text-shadow: 0 0 0!important;
  border-bottom: 0px!important;
  background-color: #171616!important;
}

.tag {
  background-color: #171616!important;
  color: #666666!important;
  text-shadow: 0 0 0!important;
  border-color: gray!important;
}

img[src$="keysall.png"] {
  content: url("http://i.imgur.com/tSiAC99.png");
}

img[src$="logout.png"] {
  content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAAQCAYAAAAbBi9cAAAAf0lEQVR42mNgwAHmzZuXExcb+59YjMscBmI0Ozs7hzs6OupbW1vzkmwQSDMQC+PVjM8ggjYTaxADuWDgDfr8+XMOsgayDfry5YsuTFNoaCgbumEkewFdI8UGwTQ7ODgIgNhAWgGvocQYAjVIgCjXwQIbV4DTP9ZoliDRixFC6gHsMeeruMs8GgAAAABJRU5ErkJggg==);
}

input[src$="search_btn4n.png"] {
  content: url("http://i.imgur.com/JoGzRYY.png");
}

img[src*="/post_img/"] {
  -webkit-filter: grayscale(0.8); filter: grayscale(0.8);
  transition: all 300ms;
  opacity: 0.6;
}

.name h4 {background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAMkAAAABCAYAAAB0fYrcAAAAg0lEQVR42q1SWwrAMAjbIXzc/6QbSjKClFG2fQRtqzZJe2TEGe4N5hXdrBGV87zWjOybEXkKOHOF1d3JvTkfaF7Cgb2udei/5yF27UJbUBtqXbS81baDN/r17E8uTxzLrxx+B96hvDL6KX/E5c+4alL+9HXlw4ZPzQvvabgrBof4yOECWY/7Wpb7E74AAAAASUVORK5CYII=) no-repeat left bottom!important;}
.name h4 a {color: #738290!important;}
.avatar {border: 4px solid #171616!important;}
.personals {background: none;}
.pc-line-sec {background-color:#171616!important;}
.login_f {border: 1px solid #171616!important;}
.short {color:#777!important;}
.pc-panel-bl {background-color:#222!important;}
.name {background: none;}
a.author {color:#c53!important;}
#search_menu {background:#222!important;}

td[style^="border-left: dotted"] {
  border-left: dotted 1px #171616!important;
}

table[style="width: 90%; background-color: rgb(255, 255, 255);"] {
  background-color:#222!important;
  border: 1px solid #1c1c1c!important;
  border-radius: 15px!important;
}

a[href$="id=awards"] {text-shadow: 0 0 0!important;}

img[src*="/post_img/"]:hover {
  -webkit-filter: grayscale(0); filter: grayscale(0);
  opacity: .99;
}
  
.profile_wrap {
  background: #171616!important;
  text-shadow: 0 0 0!important;
  border: #222!important;
}
}
