.rotate-animation {
    -webkit-animation: rotate 2s linear 0s infinite normal;
    -moz-animation: rotate 2s linear 0s infinite normal;
    -o-animation: rotate 2s linear 0s infinite normal;
    -ms-animation: rotate 2s linear 0s infinite normal;
    animation: rotate 2s linear 0s infinite normal;
}
@-webkit-keyframes rotate {
    0% {
        -webkit-transform: translate(0px, 0px) rotate(3deg);
    }
    10% {
        -webkit-transform: translate(0px, -1px) rotate(2deg);
    }
    20% {
        -webkit-transform: translate(0px, -2px) rotate(1deg);
    }
    30% {
        -webkit-transform: translate(0px, -3px) rotate(0deg);
    }
    40% {
        -webkit-transform: translate(0px, -4px) rotate(-1deg);
    }
    50% {
        -webkit-transform: translate(0px, -5px) rotate(-2deg);
    }
    60% {
        -webkit-transform: translate(0px, -4px) rotate(-3deg);
    }
    70% {
        -webkit-transform: translate(0px, -3px) rotate(-2deg);
    }
    80% {
        -webkit-transform: translate(0px, -2px) rotate(-1deg);
    }
    90% {
        -webkit-transform: translate(0px, -1px) rotate(0deg);
    }
    100% {
        -webkit-transform: translate(0px, 0px) rotate(1deg);
    }
}
@-moz-keyframes rotate {
    0% {
        -moz-transform: translate(0px, 0px) rotate(3deg);
    }
    10% {
        -moz-transform: translate(0px, -1px) rotate(2deg);
    }
    20% {
        -moz-transform: translate(0px, -2px) rotate(1deg);
    }
    30% {
        -moz-transform: translate(0px, -3px) rotate(0deg);
    }
    40% {
        -moz-transform: translate(0px, -4px) rotate(-1deg);
    }
    50% {
        -moz-transform: translate(0px, -5px) rotate(-2deg);
    }
    60% {
        -moz-transform: translate(0px, -4px) rotate(-3deg);
    }
    70% {
        -moz-transform: translate(0px, -3px) rotate(-2deg);
    }
    80% {
        -moz-transform: translate(0px, -2px) rotate(-1deg);
    }
    90% {
        -moz-transform: translate(0px, -1px) rotate(0deg);
    }
    100% {
        -moz-transform: translate(0px, 0px) rotate(1deg);
    }
}
@keyframes rotate {
    0% {
        transform: translate(0px, 0px) rotate(3deg);
    }
    10% {
        transform: translate(0px, -1px) rotate(2deg);
    }
    20% {
        transform: translate(0px, -2px) rotate(1deg);
    }
    30% {
        transform: translate(0px, -3px) rotate(0deg);
    }
    40% {
        transform: translate(0px, -4px) rotate(-1deg);
    }
    50% {
        transform: translate(0px, -5px) rotate(-2deg);
    }
    60% {
        transform: translate(0px, -4px) rotate(-3deg);
    }
    70% {
        transform: translate(0px, -3px) rotate(-2deg);
    }
    80% {
        transform: translate(0px, -2px) rotate(-1deg);
    }
    90% {
        transform: translate(0px, -1px) rotate(0deg);
    }
    100% {
        transform: translate(0px, 0px) rotate(1deg);
    }
}
