css:
@-moz-document url-prefix(https://news.ycombinator.com/) {

@font-face {
  font-family: "Open Sans";
  src: local("Open Sans"), local("OpenSans"), /* in case the font is installed */
  url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

body {
  margin: 0;
}
body > center > table {
  width: 100%;
  background-color: transparent;
}
body > center > table > tbody > tr:first-child > td {
  background-image: none;
  border-bottom: none;
  background: white;
  box-shadow: 0 2px rgba(0, 0, 0, .1);
}
body > center > table > tbody > tr:first-child > td > table {
  padding: 10px !important;
  margin: 0 auto;
  width: 960px;
}
body > center > table > tbody > tr:first-child > td > table > tbody > tr > td:first-child {
  padding-right: 10px;
}
body[alink] {
  width: 400px;
  margin: 0 auto;
  padding: 3em 0;
}
a[href="forgot?id="] {
  margin-bottom: 1em;
  display: block;
}
a:link {
  color: #555;
}
.pagetop {
  font-family: "Open Sans", "Trebuchet Ms", Helvetica, sans-serif;
  font-size: 14px;
}
.pagetop a {
  color: #696969 !important;
}
.pagetop a:visited {
  color: #7E7E7E;
}
a {
  font-weight: normal;
}
a:hover {
  text-decoration: underline;
}
body > center > table > tbody > tr:nth-child(3) > td {
  padding: 20px 0;
}
body > center > table > tbody > tr:nth-child(3) > td > table {
  width: 960px;
  margin: 0 auto;
}
.title {
  font-family: "Open Sans", "Trebuchet Ms", Helvetica, sans-serif;
  font-size: 16px;
}
.title {
  color: #696969;
}
.title > a {
  font-weight: normal;
  color: #D43900;
}
.title > a:visited {
  color: #7E7E7E;
  font-weight: normal !important;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:first-child > td:nth-child(2).title > a {
  font-size: 18px;
}
.subtext {
  height: 15px;
  font-family: "Open Sans", "Trebuchet Ms", Helvetica, sans-serif;
  vertical-align: top;
  font-size: .7em;
  color: #545454;
}
.subtext a:link {
  color: #545454
}
.subtext a:visited {
  color: #7E7E7E;
}
td, .comment, .default, .comhead, .yclinks, .dead {
  font-family: "Open Sans", "Trebuchet Ms", "Lucida Grande", "Segoe UI", Arial, Helvetica, sans-serif;
}
td[bgcolor="#ff6600"] {
  background-color: white;
}
.title .comhead {
  font-size: .8em;
}
.comhead {
  font-size: 1em;
  margin-left: 2px;
  color: #545454;
}
.comhead a:link {
  color: #545454;
}
body > center > table > tbody > tr:nth-child(3) > td > form {
  width: 960px;
  margin: 0 auto;
}
td > center > table {
  width: 960px;
  background-color: white;
  border: solid 1px #000;
}
td > center > table td {
  padding: 10px;
}
body > center > table > tbody > tr > td > table {
  width: 960px;
  margin: 0 auto;
}
.comment {
  font-size: 1.1em;
  line-height: 1.4em;
}
.comment u, .comment u a, .comment u a:visited, .comment + p u, .comment + p u a, .comment + p u a:visited {
  text-decoration: none;
  color: #777;
}
.comment p, .comment + p {
  margin: .9em 0 0 0;
  color: #555;
}
font[color="#000000"] {
  color: #555 !important;
}
tr[style="height:5px"] {
  height: 10px !important;
}
.comhead a[href*="user?id="] {
  font-weight: bold !important;
  color: #d35400 !important;
  font-size: 1.1em;
}
.subtext a[href*="user?id="] {
  border-bottom: 1px dotted #7E7E7E;
  padding-bottom: 2px;
  text-decoration: none;
}
.subtext a[href*="user?id="]:hover {
  border-bottom: 1px dotted #d35400;
}
a[href="news"] {
  font-weight: bold !important;
}
a[href*="news?p="] {
  color: #d35400;
}
a[href*="news?p="]:before {
  content: "Â» ";
}
.yclinks a {
  color: #545454 !important;
}
.cdd, .c00 {
  color: #545454;
}
.c00 a::before, .c00 a::after {
  display: inline-block;
  opacity: 0;
  -webkit-transition: -webkit-transform 0.3s, opacity 0.2s;
  -moz-transition: -moz-transform 0.3s, opacity 0.2s;
  transition: transform 0.3s, opacity 0.2s;
}
.c00 a::before {
  margin-right: 2px;
  content: '[';
  -webkit-transform: translateX(4px);
  -moz-transform: translateX(4px);
  transform: translateX(4px);
}

.c00 a::after {
  margin-left: 2px;
  content: ']';
  -webkit-transform: translateX(-4px);
  -moz-transform: translateX(-4px);
  transform: translateX(-4px);
}

.c00 a:hover::before,
.c00 a:hover::after,
.c00 a:focus::before,
.c00 a:focus::after {
  opacity: 1;
  -webkit-transform: translateX(0px);
  -moz-transform: translateX(0px);
  transform: translateX(0px);
}
input[type="submit"] {
  display: inline-block;
  width: 100%;
  padding: 6px 12px;
  margin-bottom: 0;
  font-size: 14px;
  font-weight: 400;
  line-height: 1.42857143;
  text-align: center;
  white-space: nowrap;
  vertical-align: middle;
  -ms-touch-action: manipulation;
  touch-action: manipulation;
  cursor: pointer;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  background-image: none;
  border: 1px solid transparent;
  border-radius: 2px;
  color: #333;
  background-color: #fff;
  border-color: #ccc;
  font-family: "Open Sans", "Trebuchet Ms", sans-serif;
}
textarea, input[type="text"], input[type="password"], select {
  font-family: "Open Sans", "Trebuchet Ms", sans-serif;
  font-size: 16px;
  color: #555;
  width: 100%;
  border-radius: 2px;

  background-color: #fff;
  background-image: none;
  border: 1px solid #ccc;
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
  box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
  -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
  -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
  transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
form[action="//hn.algolia.com/"] input[type="text"] {
  width: 300px;
  display: block;
  margin-top: 10px;
  padding: 5px;
}
input[type="submit"]:hover {
  color: #333;
  background-color: #e6e6e6;
  border-color: #adadad;
}
code {
  display: block;
  padding: 9.5px;
  margin: 0 0 10px;
  font-size: 13px;
  line-height: 1.42857143;
  color: #333;
  word-break: break-all;
  word-wrap: break-word;
  background-color: #f5f5f5;
  border: 1px solid #eee;
  border-radius: 2px;
  font-family: Consolas, Monaco, "Lucida Console", "Liberation Mono", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Courier New";
}

}
