.LanguageLinks {
    float:right;
    max-width:10em;
}
.mw-headline[id="Description"] {
    display:none;
}
.wrapper {
    max-width:73.8462em;
}
#bodyContent h2.pagetitle {
    margin-top:1em;
    margin-bottom:1em;
    font-size:2em;
    font-family:serif;
    line-height:1.3;
}
#bodyContent h3,
#bodyContent div > h3
{
    padding-bottom:.15em;
    margin-top:1.15em;
}
#bodyContent pre,
#bodyContent code {
    margin-bottom:2.35rem;
}

#localNotice > div[style*="padding:"] {
    padding:.7em !important;
}
#pagebody {
    font-size:inherit;
    margin-top:0;
}
#pagebody #bodyContent ul,
#pagebody #bodyContent dl,
#pagebody #bodyContent ol {
    margin-bottom:1.7em;
}
#pagebody #bodyContent p {
    margin-top:1.35em;
    margin-bottom:1em;
    max-width2:60em;
}
#pagebody #bodyContent p + p {
	margin-top:.65em;
}
#toc {
    clear:right
}
#toc ~ h2 {
    border:none;
}
body {
    font-size:16px;
    line-height:1.7;
}
div[class^="col-"] {
    margin:0 1em;
}
div.col-2 {
    width:10.3076em;
}
div.col-10 {
    width:59.5380em;
}
div#localNotice {
    font-size:.75rem !important;
}
}
