@-moz-document domain(tumblr.com) {



/* Dashboard - Sarah */



#home_button {



    padding-right: -17px !important;

}





#home_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/IUEneq5bw/sarah.png') !important;}



/* Help - Tony */



#help_button {



    padding-right: -17px !important;

}





#help_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/nN6neq70d/tony.png') !important;}



/* Inbox - Alison */



#inbox_button {



    padding-right: -17px !important;

}





#inbox_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/gfyneq672/alison.png') !important;}



/* Settings - Cosima */



#preferences_button {



    padding-right: -17px !important;

}





#preferences_button a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/4pqneq4iy/cosima.png') !important;}



/* xkit - Helena */



#xkit-control {



    padding-right: -17px !important;

}





#xkit-control a.tab_anchor {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/V7Aneq86n/helena.png') !important;}



/* Logout - Rachel */



.tab.iconic.tab_log_out a{



    padding-right: -17px !important;

}





.tab.iconic.tab_log_out a {



    height: 0 !important;

    width: 0 !important;

    top: 0px !important;

    padding-left: 36px !important;

    padding-top: 40px !important;

    background: url('http://static.tumblr.com/9wzmztk/kOVneq59u/rachel.png') !important;}





/*Positioning*/

.tab_bar .iconic a.tab_anchor {background-size: 175px 36px;}



.tab_bar .tab_discover .tab_anchor {background-position: -175px 0}



.tab_bar .tab_log_out .tab_anchor {background-position: -140px 0}



.tab_bar .tab_settings .tab_anchor {background-position: -105px 0}



.tab_bar .tab_help .tab_anchor {background-position: -70px 0}



.tab_bar .tab_inbox .tab_anchor {background-position: -35px 0}



.tab_bar .tab_home .tab_anchor {background-position: 0 0}



.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}



.tab.iconic > .tab_anchor::before {content: none !important;}



}
