css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/")

{

#posts .post.new_post 
{
min-height: 88px !important;
}

.new_post_label/*_icon*/ > i
{
background-image: url('/*[[theimage]]*/') !important;
background-size: 513px, 190px !important;
margin-left: 3px !important;
margin-top: -3px !important;
background-repeat: no-repeat !important;
}

.new_post_label
{
padding: 62px 1px 5px 0px !important;
}

.new_post_label i
{
height: 60px !important;
}

.new_post_label > i 
{
color: transparent !important;
}

#new_post_label_text > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:active > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:hover > i
{
background-position: 3px -6px !important;
}

#new_post_label_photo > i
{
background-position: -72px -4px !important;
}
#new_post_label_photo:active > i
{
background-position: -72px -4px !important;
}
#new_post_label_photo:hover > i
{
background-position: -72px -6px !important;
}

#new_post_label_quote > i
{
background-position: -149px -4px !important;
}
#new_post_label_quote:active > i
{
background-position: -149px -4px !important;
}
#new_post_label_quote:hover > i
{
background-position: -149px -6px !important;
}

#new_post_label_link > i
{
background-position: -223px -4px !important;
}
#new_post_label_link:active > i
{
background-position: -223px -4px !important;
}
#new_post_label_link:hover > i
{
background-position: -223px -6px !important;
}

#new_post_label_chat > i
{
background-position: -297px -4px !important;
}
#new_post_label_chat:active > i
{
background-position: -297px -4px !important;
}
#new_post_label_chat:hover > i
{
background-position: -297px -6px !important;
}

#new_post_label_audio > i
{
background-position: -374px -4px !important;
}
#new_post_label_audio:active > i
{
background-position: -374px -4px !important;
}
#new_post_label_audio:hover > i
{
background-position: -374px -6px !important;
}

#new_post_label_video > i
{
background-position: -449px -4px !important;
}
#new_post_label_video:active > i
{
background-position: -449px -4px !important;
}
#new_post_label_video:hover > i
{
background-position: -449px -6px !important;
}

#new_post_label_text,#new_post_label_photo,#new_post_label_quote,#new_post_label_link,#new_post_label_chat,#new_post_label_audio,#new_post_label_video
{
line-height: 25px !important;
font-size: 12px !important;
font-family: Arial Black !important;
text-transform: uppercase!important;
} 

#new_post_label_text
{
color: rgba(109, 109, 110, 1) !important;
}
#new_post_label_photo
{
color: rgba(223, 129, 106, 1) !important;
}
#new_post_label_quote
{
color: rgba(243, 177, 91, 1) !important;
}
#new_post_label_link
{
color: rgba(110, 198, 150, 1) !important;
}
#new_post_label_chat
{
color: rgba(109, 184, 222, 1) !important;
}
#new_post_label_audio
{
color: rgba(186, 151, 204, 1) !important;
}
#new_post_label_video
{
color: rgba(152, 161, 168, 1) !important;
}

#new_post_label_text:hover,#new_post_label_photo:hover,#new_post_label_quote:hover,#new_post_label_link:hover,#new_post_label_chat:hover,#new_post_label_audio:hover,#new_post_label_video:hover
{
font-size: 12px !important;
font-family: Arial Black !important;
text-transform: uppercase!important;
color: #000000 !important;
}
}
