css:
@-moz-document domain("player.me") {
body {
	color: #A5A5A5;
}

.card, .new-post .writer.real, #page-feed.theme--light .post--container {
	background: #282828;
	color: #8C8C8C;
}

.btn-transparent {
	border: none;
}

.feed-sidebar .stats>button:first-child {
	border-right: 1px solid #5B5B5B;
}

#page-feed .new-post.card .writer-container, #page-feed.theme--light .comment--writer-container {
	border-color: #5B5B5B;
  	color: #666;
}

.feed-sidebar .stats {
	border-top: 1px solid #5B5B5B;
}

#page-feed .feed-sidebar .online-users .title, #page-feed .feed-sidebar .profile .username, #page-feed .feed-sidebar .suggestion .title, .feed-sidebar .online-users #page-feed .title, .feed-sidebar .profile #page-feed .username, .feed-sidebar .suggestion #page-feed .title {
	color: #999;
}

.feed-sidebar .online-users li, .feed-sidebar .suggestion li {
	border-top: 1px solid #5B5B5B;
}

.feed-sidebar .btn-stats strong, #page-feed .username, #page-feed.theme--light .post--info-username {
	color: #7584A2;
}

.feed-container .activity .content-inner, #page-feed.theme--light .post--content-wrapper {
	background: #2B2B2B;
	border-bottom: 1px solid #1B1B1B;
}

#page-feed .comments-show-all-container, #page-feed.theme--light .comments--show-all-container, #page-feed.theme--light .post--show-more-content {
	background: #131313;
}

.new-comment-container, #page-feed.theme--light .comment--container {
	position: relative;
	margin-bottom: 10px;
	border-top: 1px solid #333333;
}

.comment-writer.mirror, .comment-writer.real, #page-feed.theme--light .comment--writer.mirror, #page-feed.theme--light .comment--writer.real {
	background: #222;
	border-bottom: 1px solid #1B1B1B;
  	color: #666;
}

.feed-container .activity .post, #page-feed.theme--light .comment--content {
    color: #A6A6A6;
}

.feed-container .activity .comment-metas, .feed-container .activity .post-metas, #page-feed.theme--light .post--meta {
	background-color: #3F3F3F;
}

.feed-container .activity .comment-parent-container {
	border-top: 1px solid #464646;
}

a[href=PCMasterRace], #page-feed a[href$="pcmasterrace"] .username {
	color: #ffd305;
}

#top-bar .messaging .title, #top-bar .notification-container .notifications .title {
	border-bottom: 1px solid #282828;
	background: #2D2B2B;
}

.dropdown-menu {
	background-color: #292929;
}

#top-bar .messaging a .username, #top-bar .notification-container .notifications a .username {
	color: #7584A2;
}

.messaging--header {
	background: #222222;
}

.messaging--input .message--writer.mirror, .messaging--input .message--writer.real {
	background: #1C1C1C;
}

.messaging--input {
	border-top: 1px solid #303030;
}

.messaging--container.minimized {
	border: 1px solid #424242;
}

.messaging--container.offline .messaging--header {
	border-bottom: 2px solid #121212;
}

.dropdown-menu>li>a {
	color: #A6A6A6;
}

.dropdown-menu .divider {
	background-color: #434343;
}

#top-bar .messaging .unread, #top-bar .notification-container .notifications .unread {
	background: #222222;
}

.page-profile .content-container, .page-browse, #wrap {
	background: #1D1D1D;
}

.nav>li>a:focus, .nav>li>a:hover {
	background-color: #2D2C2C;
}

.form-control, .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
	background-color: #181818;
	border: 1px solid #131313;
}

.loading {
	background: rgba(29, 29, 29, 0.8);
}

#page-feed .stream-entity .game-name {
	border-bottom: 1px solid #424242;
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {
	background-color: #252525;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
	border-top: 1px solid #2A2A2A;
}

.table>thead>tr>th {
	vertical-align: bottom;
	border-bottom: 2px solid #161616;
}

.table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
	background-color: #2E2E2E;
}

#page-feed .comment blockquote div:before, #page-feed .comment blockquote p:before {
	border-left: solid 4px #444343;
}

.card.flat {
	-webkit-box-shadow: none;
	box-shadow: none;
	border: 1px solid #3A3B3C;
}
  
.messaging--container .messaging--title a {
    color: #7584A2;  
}

.heading-tabs li a, .browse-media .name a, .player-card.player-card-simple .username {
	color: #7E8FB1;
}

.btn:not([class$="btn-"]):not([class*=" btn-"]) {

  	background: #2D2D2D;
}

  
.btn:not(.btn-primary):focus, .btn:not(.btn-primary):hover {
color: #6B6767;
}

.search-input-container:after {
    background-position: -515px -372px;
}

.feed-container .showmore {
	background: #303030;
}

#page-feed.theme--light .comment--username {
	background: transparent; 
}
           
a[href="/cronhound"], #page-feed a[href="/cronhound"] .username, #page-feed.theme--light a[href="/cronhound"] .post--info-username {
	color: #f00;
}
            
a[href="/pcmasterrace"], #page-feed a[href="/pcmasterrace"] .username, #page-feed.theme--light a[href="/pcmasterrace"] .post--info-username {
	color: #ffd305;
}
}
