css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://tut-tuuut.github.io/2014/11/08/5-ans-de-nanowrimo.html") {
  
  body
  {
    background-image: none;
    background-image: linear-gradient(transparent calc(100% - 1px), rgba(0, 0, 0, 0.2) 100%);
    background-position: 0 0;
    background-size: 3.07692308em 3.07692308em;
    margin: 0;
  }
  .site
  {
    font-size: 1.53846154em;
    line-height: 2em;
    margin-top: 2em;
  }
  header h1
  {
    margin-top: -4px;
    position: relative;
    top: 4px;
    padding: 1.33333em;
    margin-bottom: 1.33333em;
  }
  p
  {
    margin: 2em 0;
  }
  .post img
  {
    height: 560px;
    width: auto;
    display: block;
  }
  h2
  {
    margin: 1.3333333em 0;
  }
  .prevnext
  { 
    margin-top: -2px;
    position: relative;
    top: 1px;
  }
  footer
  {
    margin-top: -4px;
    position: relative;
    line-height: 2.5em;
  }
  footer p
  {
    margin: 1.25em 0;
  }
  article
  {
    margin-bottom: 3em;
  }
  #disqus_thread
  {
    height: 32em;
  }
}
