css:
@-moz-document url-prefix("http://tabun.everypony.ru/"), url-prefix("https://tabun.everypony.ru/") {



.block.block-type-search, .nav.nav-menu, .blog, section.block.block-type-pseudomenu, section.block.block-type-stream, .spoiler-title, .button, input.input-text, select, .modal, .comment-preview, #container {border-radius: 0 !important;}

.comment .folding {background: url(http://i.imgur.com/Pq6QlnL.png) !important; background-size: auto 13px !important;}

.comment .folding.folded {background-position: -14px -14px !important;}

.modal.modal-write .write-list li .write-item-image {background: url(http://i.imgur.com/6boHMOx.png) no-repeat}

.modal.modal-write .write-list li.write-item-type-file .write-item-image {background: url(http://i.imgur.com/lmvAgl2.png) no-repeat}

.modal.modal-write {background: #181818 !important;}

.block.block-type-blogs .block-blog-list li, .profile-dotted-list li {background: none !important;}

#c-header,
.modal.modal-login,
a.avatar,
div.wrapper-content.wrapper-content-dark,
p.upload-photo,
ul.profile-dotted-list li span,
ul.profile-dotted-list li strong,
.modal.modal-upload-avatar,
.comment, .blog .blog-footer,
.blog-mini,
.blog-inner,
.blog .blog-content,
.blog-info li span,
.blog-info li strong,
.dropdown-menu-create,
.poll-create {
	background: #131313 !important;
}

.poll .poll-result li dl dd div {background: #202020 !important;}

div.topic-content.text a {background: inherit !important;}

#container {background: #131313 !important;	border-color: #969696 !important;}

#content {border-style: none !important;}

#header {background: #202020 !important;}

#wrapper {color: #969696 !important; border-color: #969696 !important;}

a.button-write.js-write-window-show {color: #ff59ff !important;}

section.toolbar-topic {background-color: #202020 !important; color: #00ff00 !important;}

.button {background: #303030 !important; color: #969696 !important; border: none !important; box-shadow: none !important; -webkit-box-shadow: none !important;}

ul.nav.nav-pills li a, hr {border-color: #969696 !important;}

ul.main-menu li a {color: #969696 !important; margin-left: 5px !important;}

.nav.nav-main li.active a {color: #969696 !important; text-decoration: none !important;}

.nav.nav-menu li.active a {font-weight: 700 !important; color: #969696 !important; text-decoration: none !important;}

.nav.nav-pills li.active a {color: #969696 !important; text-decoration: none !important; background: inherit !important; border-color: #ebf0f4 !important;}

.modal.modal-image-upload {color: #969696; background: #131313}

.dropdown-user-menu li.item-stat .strength {color: #f2555c !important; margin-right: 8px !important;}

.table.table-blogs .cell-name p .blog-name {font-size: 18px !important; color: #969696 !important;}

.table .cell-tab .cell-tab-inner.active {background: rgba(64, 78, 128, 0) !important;}

.nav.nav-pills li a.new {margin-left: 5px !important; text-decoration: none !important; color: #969696 !important; position: relative !important; top: -2px !important;}

.comment-content {background-color: #202020 !important;	margin-bottom: 5px !important; border: none; padding: 5px 10px; border-radius: 0 !important; border-bottom: 1px solid #222222;}

.text blockquote { background: inherit !important; color: #969696 !important; border: none !important; border-left: 5px solid #969696 !important;}

.text pre, .text code {background: #050505 !important; color: #c80000 !important; border: 1px solid #252525 !important;}

.comment.comment-current .comment-content {border: none; border-bottom: 1px solid #222222; background: #343434 !important; border-left: 5px solid #666666 !important; padding: 5px 5px;}

.comment.comment-new .comment-content, .comment-preview {background: #343434 !important;}

.comment-preview {color: #ffcdcd !important; border: none !important; border-left: 5px solid #969696 !important; padding: 5px 6px;}

.comment-preview:hover {border-left: 5px solid #969696 !important; background: #404040 !important;}

.toolbar section {background: #202020 !important; border: none !important; border-radius: 10px !important; transition: 0.5s; box-shadow: 0 0 5px black;}

.toolbar section:hover {border-radius: 0 !important; box-shadow: none;}

h1, h2, h3, h4, h5, h6 {color: #969696 !important;}

.block {border-bottom: 1px dotted #202020 !important;}

.vote-count-positive .vote-count {color: #2DCC4C !important;}

.stream-comment-preview:after {border-color: transparent transparent #202020 !important; box-shadow: none !important;}

.stream-comment-preview {background: #202020 !important; color: #969696 !important; box-shadow: none !important;}

.block.block-type-blog .block-header h3 a {color: #969696 !important; text-decoration: none;}
 
.comment-info li.comment-author a {color: #aaaaaa !important; font-size: 1.2em;}

.comment-info {background: #131313 !important;}

.comment {-webkit-transition: none !important;}

p.text a img {display: none !important;}

a {color: #aaaaaa !important;}

.dropdown-menu-create li a {border-bottom: 1px dotted #969696;}

.spoiler-gray {color: #505050 !important; background-color: #505050 !important; transition: 0.2s;}

.spoiler-gray:hover {color: #DFDFDF !important;}

.toolbar .toolbar-update .update-comments i {
	background: #303030;
	width: 2em;
	height: 2em;
	border-radius: 50%;
	margin-top: 0.5em;
	margin-bottom: 0.5em;
	margin-left: auto;
	margin-right: auto;
	transition: 0.5s;
}

.toolbar .toolbar-update .update-comments.active i {background: #666666;}

.toolbar {top: 40vh !important; right: 10px !important;}

.toolbar .toolbar-topic {display: none;}

.block .block-update {
	width: 20px;
	height: 20px;
	border-radius: 10px;
	background: #303030;
	transition: 0.5s;
}

.block .block-update:hover {background: #666666;}

#up-switch, #down-switch {display: none !important;}

.blog-list-info {
	width: 0;
	height: 0;
	display: inline-block !important;
	background: none !important;
	border-left: 5px solid transparent;
	border-right: 5px solid transparent;
	border-top: 10px solid #303030;
	margin-left: 0.5em;
	transition: 0.5s;
}

.blog-list-info:hover {border-top: 10px solid #666666;}

.infobox.infobox-help .tip-arrow {background: none !important;}

.block.block-type-profile-note {border: 1px solid #252525 !important;}

body, #c-header, .ui-widget-content {font-family: Arial, sans-serif !important;}

.nav.nav-pills li,
.block-type-stream .block-content .latest-list .stream-topic,
.nav.nav-menu li.active a,
b,
strong,
.table.table-users tbody .cell-skill,
.table.table-blogs tbody .cell-rating,
.ls-user {
	font-family: Tahoma, Arial, sans-serif !important;
}

.comment-info li.comment-author.comment-topic-author a {font-style: italic !important;}

.block-type-stream .block-content .latest-list .stream-topic {font-weight: 400 !important;}

.text pre,
.markItUpEditor,
.comment-info li.comment-author.comment-topic-author a,
.comment-info li.comment-author a,
.block-type-stream .block-content .latest-list p a.author,
#header .dropdown-user .username,
div.topic-info,
.table.table-profile-notes .cell-username,
.table.table-talk tbody .cell-recipients,
.topic-footer .topic-info a,
a.username,
.user,
.ls-user {
	font-family: Consolas, monospace !important;
}

#header .dropdown-user .username {font-size: 1.3em;}

div.topic-info, .table.table-talk tbody .cell-recipients {font-size: 1.1em;}

.table.table-profile-notes .cell-username, .topic-footer .topic-info a {font-size: 1.2em;}

#new_messages,
#logolink a,
div.topic-content.text,
div.topic-content.text h4,
div.topic-info,
h2.page-header,
h2.page-header.user-login.word-wrap.no-user-name,
header.block-header.sep h3,
header.block-header.sep h3 a,
li.item-favourite a,
li.item-settings a,
li.item-signout a,
tbody tr td,
ul.nav.nav-main li a,
div.talk-blacklist-form h3,
header.block-header h3,
div.content.faq-page h2,
div.topic-content.text h3,
#smiles_config,
a.list-item-block,
a.desc span,
.table a.asc,
.table a.desc,
li.topic-info-comments a.new,
.nav.nav-profile li.active a,
.profile-info-about,
.table.table-users tbody .cell-skill,
li.topic-info-comments span.count,
.block-type-stream .block-content .latest-list .stream-topic,
.pagination ul li.active span,
li.active a {
	color: #969696 !important;
}

#new_messages:hover,
a:hover,
.favourite:hover,
li.item-signout a:hover,
li.item-favourite a:hover,
.block-type-stream .block-content .latest-list .stream-topic:hover,
.block.block-type-blog .block-header h3 a:hover,
li.item-settings a:hover,
ul.main-menu li a:hover,
ul.nav.nav-main li a:hover,
.table.table-blogs .cell-name p .blog-name:hover,
.comment-info li.comment-author a:hover,
.nav.nav-menu li.active a:hover,
.nav.nav-pills li.active a:hover,
.nav.nav-profile li.active a:hover,
li.active a:hover,
.topic-tags a:hover,
.comment-info li a.link-dotted:hover,
#widemode a:hover,
.block.alltags ul.cloud li a:hover,
li.topic-info-comments:hover,
div.favourite:hover {
	color: #f2555c !important;
	text-decoration: underline;
}

#logolink a:hover {color: #f2555c !important;}

.block.alltags ul.cloud li a:hover {background: none;}

a, #header .dropdown-user .username, .topic-tags a, #widemode a, li.topic-info-comments span, div.favourite, .nav.nav-profile li a, .nav.nav-pills li a, .block .nav.nav-pills a {text-decoration: none;}

.block .nav.nav-pills a:hover {border-bottom: 1px dotted #ff4747 !important;}

.nav.nav-profile li {border-bottom: none !important;}

.nav.nav-profile {margin-top: 0 !important;}

.comment-content:hover,
#widemode:hover,
div.block.block-type-blogs:hover,
div.talk-search:hover,
section.block:hover,
section.block.block-type-stream:hover,
section.talk-search.talk-friends:hover,
section.block.block-type-pseudomenu:hover,
.toolbar .toolbar-update:hover,
.toolbar section:hover,
.stream-get-more:hover,
div.profile-photo-wrapper:hover,
.comment.comment-self .comment-content:hover,
.search-item.search-item-abc ul li a:hover,
#header:hover,
.poll .poll-result li dl dd div:hover,
.profile-page-header:hover {
	background: #222222 !important;
}

.nav.nav-profile li.active a, .nav.nav-pills li.active a {font-weight: bold;}

.comment.comment-new .comment-content:hover {background: #363636 !important;}

.comment.comment-current .comment-content:hover {background: #363636 !important;}

div.block.block-type-blogs,
div.talk-search, section.block,
section.block.block-type-stream,
section.talk-search.talk-friends,
.stream-get-more,
body,
div.profile-photo-wrapper {
	background: #202020 !important;
	border: none !important;
	border-radius: 0;
}

.block.block-type-blogs, .block.block-type-stream, .download {background: none !important;}

div.block.alltags,
div.cell-tab-inner.active,
.ui-widget-content,
.search-form-wrapper,
.block.block-type-profile .upload-photo,
.system-message-notice {
	background: #202020 !important;
}

div.profile-info-about h3, h2.header-table {color: #858585 !important;}

input.input-text, select {color: #969696 !important; background: #202020 !important; border: 1px solid #505050 !important;}

span.rating {color: #55eb55 !important;}

section.block.block-type-pseudomenu,
span.spoiler-title,
div.talk-blacklist-form {
	background-color: #303030 !important;
	color: #969696 !important;
	border: none !important;
}

span.spoiler-title, .button {transition: 0.2s;}

section.block.block-type-pseudomenu {background-color: #202020 !important; color: #969696 !important; border: none !important;}

span.strength, .profile .strength .count {color: #f2555c !important;}

.comment.comment-self .comment-content {background: #202020; color: #d89696 !important;}

.infobox.infobox-help {background: #131313 !important; color: #969696 !important; border: 1px solid #252525 !important;}

span.spoiler-title:hover,
.button:hover {
	background: #404040 !important;
	box-shadow: none !important;
}

.table tr:hover td {background: #202020 !important;}

.table tbody td, .table th, .table .cell-tab .cell-tab-inner.active, .table.table-profile-notes {border: none !important;}

::selection {color: #969696; background: rgba(255, 255, 255, 0.2);}
::-moz-selection {color: #969696; background: rgba(255, 255, 255, 0.2);}

.input-text {box-shadow: none !important; padding: 6px !important;}

.form-profile .avatar-change .avatar,
.profile-info-about .avatar,
#header .dropdown-user .avatar {
	border: none !important;
	background: inherit !important;
	transition: background 0.5s;
}

textarea {color: #969696 !important; background-color: #202020 !important;}

.markItUpHeader {background: #505050 !important;}

textarea, .markItUpHeader, .dropdown-menu-create {border: 1px solid #505050 !important;}

textarea.markItUpEditor {width: 100% !important; min-height: 100px !important;}

.user-list-mini li:hover {background: #202020 !important;}

.ui-state-hover, .ui-widget-content .ui-state-hover {background: #202020 !important; border: 1px solid #999999 !important;}

.napony img {filter: grayscale(70%); -webkit-filter: grayscale(70%); transition: 0.5s;}

.napony img:hover {filter: grayscale(0%); -webkit-filter: grayscale(0%);}

.comment .comment-info li.comment-hide a,
.reply-link,
.link-dotted,
.blog-mini .fl-r a,
.edit,
.save,
.cancel,
.favourite,
.delete {
	color: #969696 !important;
	opacity: 0.5;
} 

.comment-info li a.link-dotted,
.favourite:hover,
.link-dotted,
a.q-link,
.block .nav.nav-pills a {
	border-bottom: none !important;
}

a.q-link:hover, .block .nav.nav-pills a:hover {border-bottom: none !important;}

textarea:focus, select:focus, .button:focus {outline: none;}

.loader {
	-webkit-animation: loader_c 0.5s infinite alternate;
	animation: loader_c 0.5s infinite alternate;
	background-image: none !important;
}

@keyframes loader_c {
	0% {color: #969696;}
	100% {color: #666666;}
}

@-webkit-keyframes loader_c {
	0% {color: #969696;}
	100% {color: #666666;}
}

.profile-page-header {background: #202020 !important; border-top: none !important;}

.comment-content:active .text img, .topic:active .text img {background: #dadada;}

.talk-search .talk-search-content {border-top: none;}

.talk-search.opened header {border-bottom: none;}

.ls-user-role-user #footer .nya-foot {
    float: right;
    position: fixed;
    right: 50px;
	bottom: 0px;
	background: url(http://i.imgur.com/u922I9k.png) no-repeat;
	width: 99px;
	height: 150px;
	padding-right: 99px;
	z-index: 10;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
}

.comment .comment-avatar {left: 0 !important; border-radius: 50%;}

#widemode {
	border: none !important;
	bottom: 10px;
	right: 10px;
	border-radius: 10px;
	width: 25px;
	height: 25px;
	overflow: hidden;
	text-align: center;
	color: #202020;
	background: #202020 !important;
	padding: 7px 0px 10px 10px;
	box-shadow: 0 0 5px black;
	transition: box-shadow, background, border-radius 0.5s;
}

#widemode a {color: inherit !important;}

#widemode:hover {width: inherit; height: inherit; color: #aaaaaa; border-radius: 0; overflow: inherit; text-align: inherit; box-shadow: none;}

span.blue {color: #6b6be4;}

span.red {color: #e4b6b6;}

span.green {color: #b6e4b6;}

img[src="http://files.everypony.ru/tabun/pinkamena.png"] {
	display: block;
	position: fixed;
	float: right;
	left: inherit !important;
	right: 0px;
	background: url(http://i.imgur.com/0ZdvFFF.png) no-repeat;
	background-size: cover;
	width: 1037px !important;
	height: 95vh !important;
	padding-left: 1037px;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	opacity: 1;
}

#header .dropdown-user .avatar, .table.table-users .cell-name img, .user-avatar img, .topic-info .avatar, .stream-list li.stream-item .avatar, .user-list-mini li img, .blog .blog-header .avatar {
	border-radius: 50%;
	transition: 0s !important;
}

.profile-info-about .avatar img {border-radius: inherit;}

.comment-info li.vote .vote-down {
	width: 0 !important;
	height: 0 !important;
	background-image: none !important;
	border-top: 15px solid #A30000;
	border-left: 0px solid transparent;
	border-right: 10px solid transparent;
}

.comment-info li.vote .vote-up {
	width: 0 !important;
	height: 0 !important;
	background-image: none !important;
	border-bottom: 15px solid #00A300;
	border-left: 10px solid transparent;
	border-right: 0px solid transparent;
	margin-right: 4px;
}

.vote-profile .vote-up, .topic-info-vote .vote-up {
	width: 0 !important;
	height: 0 !important;
	border-bottom: 15px solid #00A300;
	border-left: 10px solid transparent;
	border-right: 0px solid transparent;
	margin-right: 4px;
	margin-top: 5px;
}

.vote-profile .vote-down, .topic-info-vote .vote-down {
	width: 0 !important;
	height: 0 !important;
	border-top: 15px solid #A30000;
	border-left: 0px solid transparent;
	border-right: 10px solid transparent;
	margin-top: 5px;
}

.vote-down, .vote-up {opacity: 0.2;}

.icon-synio-link {
	width: 14px;
	height: 14px;
	background-position: 0;
	background: url(data:image/svg+xml;base64,) no-repeat;
}


}
