css:
@-moz-document
	 url-prefix('http://demotivators.to/'),
	 url-prefix('https://demotivators.to/'){


html, body {
		background: url("https://i.imgur.com/o5MkvuY.png") fixed right bottom no-repeat !important;
}
a:hover, #adminbar ul.menu ul li a:hover, .rmenu a:hover {
	color: #717171 !important;
}
.navbar-inner {
min-height: 40px;
padding-left: 20px;
padding-right: 20px;
background-color: #FFFFFF;
background-image: -moz-linear-gradient(top,#333,#000);
background-image: -webkit-gradient(linear,0 0,0 100%,from(#FFF),to(#FFF));
background-image: -webkit-linear-gradient(top,#FFF,#FFF);
background-image: -o-linear-gradient(top,#333,#000);
background-image: linear-gradient(to bottom,#FFF,#FFF);
background-repeat: repeat-x;
border: 1px solid #FFF;
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
-webkit-box-shadow: 0 1px 4px rgba(0,0,0,0.065);
-moz-box-shadow: 0 1px 4px rgba(0,0,0,0.065);
box-shadow: 0 1px 4px rgba(0,0,0,0.065);
}
.navbar .navbar-search .search-query {
padding-left: 29px;
background-image: url('https://i.imgur.com/18NVChY.png');
background-repeat: no-repeat;
background-position: 12px 8px;
color: #000000;
text-shadow: none;
background-color: #FFF;
border-color: #FFFFFF;
width: auto;
}
.navbar .nav>.active>a, .navbar .nav>.active>a:hover, 
.navbar .nav>.active>a:focus {
color: #ADADAD;
text-decoration: none;
background-color: #FFF;
-webkit-box-shadow: inset 0 3px 8px rgba(255, 255, 255, 0.125);
-moz-box-shadow: inset 0 3px 8px rgba(0,0,0,0.125);
box-shadow: inset 0 3px 8px rgba(255, 255, 255, 0.125);
}
.navbar .nav>li>a {
float: none;
padding: 10px 15px 10px;
color: #adadad;
text-decoration: none;
text-shadow: 0 1px 0 #EAEAEA;
}
.navbar .brand {
float: left;
display: block;
padding: 10px 20px 10px;
margin-left: -20px;
font-size: 20px;
font-weight: 200;
color: #777;
text-shadow: 0 1px 0 #FFF;
}
.navbar .brand:first-letter {
background-color: #fff;
padding: 1px 6px;
margin: 2px;
border: 2px solid #b3b3b3;
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
}
.content {
color: #949494;
background-color: rgba(255, 255, 255, 0);
padding: 10px 20px 20px;
margin-bottom: 20px;
}
.content h1, .content h2, .content h3, .content h4 {
font-family: 'Russo One',sans-serif;
text-transform: uppercase;
color: #909090;
margin: 0;
}
.pagination ul>li>a, .pagination ul>li>span {
float: left;
padding: 4px 12px;
line-height: 20px;
text-decoration: none;
background-color: #FFF;
border: 1px solid #FF0000;
border-left-width: 0;
}
.poster-info {
background-color: #FFFFFF;
-webkit-border-radius: 10px;
-moz-border-radius: 10px;
border-radius: 10px;
padding: 10px 10px;
margin-bottom: 10px;
color: #333;
}
#rtc #conversation {
background-color: rgba(234, 234, 234, 0.51);
padding: 5px 20px;
text-align: left;
font-family: Verdana,Arial,Helvetica;
}
#rtc #conversation .comment {
margin: 4px 0;
padding: 2px;
overflow: hidden;
background-color: rgba(255, 255, 255, 0.64);
-webkit-border-radius: 4px;
-moz-border-radius: 4px;
border-radius: 4px;
}
#rtc #replyForm #data {
height: 60px;
background-color: #FFFFFF;
color: #000;
width: 320px;
}
#main .label {
background-color: #FFF;
color: #000;
}
.nav-tabs.nav-stacked li>a {
border: 1px solid #FFF;
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}
  .nav-tabs.nav-stacked li>a {
background-color: #FFFFFF;
color: #777;
}
#rtc #conversation .comment .crating {
color: #000000;
padding: 2px 5px;
float: right;
font: bold 14px Verdana;
cursor: pointer;
}
.nav-tabs.nav-stacked li>a:hover, .nav-tabs.nav-stacked li.active>a, .nav-tabs.nav-stacked li.active>a:hover {
border: 1px solid #fff;
color: #e6e6e6;
background-color: #DDD;
}
#rtc #conversation .comment .crating:hover {
color: #1EBE09;
}
#rtc #conversation .comment .crating.positive {
color: #1EBE09;
}
.btn {
-webkit-box-shadow: 1px 1px 2px #FFF;
-moz-box-shadow: 1px 1px 2px #111;
box-shadow: 1px 1px 2px #FFF;
color: #fff;
text-shadow: 0 -1px 0 rgba(0,0,0,0.25);
background-color: #FFFFFF;
background-image: -moz-linear-gradient(top,#333,#191919);
background-image: -webkit-gradient(linear,0 0,0 100%,from(#FFF),to(#FFFFFF));
background-image: -webkit-linear-gradient(top,#FFF,#FFFFFF);
background-image: -o-linear-gradient(top,#333,#191919);
background-image: linear-gradient(to bottom,#FFFFFF,#FFFFFF);
background-repeat: repeat-x;
border-color: #FFFFFF #FFFFFF #FFF;
border-color: rgba(0,0,0,0.1) rgba(0,0,0,0.1) rgba(0,0,0,0.25);
color: #8B8B8B;
text-shadow: none;
font-weight: bold;
}
.btn:hover {
-webkit-box-shadow: 1px 1px 2px #FFF;
-moz-box-shadow: 1px 1px 2px #111;
box-shadow: 1px 1px 2px #FFF;
color: #fff;
text-shadow: 0 -1px 0 rgba(0,0,0,0.25);
background-color: #FFFFFF;
background-image: -moz-linear-gradient(top,#333,#191919);
background-image: -webkit-gradient(linear,0 0,0 100%,from(#FFF),to(#FFFFFF));
background-image: -webkit-linear-gradient(top,#FFF,#FFFFFF);
background-image: -o-linear-gradient(top,#333,#191919);
background-image: linear-gradient(to bottom,#FFFFFF,#FFFFFF);
background-repeat: repeat-x;
border-color: #FFFFFF #FFFFFF #FFF;
border-color: rgba(0,0,0,0.1) rgba(0,0,0,0.1) rgba(0,0,0,0.25);
color: #8B8B8B;
text-shadow: none;
font-weight: bold;
}
.form-actions {
border-top: 1px solid #FFF;
background-color: #FFFFFF;
text-align: center;
}  
select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
background-color: #FFFFFF;
color: #999;
}
.nav-tabs>.active>a, .nav-tabs>.active>a:hover {
color: #9B9B9B;
background-color: #FFFFFF;
border: 1px solid #FFFFFF;
border-bottom-color: transparent;
cursor: default;
}
.nav-tabs>li>a {
background-color: #FFFFFF;
color: #A1A1A1;
text-transform: uppercase;
}
#main .label {
background-color: #F0F0F0;
color: #5A5A5A;
}
.uneditable-input {
background-color: #0d0d0d;
border: 1px solid #F00;
-webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
-moz-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
-webkit-transition: border linear .2s,box-shadow linear .2s;
-moz-transition: border linear .2s,box-shadow linear .2s;
-o-transition: border linear .2s,box-shadow linear .2s;
transition: border linear .2s,box-shadow linear .2s;
}
#rtc {
color: #000000;
}
.controls li:hover, .mod-controls li:hover {
color: #626262;
}
::selection {
background: #ccc;
color: #333;
}
}
