/********************************************/
/************** ADK Dark Qwant **************/
/***** Created By: Amergan Douar Koadeg *****/
/********************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("qwant.com")

{

/******************* MAIN ********************/

div#body,div#wrapper.wrapper>div.card.centered {
background-color: #262626 !important;
}

div#body>div#home-wrapper.container,div#body>div#wrapper.wrapper,div#body>#push {
background-color: #262626 !important;
color: #999999 !important;
}

body#search.locale_fr_fr>div#body,body#multi.locale_fr_fr>div#body,body#media.locale_fr_fr>div#body,body#people.locale_fr_fr>div#body,.searchQ.left.qtypeahead.tt-input,.trendsTitle,.btnClassic,.btnSingle,.btnMosaic {
background-color: #484848 !important;
}

.tt-dropdown-menu,.tt-suggestion.tt-cursor {
background-color: #484848 !important;
border-color: #7B7B7B !important;
}

.btnClassic.selected,.btnSingle.selected,.btnMosaic.selected,input.btnSubmit.left {
background-color: #262626 !important;
}

.searchQ.left.qtypeahead.tt-input,.btnSubmit,.btnClassic,.btnSingle,.btnMosaic,.trendsTitle {
border-color: #7B7B7B !important;
}

a._search_query.menu_classique.selected {
background-color: #262626 !important;
}

footer#footer_2,header,#settings.small_block {
background-color: #000000 !important;
}

div.top_right>a.loginBtn,.trendsHeader {
background: transparent !important;
border: none !important;
}

a:link, a:active, a:visited {
color: #0771A6 !important;
}

a:hover,a.trendsTitle>span {
color: lightgrey !important;
}

/*************** RESULTS PAGE ****************/

#topMenu.topMenu.affix,#topMenu.topMenu.affix-top {
background-color: #262626 !important;
color: #999999 !important;
border: none !important;
}

.previewItem.image,.previewItem.video,div.header.web_border,#detailsModal.popup_search.web,div.header.news_border,#detailsModal.popup_search.news,div.header.social_border,#detailsModal.popup_search.social,div.header.shopping_border,#detailsModal.popup_search.shopping {
background-color: #262626 !important;
color: #999999 !important;
border-color: #3e3e3e !important;
}

.resultspage.boxshadow,.item,.itemContent,.results,div.results>div>div>p.entete,.col2.knowledge.boxshadow,div.isoitem.tcol1.shopping.boxshadow.isotope-item>h3,div.isoitem.tcol1.social.boxshadow.isotope-item>h3,div.isoitem.tcol1.web.boxshadow.isotope-item>h3,div.isoitem.tcol1.news.boxshadow.isotope-item>h3,div.isoitem.tcol1.media.boxshadow.isotope-item>h3,div.isoitem.tcol1.wiki.knowledge.isotope-item>div.inner,div.knowledge,input#btn-more {
background-color: #262626 !important;
}

#mediaPreviewWrapper,.clearfix.resultspage,div#wrapper.wrapper.single.web,div.page_number,div#left,div#left>ul.affix,div#left>ul.affix-top>li.images>a,div#left>ul.affix-top>li.videos>a,div#left>ul.affix-top>li.news>a,div#left>ul.affix-top>li.social>a,div#left>ul.affix-top>li.shopping>a,div#left>ul.affix-top>li.web>a,div#body>div#wrapper.wrapper.single.news,div#body>div#wrapper.wrapper.single.social,div#body>div#wrapper.wrapper.single.shopping,div.labelCommercial-single,div#body>div#wrapper.wrapper.single.images,div#wrapper.wrapper.single.images>div#search,div#body>div#wrapper.wrapper.single.videos,div.left_menu_bg,div#mosaicSearch.affix-top,div#mosaicSearch.affix,#search,div#tumblelog.layout-mozaic.isotope,div#tumblelogWrapper,div#body>div#wrapper.wrapper.single.web,div#wrapper.wrapper.clearfix>div#more-container,div#body>div#wrapper.wrapper.clearfix {
background-color: #484848 !important;
}

#domain-web.result-domain.boxshadow,#domain-news.result-domain.boxshadow,#domain-qnowledge.result-domain,#domain-social.result-domain.boxshadow,#domain-shopping.result-domain.boxshadow,.itemBottom {
background-color: #111111 !important;
}

input#qMosaicDomainWeb,input#qMosaicDomainChrono,input#qMosaicDomainSocial,input#qMosaicDomainShopping,input#qMosaicDomainMedia {
background-color: #B4B4B4 !important;
}

div#left>ul.affix-top>li.web>a.selected,div#left>ul.affix>li.web>a.selected,div#left>ul.affix-top>li.images>a.selected,div#left>ul.affix-top>li.videos>a.selected,div#left>ul.affix-top>li.news>a.selected,div#left>ul.affix-top>li.social>a.selected,div#left>ul.affix-top>li.shopping>a.selected,div#left>ul.affix-top>li.web>a.selected,div#left>ul.affix>li.web>a.selected,div#left>ul.affix>li.images>a.selected,div#left>ul.affix>li.videos>a.selected,div#left>ul.affix>li.news>a.selected {
background-color: #484848 !important;
color: #FFFFFF !important;
font-weight: bold !important;
}

div#left>ul.affix>li.social>a.selected,div#left>ul.affix>li.shopping>a.selected,div#domain-all.mosaicDomain>h2#h2All:hover,div#domain-web.mosaicDomain>h2#h2Web:hover,div#domain-news.mosaicDomain>h2:hover,div#domain-social.mosaicDomain>h2:hover,div#domain-shopping.mosaicDomain>h2:hover,div#domain-media.mosaicDomain>h2:hover {
background-color: transparent !important;
color: #999999 !important;
}

input#qMosaicDomainWeb,input#qMosaicDomainChrono,input#qMosaicDomainSocial,input#qMosaicDomainShopping,input#qMosaicDomainMedia {
color: #505050 !important;
}

#domain-web.result-domain.boxshadow>h2,#domain-news.result-domain.boxshadow>h2,#domain-qnowledge.result-domain>h2,#domain-social.result-domain.boxshadow>h2,#domain-shopping.result-domain.boxshadow>h2 {
color: #999999 !important;
}

.item {
border: none !important;
}

/**************** MEDIA PAGE *****************/

div#resultsMediaGrid.isotope {
background-color: #484848 !important;
}

a._search_query.menu_media.selected {
background-color: #262626 !important;
}

div.isoitem.tcol1.boxshadow.isotope-item>div,div.isoitem.tcol1.boxshadow.isotope-item>div.desc {
background-color: #262626 !important;
}

div.isoitem.tcol1.boxshadow.isotope-item>div.itemBottom {
background-color: #111111 !important;
border: none !important;
}

/**************** PEOPLE PAGE *****************/

div#resultsPeopleGrid.layout-mozaic.isotope {
background-color: #484848 !important;
}

a._search_query.menu_people.selected {
background-color: #262626 !important;
}

div.isoitem.people.tcol1.boxshadow.isotope-item>h3 {
background-color: #262626 !important;
}

/**************** BOARDS PAGE *****************/

a.selected.menu_boards {
background-color: #262626 !important;
}

body#body_explorer.locale_fr_fr>div#body>div#wrapper.wrapper>div#boards_search,input#q.searchQ.left,select#boards_search_filter,ul#list_boards,ul#list_categories {
background-color: #484848 !important;
}

a.board_link,a.userlink,div.creator,div.block_follow,.board {
background-color: #262626 !important;
}

div.stats,li.category>.active {
background-color: transparent !important;
color: #FFFFFF !important;
font-weight: bold !important;
}

input#q.searchQ.left,select#boards_search_filter {
border-color: #7B7B7B !important;
}

.board,div.stats {
border: none !important;
}

li.category:hover * {
background-color: transparent !important;
color: #999999 !important;
}

/**************** VIDEO PLAY PAGE *****************/

div#detailsModal.popup_search.videos>div.header.media_border,div.modal_content_popup.__modal_close>div#detailsModal.popup_search.videos,div#detailsModal.popup_search.images>div.header.media_border,div.modal_content_popup.__modal_close>div#detailsModal.popup_search.images {
background-color: #262626 !important;
border: 1px solid #000000 !important;
}

div#detailsSlideshow>div.inner {
background-color: #484848 !important; opacity: .8 !important;
}

div.modal_content.__modal_close {
background-color: rgba(38,38,38,.8)
/*background-color: #262626 !important; opacity: .8 !important;*/
}

#detailsSlideshow {
background-color: #484848 !important;
border: none !important;
}

.btn-board {
background-color: #484848 !important;
border: none !important;
}

}
