css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("02ch.in") {
html, body {
    background-color: #212121 !important;
    color: #698CC0 !important;
}

input[type="submit"],input[type="reset"],input[type="button"] {
    -o-appearance: none !important;
    -webkit-appearance: none !important;
    -moz-appearance: none !important;
    background-color: #333333 !important;
    background-image: -moz-linear-gradient(center top,#4A4A4A,#292929,#4A4A4A) !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    border-bottom: 1px solid #151515 !important;
    border-top: 1px solid #151515 !important;
    border-left: 1px solid #000000 !important;
    border-right: 1px solid #000000 !important;
    color: #AAAAAA !important;
    font-weight: bold !important;
}

input[type="submit"]:hover,input[type="reset"]:hover,input[type="button"]:hover {
    background-color: #212121 !important;
    background-image: -moz-linear-gradient(center top,#151515,#505050,#151515) !important;
    border: 1px solid #000066 !important;
    color: #CCCCFF !important;
    box-shadow: inset 0 0 4px 1px #48B0FD !important;
}

input[type="password"],select[name=embedtype],input[type="file"],input[type="image"] {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    color: #BBBBBB !important;
    border: 2px solid #545454 !important;
    background-color: #111111 !important;
}

input[type="file"],input[type="image"] {
    border-radius: 5px !important;
}

input[type="text"] {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background-color: #111111 !important;
    border: 2px solid #545454 !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    color: #698cc0;
}

input:hover[type="text"],input:focus[type="text"] {
    background-color: #151515 !important;
    color: #AAAAFF !important;
}

input::-moz-focus-inner {
    border: 0 !important;
    padding: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
}

input[type="button"],input[type="submit"] {
    padding: 3px 6px !important;
}

textarea,select,.mselect {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background-color: #111111 !important;
    color: #CCCCCC !important;
    border: 2px solid #545454 !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
}

textarea:hover,textarea:focus,select:hover,select:focus {
    background-color: #151515 !important;
    color: #EEEEEE !important;
}

hr {
    height: 3px !important;
    border-width: 0px !important;
    background: url('https://02ch.in/css/line.png') center no-repeat !important;
    background-size: 100% !important;
}

a[target=_blank] img {
    padding: 2px !important;
}


a,.a-link-emulator,span.spoiler:hover a {
    color: #C9BE89 !important;
}

a:hover,.a-link-emulator:hover {
    color: #EEFEBB !important;
}

.error {
 color: inherit !important;
}

.post_head, .thread_head {
    background: #3c3c3c !important;
    font-size: 1em !important;
    color: #698cc0 !important;
    font-weight: bold !important;
}
   
.post {
    /* min-width: 24.4em !important; */
    background: url('https://02ch.in/css/lightning.png') right bottom no-repeat !important;
    background-color: #2C2C2C !important;
    color: #698CC0 !important;
    border-right: solid 1px #3c3c3c !important;
    border-bottom: solid 1px #3c3c3c !important;
}

.post_head {
      background: inherit !important;
   background-image: none !important;
}
.thread_head {
background: #3c3c3c !important;
}
.file {
        background: #272727 !important;
}

.postername {
    color: #B4B9CD !important;
    font-weight: bold !important;
}

.subject {
  color: #4C54C6;
}

.file-attr a {
    color: #789922 !important;
}

.unkfunc {
    background: inherit !important;
    color: #789922 !important;
}

.reply.hiclass {
    background: #111110 !important;
}

#postTableBody .postblock { background: none !important; }

span.spoiler,span[onmouseout*="color='black'"],span.spoiler a {
    background: #575757 !important;
    color: #575757 !important;
}

span.spoiler:hover,span[onmouseover*="color='white'"]:hover {
    color: #48B0FD !important;
}
    
nav .pagelist {
    background-color: #2C2C2C !important;
    color: #698CC0 !important;
   border-right: solid 1px #575757 !important;
   border-bottom: solid 1px #575757 !important;
}

ul.menu li a, ul.menu_bottom li a {
    background-color: #2C2C2C !important;
    color: #698CC0 !important;
   /* border-top: solid 1px #575757 !important; */
}


ul.menu li a {
    border-right: 1px solid #575757;
    border-bottom: 1px solid #575757;
    border-left: 1px solid #575757;
}

ul.menu_bottom li a {
    border-right: 1px solid #575757;
    border-top: 1px solid #575757;
    border-left: 1px solid #575757;
}

ul.menu li a:hover, ul.menu_bottom li a:hover {
    box-shadow: 1px 1px 2px #698CC0;
}
    

.boardname {
    color: #f60 !important;
}

.filename a {
    color: inherit !important;
}

#preview {
    box-shadow: 1px 2px 2px #698CC0 !important;
    border: 1px solid #3c3c3c;
}

.highlight .post {
    background-color: #414141 !important;
}

.highlight .post .file {
    background-color: #3c3c3c !important;
}

}
