css:
@-webkit-document domain(duolingo.com), url-prefix(http://www.duolingo.com) {

/*  Make the body/background dark */

body {
  background-color: #3D3D3D !important;
  background: none;
}

/*  Make the content background dark */
main:not(.full-width) .page-main.main-right, .box-colored.white {
   background-color: #282828 !important;
}
.bonus-row {
    background-color: #282828;
}


/*  Remove rounded edges */
main:not(.full-width) .page-main, .box-colored {
  -webkit-border-radius: 0px;
  border-radius: 0px;
}


/*  Make text white */
.skill-badge-name {
  color: #eee !important;
}
h1 {
  color: #eee;
}
  
/* Hide social buttons */ 
.box-gray, .box-social-buttons {
  display: none;      
}
  

}
