css:
@-moz-document domain("oc.tc") {
body {
	background-color: rgb(34, 34, 34);
}

h4, h3, h2, h1, h6, label, .small, small {
	color: rgb(136, 170, 221);
}

.table-striped > tbody > tr:nth-child(2n+1) > td, .table-striped > tbody > tr:nth-child(2n+1) > th {
	background-color: #1f1f1f;
}

table > thead > tr {
	color: rgb(136, 170, 221);
}

table > tbody > tr > td:nth-child(n+2) {
	color: rgb(136, 170, 221);
}


#forum-sidebar > ul > li > a {
	text-shadow: none;
}

.marketing img {
	display: none;
}
h4 {
	border-bottom: 1px solid #666660 !important;
}

.table td {
	border-top: 1px solid #666660 !important;
}

.page-header {
	border-bottom: 1px solid #666660 !important;
}

.navbar-inner {
	background-image: none;
}

.nav li a {
	color: rgb(136, 170, 221) !important;
}

.brand {
	color: rgb(136, 170, 221) !important;
	text-shadow: none !important;
}

.bs-docs-social {
	background-color: #666660;
	border-bottom-color: #666660;
	border-top-color: #666660;
}

.nav > li > a {
	text-shadow: none !important;
}

.nav-collapse > .nav > .active > a {
	background-color: #2b2b2b;
}

.post-content {
	color: rgb(204, 204, 204);
}

pre {
	background-color: #2b2b2b;
	color: rgb(204, 204, 204); 
}

.dropdown-menu {
	background-color: rgb(204, 204, 204);
	color: #666660;
}

blockquote {
	border-left: 5px solid rgb(204, 204, 204) !important;
}

hr.post-divider {
	border-top-color: rgb(204, 204, 204) !important;
	border-top: 1px solid rgb(204, 204, 204) !important;
	border-bottom: 1px solid rgb(204, 204, 204) !important;
}

.nav-tabs > .active > a {
	background-color: rgb(34, 34, 34);
}


.nav-tabs > .active > a:hover {
	background-color: #2b2b2b;
}

footer > ul > li > a:hover {
	background-color: #2b2b2b !important;
}

.nav-collapse > .nav > li > a:focus, .nav-collapse > .nav > li > a:hover {
	background-color: #2b2b2b !important;
}

blockquote p {
	color: rgb(204, 204, 204);
}

p {
	color: rgb(204, 204, 204);
	
}

ul > li {
	color: rgb(204, 204, 204);
	
}

.row > .span6 > span, .row > .span12 > span {
	color: rgb(204, 204, 204);
	
}

.dropdown-menu>li>a {
	color: black !important;
}

.span7 > .thumbnail  {
	border: none !important;
}

#pvp-encounters > .row > .span6 > p, #objectives > .row > .span4 > p {
	color: rgb(204, 204, 204); 
}

.row > .span12 > ol > li, .row > .span12 > ol > ol > li {
	color: rgb(204, 204, 204);
}

.navbar-inner { background-color: #222222; border-color: #1f1f1f; }
.navbar-inner .navbar-brand { color: #323232; }
.navbar-inner .navbar-brand:hover, .navbar-inner .navbar-brand:focus { color: #ffffff; background-color: transparent; }
.navbar-inner .navbar-text { color: #323232; }
.navbar-inner .navbar-nav > li > a { color: purple; }
.navbar-inner .navbar-nav > li > a:hover, .navbar-inner .navbar-nav > li > a:focus { color: orange; background-color: transparent; }
.navbar-inner .navbar-nav > .active > a, .navbar-inner .navbar-nav > .active > a:hover, .navbar-inner .navbar-nav > .active > a:focus { color: #ffffff; background-color: #1f1f1f; }
.navbar-inner .navbar-nav > .disabled > a, .navbar-inner .navbar-nav > .disabled > a:hover, .navbar-inner .navbar-nav > .disabled > a:focus { color: #262626; background-color: transparent; }
.navbar-inner .navbar-toggle { border-color: #242424; }
.navbar-inner .navbar-toggle:hover, .navbar-inner .navbar-toggle:focus { background-color: #242424; }
.navbar-inner .navbar-toggle .icon-bar { background-color: #ffffff; }
.navbar-inner .navbar-collapse, .navbar-inner .navbar-form { border-color: #202020; }
.navbar-inner .navbar-nav > .open > a, .navbar-inner .navbar-nav > .open > a:hover, .navbar-inner .navbar-nav > .open > a:focus { color: #ffffff; background-color: #1f1f1f; }
.navbar-inner .navbar-nav > .dropdown > a:hover .caret { border-top-color: #ffffff; border-bottom-color: #ffffff; }
.navbar-inner .navbar-nav > .dropdown > a .caret { border-top-color: #323232; border-bottom-color: #323232; }
.navbar-inner .navbar-nav > .open > a .caret, .navbar-inner .navbar-nav > .open > a:hover .caret, .navbar-inner .navbar-nav > .open > a:focus .caret { border-top-color: #ffffff; border-bottom-color: #ffffff; }
.navbar-inner .navbar-link { color: #323232; }
.navbar-inner .navbar-link:hover { color: #ffffff; }


.btn.active {
	color: rgba(255, 255, 255, 0.75);
}
.btn {
	color: #ffffff;
	text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
	background-color: #151616;
	background-image: -moz-linear-gradient(top, #1f2021, #060607);
	background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#1f2021), to(#060607));
	background-image: -webkit-linear-gradient(top, #1f2021, #060607);
	background-image: -o-linear-gradient(top, #1f2021, #060607);
	background-image: linear-gradient(to bottom, #1f2021, #060607);
	background-repeat: repeat-x;
	border-color: #060607 #060607 #000000;
	border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
	*background-color: #060607;
}
.btn:hover,
.btn:focus,
.btn:active,
.btn.active,
.btn.disabled,
.btn[disabled] {
	color: #ffffff;
	background-color: #060607;
	*background-color: #000000;
}
.btn:active,
.btn.active {
	background-color: #000000 ;
}

}
