css:
@-moz-document domain("forums.nexusmods.com") {
body {
  	color: #5a5a5a;
  	font: normal 13px helvetica, arial, sans-serif;
}

::selection {
  background: #757575;
}
a:hover {
	color: #328586;
}
a, .bbc_url, .bbc_email {
    color: #225985;
    text-decoration: none;
}

.content {
    background: #FFF url('http://forums.nexusmods.com/public/style_images/underground/_custom/bg-wrapper.png') repeat-x top left;
}

#branding {
	background: #225985 url('http://forums.nexusmods.com/public/style_images/underground/branding_bg.png') repeat-x;
}

#user_bar {
  	background: #225985;
	border-top: 1px solid rgba(255,255,255,0.1);
}

/* main comments containers */
.removeDefault .ipsBox {
    background: #ebf0f3;
}

.border {
    background: #ebf0f3;
    border: 9px solid #ebf0f3;
}

/* Post Title */
.sb_titlebox {
    background: none;
    border: none;
    color: #FFF;
    text-shadow: none;
}
.sb_titlebox .desc {
	color: #000;
}   
.ipsType_pagetitle {
    color: #000;
}

/* 
===================================================
==== Buttons Override =============================
===================================================
*/
ul.post_controls a, .ipsButton_secondary, .bbc_spoiler_show, .input_submit, .input_submit.alt, .user_controls li a, ipsButton {
    height: 22px;
    line-height: 22px;
    font-size: 12px;
    padding: 0 10px;
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f6f6f6), color-stop(100%,#e5e5e5));
    border: 1px solid #dbdbdb;
    -moz-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    -webkit-box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    box-shadow: 0px 1px 0px rgba(255,255,255,1) inset, 0px 1px 0px rgba(0,0,0,0.3);
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    color: #616161;
    display: inline-block;
    white-space: nowrap;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    text-shadow: none;
}


/* 
===================================================
==== Input Box ====================================
===================================================
*/
.input_text, .ipsTagBox_wrapper, textarea, #recaptcha_response_field {
    padding: 4px;
    border-width: 1px;
    border-style: solid;
    border-color: #848484 #c1c1c1 #e1e1e1 #c1c1c1;
    background: #fff;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
	text-shadow: none;
}




/* 
===================================================
==== Navigation Breadcrumb ========================
===================================================
*/
#secondary_navigation, #secondary_navigation #breadcrumb li span, #secondary_navigation #breadcrumb li a {
  	background: none;
  	border: none;
  	font-size: 13px;
  	text-shadow: none;
}
#secondary_navigation #breadcrumb li:nth-child(n+1):after {
	content: '>';
  	position:relative;
  	top: -37px;
  	left: -20px;
  	color: #636262;
}
#secondary_navigation a:hover {
	color: #9B9898;
}



/* 
===================================================
==== Main Post Edits ==============================
===================================================
*/
.maintitle {
  	background: #2c5687 url(http://forums.nexusmods.com/public/style_images/white/maintitle.png) repeat-x top;
}

/* comment background */
.ipsBox_container {
    background: #FFF;
}
.removeDefault .ipsBox_container {
    border: 1px solid #dbe4ef;
}
/* post header and user name */
.post_block {
  	background: none;
}
.post_block h3 {
    background: #D8DDE8 url('http://forums.nexusmods.com/public/style_images/underground/_custom/bg-postblock.png') repeat-x 0 0;
  	border-top: none;
}
.post_block h3 a {
   	color: #777777;
}

/* Post Controls */
ul.post_controls {
	background: none;
}    

/* quote */
p.citation {
    font-size: 12px;
    padding: 8px 10px;
    border-left: 2px solid #989898;
    /* background: #f3f3f3; */
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f6f6f6), color-stop(100%,#e5e5e5));
    border-top: 2px solid #e5e5e5;
    border-right: 2px solid #e5e5e5;
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
    font-weight: bold;
    overflow-x: auto;
  	text-shadow: none;
  	color: #000;
  	border-bottom: none;
}
/* go to quoted text */
.snapback {
    content: url('http://forums.nexusmods.com/public/style_images/white/snapback.png') repeat 0 0;
}
/* quote body */
blockquote.ipsBlockquote.built {
    font-size: 12px;
    padding: 10px;
    border: 2px solid #e5e5e5;
    border-left: 2px solid #989898;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    background: #f7f7f7;
    margin: 0 0;
    overflow-x: auto;
}

/* edited */
.edit {
    background: url('http://forums.nexusmods.com/public/style_images/underground/comment_edit.png') no-repeat 6px 10px;
    border: none;
    color: #000;
    text-shadow: none;
}

/* signature */
.signature {
	border-top: 1px solid #d5d5d5;
}



/* 
===================================================
==== Post Editor ==================================
===================================================
*/
.cke_skin_ips .cke_wrapper {
    padding: 0px 5px 0px 3px !important;
    border: 2px solid #D5DDE5 !important;
    background-color: #E4EBF2 !important;
    background-image: none !important;
}
.cke_skin_ips .cke_toolbox {
    background-color: #d8dde8;
} 
.cke_skin_ips .cke_browser_webkit .cke_toolbar {
    background-color: #E4EBF2 !important;
}
.cke_button_bold .cke_icon, .cke_button_italic .cke_icon, .cke_button_underline .cke_icon, .cke_button_strike .cke_icon, .cke_button_subscript .cke_icon, .cke_button_superscript .cke_icon, .cke_button_bulletedlist .cke_icon, .cke_button_numberedlist .cke_icon, .cke_button_link .cke_icon, .cke_button_unlink .cke_icon, .cke_button_ipscode .cke_icon, .cke_button_ipsquote .cke_icon, .cke_button_outdent .cke_icon, .cke_button_indent .cke_icon, .cke_button_justifyleft .cke_icon, .cke_button_justifycenter .cke_icon, .cke_button_justifyright .cke_icon, .cke_button_ipsmedia .cke_icon, .cke_button_textcolor .cke_icon, .cke_button_ipsemoticon .cke_icon, .cke_button_ipsbbcode_twitter .cke_icon, .cke_button_image .cke_icon {
  
}


/* Page information */
#topic_stats {
	font-size: 12px;
    padding: 8px 10px;
    border-left: 2px solid #989898;
    /* background: #f3f3f3; */
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f6f6f6), color-stop(100%,#e5e5e5));
    border-top: 2px solid #e5e5e5;
    border-right: 2px solid #e5e5e5;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    font-weight: bold;
    overflow-x: auto;
  	text-shadow: none;
  	color: #000;
  	border-bottom: none;
}
.statistics {
    margin: 20px 0 0 0;
    padding: 10px 0;
    border-top: 3px solid #d8d8d8;
    line-height: 1.3;
    overflow: hidden;
  	border-radius: none;
    -moz-border-radius: none;
    -webkit-border-radius: none;
	font-size: 12px;
}
.statistics_head {
    color: #000;
}
.statistics_head a {
    color: #225985;
}
.statistics_head h4 {	/* title */
    font: normal 14px helvetica, arial, sans-serif;
    color: #204066;
    padding: 5px 10px;
    background: #DBE2EC;
    margin: -7px -7px 10px -7px;
  	text-shadow: none;
}

/* User Image in posts */
.ipsUserPhoto {
    background: none;
}
  
h3.maintitle {
    font-size: 1.4em;
    font-weight: normal;
}
.maintitle {
background: #2c5687 url(http://forums.nexusmods.com/public/style_images/white/maintitle.png) repeat-x top;
color: #fff;
padding: 10px 10px 11px;
font-size: 16px;
font-weight: 300;
-moz-border-radius: 4px 4px 0 0;
-webkit-border-top-left-radius: 4px;
-webkit-border-top-right-radius: 4px;
border-radius: 4px 4px 0 0;
-webkit-box-shadow: inset 0px 1px 0 #528cbc;
-moz-box-shadow: inset 0px 1px 0 #528cbc;
box-shadow: inset 0px 1px 0 #528cbc;
border-width: 1px 1px 0 1px;
border-color: #316897;
border-style: solid;
}
}

@-moz-document domain("forums.nexusmods.com") {
/* 
===================================================
==== Search Box ===================================
===================================================
*/
#main_search {
  	color: #000;
}
#search_wrap {
    position: relative;
    background: #fff;
    display: block;
    padding: 0 26px 0 4px;
    height: 26px;
    line-height: 25px;
    -moz-border-radius: 3px 4px 4px 3px;
    -webkit-border-top-left-radius: 3px;
    -webkit-border-top-right-radius: 4px;
    -webkit-border-bottom-right-radius: 4px;
    -webkit-border-bottom-left-radius: 3px;
    border-radius: 3px 4px 4px 3px;
    -webkit-box-shadow: 0px 2px 4px rgba(0,0,0,0.2);
    -moz-box-shadow: 0px 2px 4px rgba(0,0,0,0.2);
    box-shadow: 0px 2px 4px rgba(0,0,0,0.2);
    min-width: 230px;
}
/* makes search options button look like all other page buttons */
#search_options {
  	max-width: 80px;
    text-overflow: ellipsis;
    overflow: hidden;
    font-size: 10px;
    height: 20px;
    line-height: 20px;
    margin: 3px 3px 3px 0;
    padding: 0 6px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    background: #f6f6f6;
    background: -moz-linear-gradient(top, #f6f6f6 0%, #e5e5e5 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f6f6f6), color-stop(100%,#e5e5e5));
    color: #616161;
    display: inline-block;
    text-shadow: none;
    float: right;
}
#search .submit_input {
	background: #7ba60d url(http://forums.nexusmods.com/public/style_images/white/search_icon.png) no-repeat 50%;
	border: 1px solid #7ba60d;
}
#search .submit_input:hover {
	background-color: #6A8F0C;
}
#search_options_menucontent {
    background-color: #f7f9fb;
    border: 1px solid #d5dde5;
    -webkit-box-shadow: rgba(0, 0, 0, 0.3) 0px 6px 6px;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 6px 6px;
}

#search_options_menucontent li {
    border-top: none;
    border-bottom: none;
}
}

@-moz-document domain("forums.nexusmods.com") {
/*
===================================================
==== Thread/Forum View ============================
===================================================
*/
table.ipb_table td {
  	border-top: none;
	border-bottom: 1px solid #f3f3f3;
}

/* Recent Topics Sidepanel */
.ipsSideBlock h3 {	/* title */
    font: normal 14px helvetica, arial, sans-serif;
    color: #204066;
    padding: 5px 10px;
    background: #DBE2EC;
    margin: -7px -7px 10px -7px;
  	text-shadow: none;
}
.ipsSideBlock {		/* background */
    background: #F7FBFC;
    padding: 10px;
    margin-bottom: 10px;
  	border: none;
  	-webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
.ipsList_withminiphoto li { /* topic info */
    border: none;
}

/* Pagination */
.pagination .pages li.active {
	background: #7BA60D url('http://forums.nexusmods.com/public/style_images/underground/highlight.png') repeat-x 0 0;
    color: #222;
    padding: 1px 8px;
    text-shadow: none;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    cursor: default;
}
.pagination .pages a {
    display: inline-block;
    padding: 1px 8px;
    color: #999;
    background: none;
    text-shadow: none;
}
.pagination .pages a:hover {
    background: #ececec;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
  	color: #000;
  	text-shadow: none;
}
.pagination .back a, .pagination .forward a {
    display: inline-block;
    padding: 0px 6px;
    height: 20px;
    background: #eaeaea;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    text-transform: uppercase;
    color: #5a5a5a;
    font-size: 11px;
    font-weight: bold;
 	text-shadow: none;
  	font-weight: normal;
}
ul.mini_pagination li a {
  	background: none;
  	border: 1px solid #225985;
  	color: #225985;
}
ul.mini_pagination li a:hover {
    background: #7BA60D  url('http://forums.nexusmods.com/public/style_images/underground/highlight_reallyfaint.png') repeat-x 0 0;
}
.ipsBadge_orange {
	background: orange;
}

/* Load more topics */
#more_topics {
	 background: none;
 	 border-top: 1px solid #f3f3f3;
}

/* Icons next to posts for read/unread/locked, etc */
td.col_f_icon {
	background: #E2E9F0;
}
td.col_f_icon>a img[src*="http://forums.nexusmods.com/public/style_images/underground/t_unread.png"] {
  	-moz-box-sizing: border-box;
  	box-sizing: border-box;
	background: url('http://forums.nexusmods.com/public/style_images/white/t_unread.png') no-repeat !important;
  	width: 28px; /* Width of new image */
  	height: 21px; /* Height of new image */
  	padding-left: 28px; /* Equal to width of new image */
  	padding-top: 5px;
}
  
td.col_f_icon>span img {
	-moz-box-sizing: border-box;
  	box-sizing: border-box;
	background: url('http://forums.nexusmods.com/public/style_images/white/t_closed.png') no-repeat !important;
  	width: 28px; /* Width of new image */
  	height: 21px; /* Height of new image */
  	padding-left: 28px; /* Equal to width of new image */
  	padding-top: 5px;
}
img[src*="http://forums.nexusmods.com/public/style_images/underground/t_unread_dot.png"] {
    -moz-box-sizing: border-box;
  	box-sizing: border-box;
	background: url(http://forums.nexusmods.com/public/style_images/white/t_unread_dot.png) no-repeat !important;
  	width: 28px; /* Width of new image */
  	height: 21px; /* Height of new image */
  	padding-left: 28px; /* Equal to width of new image */
  	padding-top: 5px;
  	
}
img[src*="http://forums.nexusmods.com/public/style_images/underground/t_read_dot.png"] {
    -moz-box-sizing: border-box;
  	box-sizing: border-box;
	background: url(http://forums.nexusmods.com/public/style_images/white/t_read_dot.png) no-repeat !important;
  	width: 28px; /* Width of new image */
  	height: 21px; /* Height of new image */
  	padding-left: 28px; /* Equal to width of new image */
  	padding-top: 5px;
}
    
img[src*="http://forums.nexusmods.com/public/style_images/underground/t_read.png"] {
    -moz-box-sizing: border-box;
  	box-sizing: border-box;
	background: url(http://forums.nexusmods.com/public/style_images/white/t_read.png) no-repeat !important;
  	width: 28px; /* Width of new image */
  	height: 21px; /* Height of new image */
  	padding-left: 28px; /* Equal to width of new image */
  	padding-top: 5px;
}

table.ipb_table tr:hover {
	background: #E2E9F0;
}
table.ipb_table tr:hover .expander {
    opacity: 0.8;
}
.highlighted {
	background-color: #A2C9F0;
}
table.ipb_table tr.highlighted:hover {
	background-color: #A2C9F0;
}
}

