@-moz-document domain("forums.nexusmods.com") {
/*
===================================================
==== Message and Notification Dropdown ============
===================================================
*/
.ipsHeaderMenu {
    background: #ffffff;
    background: -moz-linear-gradient(top, #ffffff 0%, #f6f6f6 70%, #ededed 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(70%,#f6f6f6), color-stop(100%,#ededed));
    padding: 10px;
    -moz-border-radius: 0 0 6px 6px;
    -webkit-border-bottom-right-radius: 6px;
    -webkit-border-bottom-left-radius: 6px;
    border-radius: 0 0 6px 6px;
    overflow: hidden;
    width: 340px;
}
#user_navigation a#inbox_link.menu_active, #user_navigation a#user_link.menu_active {
	background: #FFFFFF;
  	color: #5a5a5a;
}

/*
===================================================
==== Messenger Folders List =======================
===================================================
*/
#folder_list .total {
    background-color: #d5dde5;
    font-size: 0.75em;
    font-weight: bold;
    padding: 3px 6px;
    margin-right: 10px;
    right: 0;
    position: absolute;
}
.progress_bar {
    background-color: #fff;
    border: 1px solid #d5dde5;
}
.progress_bar span {
    background: #a9a9a9 url('http://forums.nexusmods.com/public/style_images/underground/highlight_reallyfaint.png') repeat-x 0 0;
    color: #fff;
    font-size: 0em;
    font-weight: bold;
    text-align: center;
    text-indent: -2000em;
    height: 10px;
    display: block;
    overflow: hidden;
}

/*
===================================================
==== Messenger Thread Buttons =====================
===================================================
*/
.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .ipsButton.important {
    background: #812200 url(http://forums.nexusmods.com/public/style_images/white/topic_button_closed.png ) repeat-x top;
    border-color: #812200;
    -moz-box-shadow: inset 0 1px 0 0 #db6e46, 0px 2px 3px rgba(0,0,0,0.2);
    -webkit-box-shadow: inset 0 1px 0 0 #db6e46, 0px 2px 3px rgba(0,0,0,0.2);
    box-shadow: inset 0 1px 0 0 #db6e46, 0px 2px 3px rgba(0,0,0,0.2);
}
}

@-moz-document domain("forums.nexusmods.com") {
/*
===================================================
==== User info popup ============================
===================================================
*/
.popupWrapper {
    background-color: #464646;
    background-color: rgba(70,70,70,0.6);
    padding: 4px;
    -webkit-box-shadow: 0px 12px 25px rgba(0, 0, 0, 0.7);
    -moz-box-shadow: 0px 12px 25px rgba(0, 0, 0, 0.7);
    box-shadow: 0px 12px 25px rgba(0, 0, 0, 0.7 );
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    border-radius: 4px;
}
.popupInner {
    background: #fff;
    width: 500px;
    overflow: auto;
    -webkit-box-shadow: 0px 0px 3px rgba(0,0,0,0.4);
    -moz-box-shadow: 0px 0px 3px rgba(0,0,0,0.4);
    box-shadow: 0px 0px 3px rgba(0,0,0,0.4);
    overflow-x: hidden;
}
.popupInner h3 {
    background: #2c5687 url(http://forums.nexusmods.com/public/style_images/white/maintitle.png) repeat-x top;
    color: #fff;
    border-width: 1px 1px 0 1px;
    border-style: solid;
    border-color: #316897;
    padding: 8px 10px 9px;
    font-size: 16px;
    font-weight: 300;
    text-shadow: 0 1px 2px rgba(0,0,0,0.3);
    -webkit-box-shadow: inset 0px 1px 0 #528cbc;
    box-shadow: inset 0px 1px 0 #528cbc;
    -moz-box-shadow: inset 0px 1px 0 #528cbc;
}
.userpopup h3 {
	padding-left: 110px;
}
.general_box {
    background: #fcfcfc;
    margin-bottom: 10px;
}

/* Send PM From Form View Popup */
.popupClose {
    top: 12px;
}
fieldset.row1 {
    background-color: #fff;
}
