@-moz-document domain("forums.nexusmods.com") {
/* =================================================== */
/* ==== User Profile ================================= */
/* =================================================== */

.ipsBox {
    background: none;
}
.ipsBox_container {
  	border: none;
}
#user_latest_status > div {
    padding: 10px 15px;
    background-color: #ebece5;
    color: #343434;
    font-size: 14px;
    text-shadow: none;
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 6px;
    min-height: 45px;
    word-wrap: break-word;
}
#user_latest_status .status_arrow {
  	display: none;
}
.general_box h3 {
    background: #DBE2EC;
    color: #204066;
    font: normal 14px helvetica, arial, sans-serif;
    padding: 8px 10px;
}
.general_box {
	border: none !important;   
}
.ipsList_data li {
	border: none;
}
.ipsVerticalTabbed_tabs > ul {
    width: 149px !important;
    margin-top: 10px;
    border-top: 1px solid #DBE4EF;
    border-left: 1px solid #DBE4EF;
}
.ipsVerticalTabbed_tabs li {
    background: #f6f8fb;
    color: #808080;
    border-bottom: 1px solid #DBE4EF;
    font-size: 13px;
}
.ipsVerticalTabbed_tabs li.active a {
    width: 135px;
    position: relative;
    z-index: 8000;
    border-right: 1px solid #fff;
    background: #fff;
    color: #353535;
    font-weight: bold;
}
