css:
@-moz-document domain("spiegel.de") {
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  -ms-box-sizing: border-box;
  -o-box-sizing: border-box;
  box-sizing: border-box;
}

body {
  padding: 0;
}

#spOasTop,
.header-top,
.header-search,
.login-links,
#breadcrumb,
.ressort-teaser-box,
.archive-link-box,
.social-media-box,
#header-karriere-main,
#header-karriere-bereiche,
#header-karriere-premiumpartner,
body:not(#home) .column-small,
.function-box,
.asset-list-box,
.article-photo-gallery,
#spVeeseo {
  display: none;
}

@media screen and (min-width: 1030px) {
  #wrapper,
  #spOasTop {
    margin-left: 0;
  }
}

#wrapper-shadow {
  padding: 0;
  width: auto;
}

#wrapper-content {
  padding: 0;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
  box-shadow: none;
  border: none;
}

#wrapper-content,
.grid-homepage,
.grid-channel,
.grid-article,
.module-box,
.module-box .ressort-teaser-box-header {
  width: 100% !important;
}

.grid-homepage > *,
.grid-channel > *,
.grid-article > *,
.grid-homepage > .module-box > *,
#footer > *,
#karriere #header > *,
#einestages #content-main.article-content {
  width: 860px;
  margin-left: auto !important;
  margin-right: auto !important;
}
  
.ressort-teaser-box-header,
.grid-channel,
.grid-article,
.header-main,
.nav-channel,
#karriere #header {
  padding-left: calc((100% - 860px) / 2) !important;
  padding-right: calc((100% - 860px) / 2) !important;      
}

#karriere #header {
  background-image: none;
  height: auto;
}

body:not(#home) .column-wide {
  float: none !important;
  width: 100% !important;
  padding-left: 90px;
  padding-right: 90px;
  
  font-family: Georgia, serif;
  font-size: 22px;
  font-weight: 400;
  font-style: normal;
  letter-spacing: .01rem;
  line-height: 1.5;
}

body:not(#home) .column-wide b,
body:not(#home) .column-wide strong {
  display: block;
  margin: 1em -90px .62em -90px;
  
  text-align: center;
  
  font-family: arial, helvetica, geneva, sans-serif;
  font-size: 1.3em;
}

.article-comments-box {
  margin-top: 5em;
}

#header-karriere-meta {
  -webkit-box-sizing: initial;
  -moz-box-sizing: initial;
  box-sizing: initial;
}
      
.grid-homepage > .module-box > .ressort-teaser-box-header {
  margin-top: 5em !important;
  margin-bottom: 5em !important;
  padding-top: 5em;
  padding-bottom: 5em;
}

.nav-channel {
  background-image: none;
}

.teaser {
  border-top: 3px solid #e6e6e6;
}

.grid-homepage .teaser.hp-first-article,
.column-small .asset-box,
.column-small .module-box,
.column-small .spCommercial,
.grid-homepage .hp-teasers-box,
.grid-homepage > .asset-box {
  border-bottom: 3px solid #e6e6e6;
}

.grid-homepage > .hp-teasers-box {
  padding-top: 30px;
}

.grid-homepage > .asset-box {
  padding-top: 20px;
}
          
.hp-gradient,
.asset-mixed-imagetypes .list-float {
  background-image: none;
  background-color: #ffffff;
  border: none;
}
.hp-gradient.double-bottom-border {
  border-bottom: none;
}
.hp-gradient.double-bottom-border:before {
  border-top: none;
  border-bottom: none;
}

.block-bullet:before { display: none; }

.module-box .ressort-teaser-box-header,
.module-box .module-title a,
.module-box .ressort-teaser-box-nav ul li a {
   color: #ffffff;
}
    
.module-box .ressort-teaser-box-nav ul li,
.module-box .module-title a:after {
  border-left-color: #eeeeee;
}
.module-box .ressort-teaser-box-nav ul li a.link-more:after {
  border-style: solid;
  border-top-color: #eeeeee;
}

.header-main,
.module-box .ressort-teaser-box-header {
  background-position: center !important;
  background-repeat: no-repeat;
  background-size: cover;
}
#panorama .header-main,
.module-box.panorama .ressort-teaser-box-header {
  background-image:
      -webkit-linear-gradient(top, rgba(255, 114, 75, .8), rgba(255, 114, 75, .8))
    , url(http://unsplash.s3.amazonaws.com/batch%203/alejandroescamilla-white-shoes.jpg);
  background-color: rgb(255, 114, 75);
}
