.wobble-animation {
-webkit-animation: wobble 5s infinite;
-moz-animation: wobble 5s infinite;
-o-animation: wobble 5s infinite;
-ms-animation: wobble 5s infinite;
animation: wobble 5s infinite;
}

@-webkit-keyframes wobble {
0%  {-webkit-transform: translate(0px, 0px) rotate(0deg);}
20%  {-webkit-transform: translate(2px, 0px) rotate(1deg);}
50%  {-webkit-transform: translate(0px, 0px) rotate(-1deg);}
100% {-webkit-transform: translate(-2px, 0px) rotate(0deg);}
}

@-moz-keyframes wobble {
0%  {-moz-transform: translate(0px, 0px) rotate(0deg);}
20%  {-moz-transform: translate(2px, 0px) rotate(1deg);}
50%  {-moz-transform: translate(0px, 0px) rotate(-1deg);}
100% {-moz-transform: translate(-2px, 0px) rotate(0deg);}}

@-o-keyframes wobble {
0%  {-o-transform: translate(0px, 0px) rotate(0deg);}
20%  {-o-transform: translate(2px, 0px) rotate(1deg);}
