css:
@-moz-document domain(m.vk.com) {
.mcont{background-color:#000}
.wk_gray{color:#000}
.sub_header{font-size:medium;word-wrap:break-word;border-bottom:1px solid #efefef;background-color:#000;color:#606060;padding:8px 7px}
.found{background-color:#2f2f2f;padding:0;font-style:normal}
body{background-color:#000;color:#fff}
.sub_header{font-size:medium;word-wrap:break-word;border-bottom:1px solid #efefef;background-color:#000;color:#606060;padding:8px 7px}
.head_panel {
    font-size: medium;
    padding: 0px 7px 7px;
    background-color: #000;
    border-bottom: 1px solid #E9E9E9;}
.main_menu {
    background-color: #000;}
.owner_panel {
    font-size: medium;
    display: block;
    padding: 5px 7px 7px;
    border-bottom: 1px solid #E9E9E9;
    background-color: #000;
}

.new_item_link {
    display: block;
    padding: 12px;
    background-color: #000;
    text-align: center;
    font-weight: 700;
    font-size: medium;
    margin-top: -1px;
}
.mm_item:hover {
    text-decoration: none;
    background-color: #000
}
.pi_text {
    line-height: 1.5em;
    word-wrap: break-word;
    color: #FFF;
}
.mi_unread, .di_unread_inbox {
    background-color: #5e5e5e;
    border-bottom: 1px solid #DFE5EB;
    border-top: 1px solid #DFE5EB;
    margin-top: -1px;
}
.create_message {
    background-color: #3e3e3e;
    position: relative;
    margin-top: -1px;
}
.textfield {
    background: none repeat scroll 0% 0% #3e3e3e;
}
.mi_text, .di_text {
    padding: 3px 0px 0px;
    line-height: 1.4em;
    word-wrap: break-word;
    color: #FFF;
}
.fi_value {
    font-size: medium;
    font-weight: 700;
    color: #FFF;
}
}
