css:
@-moz-document domain("ogame.gameforge.com") {
body {
  background: #333 !important;
}

div#banner_skyscraper {
  display: none; /* reklama */
}

ul#resources li.metal,
ul#resources li.crystal,
ul#resources li.deuterium,
ul#resources li.darkmatter,
ul#resources li.energy {
    margin-left: 30vw;
}
ul#resources li.metal {
    left: 1px !important;
}
ul#resources li.crystal {
    left: 72px !important;
}
ul#resources li.deuterium {
    left: 143px !important;
}
ul#resources li.darkmatter {
    left: 285px !important;
}
ul#resources li.energy {
    left: 214px !important;
}
.resourceIcon.metal, 
.resourceIcon.crystal, 
.resourceIcon.deuterium, 
.resourceIcon.darkmatter, 
.resourceIcon.energy {
    background: none;
}
.resourceIcon.metal:after, 
.resourceIcon.crystal:after, 
.resourceIcon.deuterium:after , 
.resourceIcon.darkmatter:after , 
.resourceIcon.energy:after {
    display: block;
    background: black;
    height: 30px;
}
.resourceIcon.metal:after {
    content: "Metal";
}
.resourceIcon.crystal:after {
    content: "Kryszta≈Ç";
} 
.resourceIcon.deuterium:after {
    content: "Deuter";
}
.resourceIcon.darkmatter:after {
    content: "Antymateria";
}
.resourceIcon.energy:after {
    content: "Energia";
}
span#resources_darkmatter {
    position: relative;
    top: 32px;
}
#info ul#resources li {
    position: fixed;
    height: 30px;
    top: 23px;
    width: 64px;
    text-align: center;
    z-index: 10000000;
    opacity: 0.7;
}

#info li .value {
    color: #fff;
    font: normal 12px/12px Verdana,Arial,Helvetica,sans-serif;
    text-align: right;
    position: relative;
    top: -17px;
    right: -24px;
}
ul#resources .resourceIcon, #info ul#resources li img {
    margin: 0 8px 2px 8px;
    width: 70px;
    height: 29px;
}


/* CHAT */
.chat_box_title, .chat_bar_pl_list_item, .chat_bar_list_item { 
    background: rgba(100,100,100,0.8) !important;
    border: 1px ridge black;
}

.chat_msg .msg_title, .msg_title {
  width: auto !important;
}

.chat_box_ctn {
    background: rgb(51, 51, 51);
    height: 300px;
}

.chat_msg {
    background-color: #203c21;
    border-radius: 3px;
    border: 1px solid #000;
    box-shadow: inset 0 1px 2px rgba(62,81,100,0.4);
    margin: 3px 40px 15px 0;
    padding: 5px;
    position: relative;
    color: #a7b5a9 !important;
}

.chat_msg.odd {
    background-color: #272727;
    color: #656565 !important;
}

.chat_msg .msg_content {
    padding: 5px 0 0 0;
    overflow: inherit;
}

.chat_box_textarea {
    height: 15px;
    line-height: 14px;
    margin: 3px 0 -5px 3px;
    min-height: 15px;
    max-height: 43px;
    max-width: 100%;
    width: -webkit-fill-available;
}

.cb_playerlist_box, .chat_box {
    background: none !important;
}

.cb_playerlist_box, .chat_box {
    background: #0d1014;
    border: 1px solid #000;
    bottom: 27px;
    box-sizing: border-box;
    left: -1px;
    min-height: 250px;
    position: absolute;
    /*width: 100%;*/
    opacity: 0.9;
    width: 445px;
}
/* //CHAT */

.adviceWrapper { /* happy hour */
  display: none;
}

.c-left, .c-right, div.detail_screen.small { /* ramki obrazkowe */ 
    background: none !important;
}

div#eventboxContent {
 /* display: block !important; /* zdrarzenia flot */
}

#buttonz ul#building div.buildingimg {
  background: url('http://i.imgur.com/8KS0xRl.png');
}

#box #inhalt .buildingimg .construction .pusher { /* loader build */
  background: linear-gradient(transparent, gold) !important;
} 

li#darkmatter_box img {display: none;} /* antymateria ico */

div#box div#info {background: none !important;} /* tlo na gorze z logo */

#star, #star1, #star2 {display: none;} /* gwiazdki w tle */

#links .leftmenu a.menubutton:link, #links .leftmenu a.menubutton:visited, .no-touch #links .leftmenu a.menubutton:hover, #links .leftmenu a.menubutton:active {
  /* menu nieaktywny link */
  background: #222 !important;
  border: 1px solid #111;
}

#links .leftmenu li a.selected:link, #links .leftmenu li a.selected:visited, .no-touch #links .leftmenu li a.selected:hover, #links .leftmenu li a.selected:active {
  /* menu wybrany link */
  background: #333 !important;
  border: 1px solid #111;
}

div#planet {
  background: #222 !important; /* podglad */
  border: 1px solid #111;
}

div#planetdata .overlay {
  background: #333 !important;
}

#buffBar {
  background: none !important; /* ? */
}

#inhalt .content-box-s .content table.construction.active {
  background: #333 !important;
}

.content-box-s .footer {
  background: #222 !important;
  border: solid #111;
  border-width:  0px 1px 1px 1px; /* bottom */
}

.content-box-s .content {
  background: #222 !important;
  border: solid #111;
  border-width:  0px 1px 0px 1px; /* center */
}

.content-box-s .header {
  background: #222 !important;
  border: solid #111;
  border-width:  1px 1px 0px 1px; /* top */
}

/* **** */

#buttonz .header, #fleet3 #sendfleet .header {
  background: #222 !important;
  border: solid #111;
  border-width:  1px 1px 0px 1px; /* top */
}

#buttonz .content, #fleet3 #sendfleet .content {
  background: #222 !important;
  border: solid #111;
  border-width:  0px 1px 0px 1px; /* center */
}

#buttonz .footer, #fleet3 #sendfleet .footer {
  background: #222 !important;
  border: solid #111;
  border-width:  0px 1px 1px 1px; /* bottom */
}

#buttonz .h_batteships {
  background: none !important; /* tlo */
}

/* wiadomosci */
textarea.mailnew.markItUpEditor {
  background: #444;
  color: #eee;
  font-size: larger;
  height: 100px;
}

.messagebox .textWrapper, .messagebox .textWrapperSmall {
  max-height: 300px;
}

a.build-faster, a.build-it_premium { /* dokoncz szybciej za antymaterie */
display: none;
}

div#mmonetbar { /* gorny pasek z innymi grami */
display: none !important;
}

/* ekran logowania */
div#login {
display: block !important;
left: 230px;
top: 190px;
background: #333;
border: 1px solid #222;
}

div#login input {
background: #999;
border: 0;
color: #000;
height: 30px;
}
}
