css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plug.dj") {

/* Image for replacement icons */
#app-menu .icon.icon-site-logo,
#playlist-button .icon.icon-playlist,
#playlist-button .icon.icon-arrow-down,
#media-panel .selected .icon-playlist-small,
.move-to-top I,
.move-to-bottom I,
.row.selected * > I,
.icon.icon-check-purple,
.icon.icon-check-circle,
.icon.icon-check-blue,
#playlist-menu .icon.icon-check-white,
.favorite.selected .icon.icon-grab,
.icon.icon-history-positive,
.icon.icon-history-grabs,
.icon.icon-history-negative,
.selected .icon.icon-history-listeners,
.icon.icon-next-track,
#vote .icon:not(.icon-skip),
#user-lists .icon.icon-woot,
#user-lists .icon.icon-grab,
#user-lists .icon.icon-meh,
.icon.icon-badge-pp,
.icon.icon-pp-small{
  background-image: url(http://i.imgur.com/AfEWL9d.png) !important;
  background-size: 210px 135px !Important;
}
  /* Image re-orientation */
.move-to-top > I,
.selected .move-to-top > I,
.selected .icon.icon-history-positive,
#vote > #woot.selected .icon{
  transform: scaley(-1) !Important;
}

/* Grouped unsorted text colours */
  /* Black text */
#footer-user .item:hover,
#playlist-menu .selected > SPAN,
#playlist-activate-button.button:not(.active) > SPAN,
#search-suggestion li:hover,
.pop-menu li:hover,
#media-panel .selected SPAN,
#media-panel .now-playing,
#media-panel .selected .playlists a,
#history-panel .selected span,
#user-menu .count,
.crowd-response.active.selected .top,
.crowd-response.disabled.active .top,
.request-count,
.requests .count,
#footer-user .count,
#user-menu .item.selected,
#room-settings .item.selected,
.tab-menu .selected,
#tooltip.blue span,
#dialog-container .destructive:not(.no-submit) .button.submit,
#dialog-preview.dialog .button.submit,
#dialog-user-role .button.submit,
#dialog-media-update .button.submit,
#room-info .button.enabled{
  color: black !Important;
}
  /* Fuschia */
#playlist-menu .count,
#the-user-profile .current-level,
#playlist-activate-button.button.active > span{
  color: #ff0080 !important;
}

/* Grouped unsorted interface backgrounds */
  /* Grey */
#video-only-dj:hover,
#footer .button:hover,
#footer .info{
  background-color: #0a0a0a !important;
}
#room .app-header,
#footer,
#playlist-panel,
#dashboard .search,
#search-bar,
#filter-bar,
#user-rollover .action,
#video-only-dj,
#tooltip:not(.blue):not(.purple):not(.orange) span{
  background-color: #151515 !important;
}
#history-panel{
  background-color: rgba(21, 21, 21, 0.95) !important;
}
#playlist-menu,
.app-left.communities,
#user-view .jspPane,
.jspTrack,
.user-content{
  background-color: #202020 !Important;
}
#playlist-panel > #search,
#footer-user.open-user .info,
#footer-user .menu,
#user-menu{
  background-color: #303030 !Important;
}
  /* Fuschia */
#footer-user .item:hover,
#user-rollover .action:hover{
  background-color: #800040 !important;
}
  /* Black */
#history-button,
#user-rollover,
#user-rollover .info{
  background-color: black !Important;
}
  
/* plug.dj icon */
#app-menu .icon.icon-site-logo{
  background-position: -1px 0px !Important;
}

/* Input field highlights */
.focused,
#the-user-profile .blurb input{
  box-shadow: inset 0px 0px 0px 1px #ff0080 !important;
}

/* Playlist colours and icons */
#playlist-panel > #search > #filter .filter-or{
  color: #151515 !important;
}
#media-panel .now-playing{
  background-color: #800040 !Important;
  border-bottom: 1px solid #ff0080 !Important;
}
#playlist-button,
#footer-user .store:hover{
  background-image: linear-gradient(
                    #bf0060 0px,
                    #800040 100%) !Important;
}
#playlist-button .icon.icon-playlist{
  background-position: -120px -45px !Important; 
}
#media-panel .selected .icon-playlist-small{
  background-position: -180px -45px !Important;
}
#playlist-button .icon.icon-arrow-down{
  background-position: -150px -45px !important;
}
#playlist-menu .row.selected,
#media-panel .selected,
#history-panel .row.selected,
#search-suggestion li:hover{
  background-color: #800040 !important;
}
.icon.icon-next-track{
  background-position: -30px -105px !Important;
}
.selected .edit > I{
  background-position: -120px -105px !important;
}
.selected .related > I{
  background-position: -90px -105px !Important;
}
.selected .play-next > I{
  background-position: -180px -105px !important;
}

.move-to-bottom > I,
.move-to-top > I{
  background-position: 0px -105px !Important;
}

.selected .move-to-bottom > I,
.selected .move-to-top > I{
  background-position: 0px -75px !Important;
}
.selected .delete > I{
  background-position: -150px -105px !important;
}
.selected .add > I{
  background-position: -60px -105px !important;
}
#history-panel .selected .timestamp{
  color: #B0A6A4 !important;
}

/* Playlist and History improvements */
#media-panel .actions{
  background-color: rgba(21, 21, 21, 0.50) !Important;
}
#history-panel .actions{
  background-color: transparent !Important;
}
#history-panel .row.selected .actions,
#media-panel .row.selected .actions{
  background-color: rgba(128, 0, 64, 0.50) !Important;
}
#media-panel .row:hover .duration,
#history-panel .row:hover .timestamp,
#history-panel .row:hover .name{
  display: none !Important;
}

/* DJ history icons */
.icon.icon-history-positive{
  background-position: -90px -45px !Important;
}
.selected .icon.icon-history-positive,
.selected .icon.icon-history-negative{
  background-position: -60px -75px !important;
}
.icon.icon-history-grabs{
  background-position: -105px -45px !important;
}
.selected .icon.icon-history-grabs{
  background-position: -75px -75px !important;
}
.icon.icon-history-negative{
  background-position: -90px -60px !important;
}
.selected .icon.icon-history-listeners{
  background-position: -60px -90px !important;
}

/* Checkmarks */
.icon.icon-check-purple{
  background-position: -120px -75px !Important;
}
#playlist-activate-button.button:not(.active){
  background-color: #800040 !important;
}
#playlist-activate-button.button:not(.active) > I,
#playlist-menu .icon.icon-check-white,
#playlist-menu .row.selected .icon.icon-check-purple,
.pop-menu li:hover .icon.icon-check-purple{
  background-position: -150px -75px !Important;
}
.icon.icon-check-circle{
  background-position: -90px -75px !Important;
}
.icon.icon-check-blue{
  background-position: -105px -60px !Important;
}

/* Vote button colours and icons */
.pop-menu li:hover{
  background-color: #800040 !important;
}
#vote > #woot .icon,
#user-lists .icon.icon-woot{
  background-position: 0px -45px !Important;
}
#vote > #woot.selected .icon,
#vote > #woot.disabled.active .icon,
#vote > #meh.selected .icon,
#vote > #meh.disabled.active .icon{
  background-position: 0px -75px !important;
}
#vote > #grab .icon,
#user-lists .icon.icon-grab,
  /* "Favourite" icon */
#room-bar .favorite.selected .icon.icon-grab,
#dashboard .favorite.selected .icon.icon-grab,
#user-communities .favorite.selected .icon.icon-grab{
  background-position: -30px -45px !Important;
}
#vote > #grab.selected .icon{
  background-position: -30px -75px !important;
}
#vote > #meh .icon,
#user-lists .icon.icon-meh{
  background-position: -60px -45px !Important;
} /* last modified BGimg */

/* === Modified code from Color-coded user actions === */
  /* Arrows */
    /* Down */
#user-rollover:not(.chat) .arrow-down{
  border-top-color: #151515 !important;
}
#user-rollover.expand:not(.chat) .arrow-down{
  border-top-color: #202020 !important;
}
    /* Right */
#user-rollover.chat .arrow-down{
  border-left-color: #151515 !important;
}
#user-rollover.chat .arrow-down{
  border-left-color: #202020 !important;
}
/* === Modified from Color-coded user actions === */
  /* Box shadows */
    /* Infobox */
#user-rollover .info{
  box-shadow: inset 0px 0px 0px 1px #151515 !Important;
}
#user-rollover.expand .info{
  box-shadow: inset 0px 0px 0px 1px #202020 !Important;
}
    /* All except bottom */
#user-rollover .meta{
  box-shadow: inset 1px 0px 0px 0px #151515,
              inset -1px 0px 0px 0px #151515,
              inset 0px 1px 0px 0px #151515 !Important;
}
#user-rollover.expand .meta{
  box-shadow: inset 1px 0px 0px 0px #202020,
              inset -1px 0px 0px 0px #202020,
              inset 0px 1px 0px 0px #202020 !Important;
}
    /* All except top */
#user-rollover.can-gift .action.gift,
#user-rollover .action.skip,
#user-rollover .action.ignore,
#user-rollover .action.mod-staff,
#user-rollover.is-none .action.mod-mute,
#user-rollover.is-staff:not(.can-staff) .action.mod-dj,
#user-rollover.is-ambassador .action.mod-dj,
#user-rollover.is-ambassador .action.mention,
#user-rollover.is-admin .action.mod-dj,
#user-rollover.is-admin .action.mention{
  box-shadow: inset 1px 0px 0px 0px #202020,
              inset -1px 0px 0px 0px #202020,
              inset 0px -1px 0px 0px #202020 !Important;
}   /* Left and bottom */
#user-rollover .action.mod-dj,
#user-rollover .action.mention,
#user-rollover.is-none .action.mod-staff{
  box-shadow: inset 1px 0px 0px 0px #202020,
              inset 0px -1px 0px 0px #202020 !Important;
}   /* Right and bottom */
#user-rollover.can-gift .action.ignore,
#user-rollover .action.friend,
#user-rollover .action.mod-mute,
#user-rollover .action.mod-ban,
#user-rollover.is-ambassador .action.gift,
#user-rollover.is-ambassador .action.mod-dj,
#user-rollover.is-ambassador .action.mod-staff,
#user-rollover.is-admin .action.gift,
#user-rollover.is-admin .action.mod-dj,
#user-rollover.is-admin .action.mod-staff{
  box-shadow: inset -1px 0px 0px 0px #202020,
              inset 0px -1px 0px 0px #202020 !Important;
}
/* === End snippet === */

/* Dialog, DJ and room mod colours and icons */
#dialog-container .destructive:not(.no-submit) .button.submit,
#dialog-preview.dialog .button.submit,
#dialog-user-role .button.submit,
#dialog-media-update .button.submit{
  background-image: linear-gradient(
                    #bf0060 0px,
                    #ff0080 100%);
}

/* === Modified from Fancy DJ and vote buttons === */
/* DJ button */
  /* Icon background */
    /* Default: Lock / Full colour */
#dj-button .left{
  background-color: rgba(0, 0, 0, /*68, 74, 89,*/ 0.50) !important;
} /* Join colours */
    /* Left side colour */
#dj-button.is-join .left,
#dj-button.is-wait .left{
  background-image: none !Important;
  box-shadow: inset 0px 0px 10px #00f998,
              inset 0px 0px 10px #00f998,
              inset 0px 0px 10px #00f998 !important;
}   /* Right side colour -- match with left */
#dj-button.is-join,
#dj-button.is-wait{
  background-image: linear-gradient(to right, transparent 72px, #00f998 72px, black 82px, black 100%) !important;
} /* Leave colours */
    /* Left side colour */
#dj-button.is-leave .left,
#dj-button.is-quit .left{
  background-image: none !Important;
  box-shadow: inset 0px 0px 10px #FF3300,
              inset 0px 0px 10px #FF3300,
              inset 0px 0px 10px #FF3300 !important;
}   /* Right side colour -- match with left */
#dj-button.is-leave,
#dj-button.is-quit{
  background-image: linear-gradient(to right, transparent 72px, #FF3300 72px, black 82px, black 100%) !important;
} /* Lockout colours */
    /* Left side colour */
#dj-button.is-locked .left,
#dj-button.is-full .left{
  background-image: none !Important;
  box-shadow: inset 0px 0px 10px #B0A6A4,
              inset 0px 0px 10px #B0A6A4,
              inset 0px 0px 10px #B0A6A4 !important;
}   /* Right side colour -- match with left */
#dj-button.is-locked,
#dj-button.is-full{
  background-image: linear-gradient(to right, transparent 72px, #B0A6A4 72px, black 82px, black 100%) !important;
} /* Other global values */
    /* Make button body transparent */
#dj-button{
  background-color: transparent !Important;
}

/* Vote settings */
  /* Unselected / Global bottom */
    /* Top */
#vote > .crowd-response{
  background-color: transparent !important;
  background-image: linear-gradient(to bottom, black 27px, #B0A6A4 37px, transparent 37px, transparent 100%) !important;
}   /* Bottom */
#vote > .crowd-response .bottom{
  background-color: rgba(0, 0, 0, /*68, 74, 89,*/ 0.50) !important;
  box-shadow: inset 0px 0px 5px #B0A6A4,
              inset 0px 0px 5px #B0A6A4,
              inset 0px 0px 5px #B0A6A4 !important;
} /* Woot! */
    /* Top */
#vote > #woot.crowd-response.active{
  background-image: linear-gradient(to bottom, black 27px, #00f998 37px, transparent 37px, transparent 100%) !important;
}
#vote > #woot.crowd-response.selected,
#vote > #woot.crowd-response.disabled.active{
  background-color: transparent !Important;
  background-image: linear-gradient(to bottom, #00f998 37px, transparent 37px, transparent 100%) !important;
}   /* Bottom */
#vote > #woot.crowd-response.selected .bottom,
#vote > #woot.crowd-response.active .bottom{
  box-shadow: inset 0px 0px 5px #00f998,
              inset 0px 0px 5px #00f998,
              inset 0px 0px 5px #00f998 !important;
} /* Grab */
    /* Top */
#vote > #grab.crowd-response.active{
  background-image: linear-gradient(to bottom, black 27px, #ff0080 37px, transparent 37px, transparent 100%) !important;
}
#vote > #grab.crowd-response.selected,
#vote > #grab.crowd-response.disabled.active{
  background-color: transparent !Important;
  background-image: linear-gradient(to bottom, #ff0080 37px, transparent 37px, transparent 100%) !important;
}   /* Bottom */
#vote > #grab.crowd-response.selected .bottom,
#vote > #grab.crowd-response.active .bottom{
  box-shadow: inset 0px 0px 5px #ff0080,
              inset 0px 0px 5px #ff0080,
              inset 0px 0px 5px #ff0080 !important;
} /* Meh */
    /* Top */
#vote > #meh.crowd-response.active{
  background-image: linear-gradient(to bottom, black 27px, #FF3300 37px, transparent 37px, transparent 100%) !important;
}
#vote > #meh.crowd-response.selected,
#vote > #meh.crowd-response.disabled.active{
  background-color: transparent !Important;
  background-image: linear-gradient(to bottom, #FF3300 37px, transparent 37px, transparent 100%) !important;
}   /* Bottom */
#vote > #meh.crowd-response.selected .bottom,
#vote > #meh.crowd-response.active .bottom{
  box-shadow: inset 0px 0px 5px #FF3300,
              inset 0px 0px 5px #FF3300,
              inset 0px 0px 5px #FF3300 !important;
} /* Other global values */
    /* Remove box shadow and gradient on selected item */
#vote > .crowd-response.selected{
  box-shadow: none !Important;
}
/* === End snippet === */

.row:not(.selected) .item > span{
  color: rgb(238, 238, 238) !important;
}
#vote > #woot.crowd-response .bottom,
.row:not(.selected) .item.positive > span{
  text-shadow: 0px 0px 6px #00f998,
               0px 0px 6px #00f998,
               0px 0px 6px #00f998 !important;
}
#vote > #grab.crowd-response .bottom,
.row:not(.selected) .item.grabs > span{
  text-shadow: 0px 0px 6px #ff0080,
               0px 0px 6px #ff0080,
               0px 0px 6px #ff0080 !important;
}
#vote > #meh.crowd-response .bottom,
.row:not(.selected) .item.negative > span{
  text-shadow: 0px 0px 6px #FF3300,
               0px 0px 6px #FF3300,
               0px 0px 6px #FF3300 !important;
}

.row .item.listeners > span,
#history-panel .selected .item > span{
  text-shadow: 0px 0px 6px #B0A6A4,
               0px 0px 6px #B0A6A4,
               0px 0px 6px #B0A6A4 !important;
}

/* Notifications, volume and other / extra tooltip stuff */
#user-menu .count,
.request-count,
.requests .count,
#footer-user .count,
#tooltip.blue span,
.friends.has-requests > .header > I,
#volume .circle:not([style="left: 7.5px;"]){
  background-color: #ff0080 !Important;
}
#tooltip.blue .corner{
  border-right-color: #ff0080 !Important;
}
#tooltip:not(.blue):not(.purple):not(.orange) .corner{
  border-left-color: #151515;
}
#tooltip.right:not(.blue):not(.purple):not(.orange) .corner{
  border-right-color: #151515;
}
#tooltip.right .corner{
  border-left: transparent !Important;
}

/* User menus */
.tab-menu .selected,
#user-menu .item.selected,
#room-settings .item.selected{
  background-color: #800040 !important;
}

/* Avatar backdrops */
#user-menu .avatar,
#user-avatars .cell.selected .top,
#user-avatars .cell:hover .top{
  background-image: linear-gradient(to bottom, #ff0080 0px,#800040 56%,#000000 73%,#6d6e71 100%) !important;
}

/* PP tokens */
.icon.icon-badge-pp{
  background-position: -155px 0px !Important;
}
.icon.icon-pp-small{
  background-position: -125px -15px !Important;
}

/* === Modified code from Fancy Glowing XP Bar === */
/* Rounding of progress bars */
.info .bar .progress,
.info .bar{
border-radius: 6px !Important;
}
#the-user-profile .bar,
#the-user-profile .bar .progress{
border-radius: 15px !Important;
}

/* Min width to fix 0 XP bug */
.info .bar .progress{
min-width: 12px !Important;
}
#the-user-profile .bar .progress{
min-width: 30px !Important;
}

/* Coloring and shadowing progress */
.info .bar .progress,
#the-user-profile .bar .progress{
background: linear-gradient(to bottom,
                            #800040 0%,
                            #bf0060 50%,
                            #800040 100%) !important;
}
.info .bar .progress{
box-shadow: inset 0px 0px 3px #ff0080,
            inset 0px 0px 1px #ff0080,
            0px 0px 4px #ff0080,
            0px 0px 4px #ff0080 !important;
}
#the-user-profile .bar .progress{
box-shadow: inset 0px 0px 6px #ff0080,
            inset 0px 0px 3px #ff0080,
            0px 0px 8px #ff0080,
            0px 0px 8px #ff0080 !important;
}

/* Always show volume slider */
#volume .slider{
  display: block !Important;
}

/* Miscellanious box shadows */
  /* Inventory footer button */
#footer .inventory{
  box-shadow: inset 1px 0px 0px 0px #0A0A0A !Important;
}
  /* Footer info stuff */
#footer .info{
  box-shadow: 0px 1px 0px 0px #ff0080,
              inset 0px 1px 0px 0px #ff0080,
              inset 1px 0px 0px 0px #ff0080,
              inset -1px 0px 0px 0px #ff0080 !important;
}
#footer-user .profile:hover{
  box-shadow: 1px 0px 0px 0px #ff0080,
              inset 1px 0px 0px 0px #ff0080 !important;
  z-index: 1 !Important;
}

#footer-user .profile:hover:after{
  position: absolute !Important;
  top: 0px !Important;
  left: 1px !Important;
  width: 68px !Important;
  height: 53px !Important;
  content: '' !important;
  box-shadow: 0px -1px 0px 0px #0a0a0a !Important; 
  z-index: 1 !Important;
}
  
}
